// Distance to an vesica (intersection of two disks). Can be seen as a
// special case of abje's crescent shape (https://www.shadertoy.com/view/XlVfz1)


// List of some other 2D distances:
//
// Triangle:             https://www.shadertoy.com/view/XsXSz4
// Isosceles Triangle:   https://www.shadertoy.com/view/MldcD7
// Regular Triangle:     https://www.shadertoy.com/view/Xl2yDW
// Regular Pentagon:     https://www.shadertoy.com/view/llVyWW
// Regular Octogon:      https://www.shadertoy.com/view/llGfDG
// Rounded Rectangle:    https://www.shadertoy.com/view/4llXD7
// Rhombus:              https://www.shadertoy.com/view/XdXcRB
// Trapezoid:            https://www.shadertoy.com/view/MlycD3
// Polygon:              https://www.shadertoy.com/view/wdBXRW
// Hexagram:             https://www.shadertoy.com/view/tt23RR
// Regular Star:         https://www.shadertoy.com/view/3tSGDy
// Ellipse 1:            https://www.shadertoy.com/view/4sS3zz
// Ellipse 2:            https://www.shadertoy.com/view/4lsXDN
// Quadratic Bezier:     https://www.shadertoy.com/view/MlKcDD
// Uneven Capsule:       https://www.shadertoy.com/view/4lcBWn
// Vesica:               https://www.shadertoy.com/view/XtVfRW
// Cross:                https://www.shadertoy.com/view/XtGfzw
// Pie:                  https://www.shadertoy.com/view/3l23RK
// Arc:                  https://www.shadertoy.com/view/wl23RK
// Horseshoe:            https://www.shadertoy.com/view/WlSGW1
// Parabola:             https://www.shadertoy.com/view/ws3GD7
//
// and many more here:   http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm


float sdVesica(vec2 p, float r, float d)
{
    p = abs(p);

    float b = sqrt(r*r-d*d);  // can delay this sqrt by rewriting the comparison
    return ((p.y-b)*d > p.x*b) ? length(p-vec2(0.0,b))
                               : length(p-vec2(-d,0.0))-r;
}

/** This is a different way of specifying the vesica. We specify the half-height h
    and its width w. This allows us to maintain the same height of the vesica, which is
    good if you're trying to simulate eyelids and things. -Shane Celis */

float sdLens(vec2 p, float h, float w)
{
 	float d = h / w - w / 4.0;
    float r = w / 2.0 + d;
    return sdVesica(p, r, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float d = sdLens( p, 0.8, 0.8 + 0.2*sin(iTime) );

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(140.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.005,abs(d)) );


	fragColor = vec4(col,1.0);
}

// "Turn of the Screw" by dr2 - 2019
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Fbm2 (vec2 p);

vec3 ltDir, qHit;
float tCur, dstFar, tPhs;
int idObj;
const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, len, r, a;
  dMin = dstFar;
  len = 3.;
  q = p;
  q.xy = Rot2D (q.xy, -4. * len * pi * tPhs);
  q.z -= len * (1. - 2. * tPhs);
  a = (length (q.xy) > 0.) ? atan (q.y, - q.x) / (2. * pi) : 0.;
  r = 0.3 + 0.2 * q.z / len - 0.2 * abs (sin (2. * pi * (q.z - a)));
  r = max (0.07, r);
  d = 0.7 * PrCylDf (q, r, len);
  DMINQ (1);
  q.z -= len - 0.2;
  d = max (PrSphDf (q, 0.9), - q.z + 0.2);
  q.z -= 0.9;
  d = max (d, - min (PrBox2Df (q.xz, vec2 (0.1, 0.4)), PrBox2Df (q.yz, vec2 (0.1, 0.4))));
  DMINQ (2);
  q = p;
  q.z -= -0.5;
  d = PrBoxDf (q, vec3 (2.5, 2.5, 0.5));
  DMINQ (3);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = 0; j < 60; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.03 * d, h));
    d += clamp (h, 0.05, 0.4);
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
}

float BgPat (vec3 rd)
{
  vec3 tm, qn, u;
  vec2 q;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = max (max (tm.x, tm.y), tm.z) * rd;
  q = mod (8. * vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.));
  return min (SmoothBump (0.16, 0.84, 0.06, q.x), SmoothBump (0.16, 0.84, 0.06, q.y));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, nDotL, sh, t;
  bool refBg;
  t = mod (0.03 * tCur, 1.);
  tPhs = SmoothBump (0.25, 0.75, 0.23, t);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    refBg = (idObj == 1 || idObj == 2 && length (qHit + vec3 (0., 0., 0.9)) > 0.899 &&
       qHit.z > -0.69);
    if (idObj == 1 || idObj == 2) {
      col4 = vec4 (0.7, 0.7, 0.6, 0.2) * (1. -
         0.07 * cos (8. * atan (qHit.y, - qHit.x)));
    } else if (idObj == 3) {
      col4 = vec4 (0.5, 0.3, 0.1, 0.05);
      if (abs (vn.z) > 0.99) col4.rgb *= 1. - 0.3 * Fbm2 (qHit.xy * vec2 (4., 1.));
      else col4.rgb *= 0.95 + 0.05 * cos (16. * pi * qHit.z);
      if (vn.z < -0.99) col4.rgb *= mix (0.5 + 0.5 * smoothstep (0.05, 0.2, length (qHit.xy)),
         1., smoothstep (0.2, 0.24, abs (t - 0.75)));
    }
    nDotL = max (dot (vn, ltDir), 0.);
    if (idObj == 1 || idObj == 2) {
      nDotL *= nDotL;
      nDotL *= nDotL;
    }
    sh = ObjSShadow (ro, ltDir);
    col = col4.rgb * (0.2 + 0.1 * max (dot (vn, - ltDir), 0.) + 0.8 * sh * nDotL) +
       col4.a * step (0.95, sh) * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 16.);
    if (refBg) col = mix (col, vec3 (1.) * (0.9 + 0.1 * BgPat (reflect (rd, vn))), 0.2);
  } else {
    col = mix (vec3 (0.07, 0.1, 0.07), vec3 (1.) * (0.9 + 0.1 * BgPat (rd)), 0.1);
  }
  return clamp (col, 0., 1.);
}

#define AA  1   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.7 * pi;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 2. * pi * mPtr.y;
  } else {
    az += 0.01 * pi * tCur;
    el -= 0.2 * pi * sin (0.05 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -30.);
  zmFac = 5.;
  dstFar = 50.;
  ltDir = vuMat * normalize (vec3 (-1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

void CreateCrossFX(vec2 uv, vec2 pos, inout vec3 result)
{
    uv += pos;
    vec3 preResult = vec3(0.);
    preResult += vec3(abs(-((sin(iTime) + 1. / 2.)) * 20. + length(uv + 50.)));
    preResult *= vec3(abs(uv.x -0.3));
    preResult *= vec3(abs(uv.x -0.6) * 2.);
    preResult *= (vec3(1.) - vec3(abs(uv.x -sin(iTime))));
    preResult *= (vec3(1.) - vec3(abs(uv.x -sin(iTime))));
    preResult *= vec3(abs(uv.x + 0.3));
    preResult *=( vec3(sin(iTime) * .1 + 0.3) - vec3(abs(uv.y)));
    preResult *= sin(iTime) + 1. /2. + 1.8;
    result += preResult / 0.1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv -= .5;

    vec3 result = vec3(.0);
	CreateCrossFX(uv, vec2(0.,0.), result);
    result *= mix(min(vec3(0.2,0.9,0.1),vec3(.2)), 
                  vec3(0.5,0.1,0.2), uv.x);
    fragColor = vec4(result,1.0);
}

