// ========== Coherent Noise ==========

// - Value Noise, 3d, Unsigned
float valueNoise3du(vec3 samplePoint) {
    vec3 pointI = floor(samplePoint);
    vec3 pointF = fract(samplePoint);
    vec3 u = valueNoiseFilter(pointF);

    //Slight Optimisation
