float getConcreteErosion(vec3 position, bool showLine) {
    float concrete=0.0;
    concrete+= valueNoise3du(position * 4.0) * 0.25;
    concrete+= valueNoise3du(position * 8.0) * 0.125;
    concrete+= valueNoise3du(position * 16.0) * 0.0625;
    concrete+= valueNoise3du(position * 32.0) * 0.03125;
    concrete/=0.25+0.125+0.0625+0.03125;
    concrete = (abs(concrete*2.0-1.0)+0.3);

    if(showLine) {
        float line = abs(fract(position.y)-0.5);
        if(line < 0.05) {
            line = smoothstep(-0.5,1.0,clamp(line*15.0,0.0,1.0));
            line = 1.0-line;
            line*=4.0;
            line=1.0-line;
            line = clamp(line,-10.0,1.0);
            concrete = min(line,concrete*line);
        }
    }

    return concrete;
}

float sampleConcrete(vec3 position, bool showLine) {
    float concrete;

    concrete= valueNoise3du(position);
    concrete+= valueNoise3du(position * 2.0) * 0.5;
    concrete+= valueNoise3du(position * 4.0) * 0.25;
    concrete+= valueNoise3du(position * 8.0) * 0.125;
    concrete+= valueNoise3du(position * 16.0) * 0.0625;
    concrete/=1.0+0.5+0.25+0.125+0.0625;
    concrete = (abs(concrete*2.0-1.0)+0.9);

    if(showLine) {
        float line = abs(fract(position.y)-0.5);
        if(line < 0.05) {
            line = smoothstep(0.0,1.0,clamp(line*10.0,0.0,1.0))*3.0;
            line = clamp(line+0.5,0.0,1.0);
            concrete *= line;
        }
    }

    return concrete;
}

float getConcrete(vec3 position, inout vec3 normal, bool showLine) {
    vec3 rightDir = normalize(cross(vec3(0.0,1.0,0.0), normal))*0.00001;
    vec3 upDir =  normalize(cross(normal, rightDir))*0.00001;

    float center = getConcreteErosion(position, showLine);
    float right = getConcreteErosion(position+rightDir, showLine);
    float up = getConcreteErosion(position+upDir, showLine);

    //FIXME: apply correct normal calculations, this is NOT how you do it!
    //vec3 worldNormal = tangent * texNormal.x + binormal * texNormal.y + normal * texNormal.z;
    vec3 pNormal;
    pNormal=(vec3(
        right - center,
        up - center,
        right - center
    ));

    pNormal*=-3000.0;
    normal = normalize(normal + pNormal);
    float concrete=sampleConcrete(position, showLine);//*0.5+0.5;
    return concrete;
}

void textureSolid(vec2 block, vec3 position, inout vec3 colour, inout vec3 normal) {
    float concrete = getConcrete(position, normal, true);
    colour = hash33(block.xyx) * vec3(0.25,0.1,0.2) + 0.5;
    colour = clamp(colour,vec3(0.0),vec3(1.0));
    colour *= concrete;
}

void textureWall(vec2 block, vec3 position, inout vec3 colour, inout vec3 normal, inout int material) {
    float scale = 2.0;

    float windowHeight =	hash21(block*39.195)*0.4+0.4;
    float windowWidth =		hash21(block*26.389)*0.7+0.2;

    if (windowWidth > 0.8){
        windowWidth=1.0;
    }

    vec3 ramp = fract(position*scale)*2.0-1.0;

    vec2 uv;
    if (windowWidth==1.0) {
        uv.x=0.0; 
    } else if (abs(ramp.x) > abs(ramp.z)) {
        uv.x = ramp.x;
    } else {
        uv.x = ramp.z;
    }
    uv.y = ramp.y;

    if ( (abs(uv.x) < windowWidth) && abs(uv.y) < windowHeight) {
        colour=vec3(0.0);
        material = MAT_WINDOW;
    } else {
        //uv.x=clamp(abs(uv.x)-windowWidth,0.0,1.0)/(1.0-windowWidth);
        //uv.y=clamp(abs(uv.y)-windowHeight,0.0,1.0)/(1.0-windowHeight);

        //Remove vertical lines when windows are close togther
        if (windowWidth>=0.7) {
            uv.x=0.0; 
        }

        //FIXME: apply correct normal calculations, this is NOT how you do it!
        vec3 pNormal;
        uv*=uv*uv*0.5;
        pNormal = vec3(uv.x,uv.y,uv.x);
        normal = normalize(normal-pNormal);

        float concrete = getConcrete(position, normal, false);
        colour = hash33(block.xyx) * vec3(0.25,0.1,0.2) + 0.5;
        colour = clamp(colour,vec3(0.0),vec3(1.0));
        colour *= concrete;
    }
}

