void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    float time = iTime;
    //vec3 debugOverlay = vec3(0.0);

    // Normalized Pixel coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    // Normalized Mouse coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 mouse = ((iMouse.xy-0.5*iResolution.xy)/iResolution.y);// - vec2(0.5,0.5)*(iMouse.w*0.1);

    //Snap to center on startup.
    if((iMouse.x <= 1.0 && iMouse.y <= 1.0)) {
        mouse = vec2(0.0,0.0);
    }

    camera camera = getCamera(mouse, time);

    // --
    ray ray = getRay(uv, camera);
    rayMarchHit hit = RayMarch(ray, RAY_MAX_DISTANCE, OBJECT_MIN_SURFACE_DISTANCE, RAY_MAX_STEPS, time);
    hit.surfaceNormal = calcNormal( hit.origin, time );

    // ---- Render
    vec3 colour;
    vec3 skybox = mix(vec3(0.03,0.1,0.25),vec3(0.5,0.4,0.3),(ray.direction.y*0.5+0.5)+0.2);

    if (hit.materialId == MAT_WALL) {
        vec2 block= floor(hit.origin.xz/blockSize);
        textureWall(block, hit.origin, colour, hit.surfaceNormal, hit.materialId);        
    }else if (hit.materialId == MAT_SOLID) {
        vec2 block= floor(hit.origin.xz/blockSize);
        textureSolid(block, hit.origin, colour, hit.surfaceNormal);        
    }else if (hit.materialId == MAT_NEON) {
        #if defined(DEBUG_WHITE_NEON)
        float lightStrobe = getRoadLightStrobe(hit.distance, time);
        colour = mix(roadLightColour*0.1,vec3(4.0),lightStrobe);
        #else 
        colour = vec3(0.0);
        #endif
    } else {
        colour = vec3(0.5,0.4,0.3);
    }

    if(hit.materialId == MAT_WINDOW) {
        vec3 hash = hash33(floor(hit.origin*2.0));
        float on = (hash.x+hash.y+hash.z)+sin(time*0.4+hash.y)*0.05;
        if(on >1.5) {
            vec3 wColour = normalize(hash+vec3(0.99));
            colour=wColour * 5.0;
        } else {
            colour=vec3(0.0);
        }
    }

    #if defined(DEBUG_LIGHTING)
    if (hit.materialId != MAT_NEON && hit.materialId != MAT_SKY) {
        vec3 diffuseLight;
        vec3 specularLight;
        float shininess = 10.0;
        float specularIntensity = 0.1;

        if (hit.materialId == MAT_SOLID) {
            shininess = 2.0;
            specularIntensity = 0.1;
        } else if (hit.materialId == MAT_WALL) {
            shininess = 20.0;
            specularIntensity = 0.5;
        } else if (hit.materialId == MAT_WINDOW) {
            shininess = 100.0;
            specularIntensity = 2.0;
        } 

        getLight(hit.origin, hit.surfaceNormal, ray.direction, shininess, camera.origin, diffuseLight, specularLight, time);

        #if defined(DEBUG_AMBIENT)
        vec3 ambient = skybox*0.5;
        #if defined(DEBUG_AMBIENT_OCCLUSION)
        if (hit.materialId == MAT_WALL || hit.materialId == MAT_SOLID) {
            ambient *=(getOcclusion(hit.origin, hit.surfaceNormal, time));
        }
        #endif
        diffuseLight += ambient;
        #endif

        if (hit.materialId != MAT_WINDOW) {
            colour*=diffuseLight;
        }
        colour+=(specularLight*specularIntensity);

    }
    #endif

    #if defined(DEBUG_OCCLUSION)
    if (hit.materialId == MAT_WALL || hit.materialId == MAT_SOLID) {
        colour *=(getOcclusion(hit.origin, hit.surfaceNormal, time));
    }
    #endif

    #if defined(DEBUG_DISTANCE_FOG)
    float distanceFog = hit.distance/RAY_MAX_DISTANCE;
    colour = mix(colour, skybox, distanceFog);
    #endif

    #if defined (DEBUG_FACE_FOG)
    float faceFog = getFog(ray.direction+ray.origin,time) * (0.4+0.1*sin(time*0.1));
    colour += hit.neon * (0.5+faceFog*2.0);
    colour = mix(colour, vec3(2.0), faceFog);
    //colour=vec3(faceFog);
    #else
    colour += hit.neon;
    #endif

    // Exposure and Gamma 
    float exposure = 0.5;
    colour = pow( exposure * colour, vec3(1.0/2.2) );

    //Clamp
    colour = clamp(colour,0.0,1.0); 

    // colour grading
    //colour = colour*vec3(1.0,0.8,0.9);
    // compress        
    colour = 1.7*colour/(1.0+colour);
    colour = colour*colour*(3.0-2.0*colour);

    #if defined(DEBUG_VIGNETTE) 
    // Vignette
    uv = fragCoord.xy / iResolution.xy;
    uv *=  1.0 - uv; 
    float vignette = clamp(pow( uv.x*uv.y*5.0,0.5)+0.4,0.0,1.0);
    //Burn it in
    colour=vec3(1.0)-((vec3(1.0)-colour)/vignette);
    #endif

    fragColour = vec4(colour,1.0);
}

