float PI = 3.14159265358979323;

// Scene (creating the scene)
float scene(vec3 from) {

    float size = 1.0;
    float de = deCuboid(from, vec3(0.0), vec3(size));
    float concentration = 3.0 - ceil(cos(iTime * 0.5));
    for(int iteration = 0; iteration < 3; iteration ++) {
        vec3 scaledFrom = abs(concentration - concentration * abs(mod(from * size, 2.0) - 1.0));
        size *= concentration;
        de = boolSubtraction(de, deAxis(scaledFrom, vec3(0.0), 1.0) / size);
    }

    float other = deCuboid(from, vec3(cos(iTime) * 2.0, 0.0, 0.5), vec3(0.5));
    // de = smoothBoolUnion(de, deSphere(from, vec3(cos(iTime) * 2.0, 0.0, 0.0), 0.5), 0.5);
    // de = smoothBoolUnion(de, other, 0.5);

    return de;

}

// Get the normal of a point on the surface of an object
vec3 getNormal(vec3 from) {
    vec2 slopeOther = vec2(0.0001, -0.0001); 
    return normalize(
        slopeOther.xxx * scene(from + slopeOther.xxx) +
        slopeOther.xyy * scene(from + slopeOther.xyy) +
        slopeOther.yyx * scene(from + slopeOther.yyx) + 
        slopeOther.yxy * scene(from + slopeOther.yxy)
    );
}
// March the rays!
vec3 render(vec3 from, vec3 dir) {
	vec3 at = from;
	float closest = 10000.0;
	for(float totalDE = 0.0; totalDE < 100.0; totalDE += 1.0) {
		float de = scene(at);
		at += dir * de;
		if(de < closest) {
			closest = de;
		}
		if(de < 0.01) {
		    vec3 normal = getNormal(at);

		    float shade = dot(normal, -sun);
		    float bias = 1.0;
		    vec3 atShadow = at + normal * bias;
		    float closestShadow = 10000.0;
		    for(float shadowDE = 0.0; shadowDE < 100.0; shadowDE += 1.0) {
		        float deShadow = scene(atShadow);
		        atShadow += -sun * deShadow;
		        if(deShadow < closestShadow) {
		            closestShadow = de;
		        }
		        if(deShadow < 0.01) {
		            shade = 0.0;
		        }
		    }

		    return clamp(vec3(0.0, 1.0, 1.0) * clamp(shade, 0.25, 1.0) - totalDE / 100.0, 0.0, 1.0);
		}
	}
    return glow_color * glow_strength - closest / 10.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    cam.x = cos(mod(iTime / 2.0, PI * 2.0)) * 3.0;
    cam.y = 2.0;
    cam.z = sin(mod(iTime / 2.0, PI * 2.0)) * 3.0;

    float yaw = mod(iTime / 2.0, PI * 2.0) + PI / 2.0;
    float pitch = PI / 2.0 + 0.4;
    // float yaw = 0.0;
    // float pitch = PI / 2.0;

    float xMap = fragCoord.x / iResolution.x - 0.5;
    float yMap = fragCoord.y / iResolution.x - 0.5;
    vec3 dir = normalize(vec3(xMap, yMap * cos(pitch - PI / 2.0) + cos(pitch), yMap * sin(pitch - PI / 2.0) + sin(pitch)));
    vec3 rotated = rotate(dir, 0.0, yaw, 0.0, vec3(0.0));
    dir.x = rotated.x;
    dir.z = rotated.z;

    c = render(cam, dir);
    fragColor = vec4(c, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

    float d = 1.0 - (dot(uv, uv));
    d += iTime / 3.;
    d *= 3.;

    float id = floor(d);
    id = fract(id * 38.97);

    vec3 colors = id * vec3(46.35, 17.376, 49.84);
    colors = fract(colors);

    float m = fract(d);
    m = 2.0 * (m - 0.5);
    m = 1.0 - abs(m);
    m = smoothstep(0.0, 1.0, m);

    col += (colors * m);

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int max_iter = 60;
    float scale = 4.0;

    vec2 uv = scale * fragCoord/iResolution.xy - vec2(0.5 * scale);
    uv.y *= iResolution.y / iResolution.x;

    // c = 0.7885 * e ^ (i * alpha)
    float alpha = mix(0.0, 2.0 * 3.14159, fract(0.1 * iTime + 0.5));
    vec2 c = vec2(0.7885 * cos(alpha), 0.7885 * sin(alpha));


    // otros valores interesantes para c:
    // c = 0.285 + 0.01i
    // c = ‚àí0.70176 ‚àí 0.3842i
    // c = ‚àí0.8 + 0.156i
    // c = ‚àí0.7269 + 0.1889i

    vec2 Z = uv;

    int iter;
    for (iter = 0; iter < max_iter; ++iter)
    {
        vec2 Zn = Z;
        Zn.x = Z.x * Z.x - Z.y * Z.y;
        Zn.y = 2.0 * Z.x * Z.y;

        Z = Zn + c;

        if (dot(Z,Z) > 4.0)
        {
            break;
        }
    }

    float s = float(iter) / float(max_iter);    
    vec3 col = mix(vec3(0.0, 0.0, 1.0), vec3(1.0, 1.0, 1.0), 1.5 * s);

    // Output to screen
    fragColor = vec4(col,1.0);
}

