vec3 rdrBack(vec2 uv)
{
  const int lines = 8;
  vec3 acc= vec3(1.);

  for (int i = 0; i< lines;++i)
  {
    float fi = float(i);
    vec2 pos= 1.*vec2(rand(vec2(fi*12., 8.-fi)), rand(vec2(mod(fi,0.5),fi)));
    pos-= vec2(0.2);
    pos-=pos;
    float speed = 0.5;
    pos -= max((fi/8.),0.1)*vec2(sin(speed*(iTime+fi)),cos(speed*(fi+iTime*(mod(fi,2.)<0.1?1.:-1.))));
    acc -= 3.*pow(length(uv-pos)*0.005*fi,1.);
  }
  return acc;
}


vec3 rdr(vec2 uv)
{
  vec3 overlay;
  vec3 col;
  vec3 back;
  back = pow(length(uv),4.)+vec3(.5)*rand(uv);
  back *= rdrBack(uv);
  float obj = map(uv);
  float obs = float(0.05*sin(uv.x*5.+iTime)<uv.y+0.1*sin(iTime*0.1));
vec3 light = obs*2.*vec3(0.15,0.21,0.24)*sat(1.-lenny(uv*vec2(0.5,3.)));

  col = mix(vec3(.0),back+light,clamp(obj*50.,.0,1.))*3.;
float freq = 200.;
back*= vec3(0.5)*((sin(uv.x*freq)*0.5+0.5)+
  (sin(uv.y*freq+iTime)*0.5+0.5))
  +sin((sin(uv.x*20.-iTime*0.5)*uv.y*1.2+iTime*0.1)*20.5);
  overlay = back;
  float a = atan(uv.y,uv.x);
  return clamp(1.-length(uv*1.3),0.,1.  )*(col+overlay)*(1.-(sin(a*30.))*(length(uv)*(sin(a*20.+iTime))));
}

