    uv*=4.;
    vec2 uvf=fract(uv);
    uvf=uvf*uvf*(3.-2.*uvf);
    vec2 bl=floor(uv);
    float vbl=rand(bl);
    float vbr=rand(bl+vec2(1.,0.));
    float vtl=rand(bl+vec2(0.,1.));
    float vtr=rand(bl+vec2(1.,1.));
    float x0=mix(vbl,vbr,uvf.x);
    float x1=mix(vtl,vtr,uvf.x);
  return mix(x0,x1,uvf.y);
