vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}


// -----------------

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

// x: distance, y: index of closest material

vec2 d(vec3 position) {
    float mossDist = sdSphere(position, 0.48) - pow(max(0.0, noise(position * 4.0 + vec3(0.05,0.1,0.02) * iTime)), 0.7) * 0.08;

    float sphereDist = sdSphere(position, 0.48);
    if (mossDist < sphereDist) {
        return vec2(mossDist, 1.0);
    }

    return vec2(sphereDist, 0.0);
}

vec3 gradient(vec3 p, float v) {
    const vec3 eps = vec3(0.0001, 0.0, 0.0);
    return (vec3(d(p + eps.xyy).x, d(p + eps.yxy).x, d(p + eps.yyx).x) - v) / eps.x;
}

// x: distance traveled, y: distance to surface at the intersection point, z: material index
vec3 march(vec3 from, vec3 towards, float prec) {
    vec3 lastSamplePosition = from;
    float lastDistance = 0.0;
    float rayLength = 0.0;
    for(int i = 0; i < 90; i++) {
        float stepLength = max(lastDistance * 0.8, prec);
        vec3 samplePosition = lastSamplePosition + stepLength * towards;
        rayLength += stepLength;

        vec2 distanceAndMaterial = d(samplePosition);

        lastSamplePosition = samplePosition;
        lastDistance = distanceAndMaterial.x;

        if (lastDistance < 0.0) {
            return vec3(rayLength, lastDistance, distanceAndMaterial.y);
        }
    }
    return vec3(0.0,1.0,0.0);
}

vec3 lightSurface(vec3 position, vec3 normal, vec3 toEye, float materialIndex) {
    vec3 toLight = normalize(vec3(-0.3, 0.9, 0.5)  - position);
    float ndotL = max(0.0, dot(normal, toLight));
    float ndotV = max(0.0, dot(normal, toEye));
    float ndotH = max(0.0, dot(normal, normalize(toEye + toLight)));

    float localNoiseValue = noise(position * 160.);

    vec3 albedo = mix(vec3(0.7 + 0.1 * localNoiseValue), mix(vec3(0.3,0.6,0.1), vec3(0.1,0.3,0.), pow(1.-abs(localNoiseValue),2.0)), materialIndex);

    float ambience = mix(0.2,0.5,clamp(dot(normal, vec3(0.0,-1.0,0.0)), 0.0, 1.0));
    const float specular = 0.4;
    float specularPower = mix(8.,2.,materialIndex); // moss is rougher
    float shadow = smoothstep(0.0, 0.1, march(position + normal * 0.05, toLight, 0.002).y);
    float lighting = (ndotL + pow(ndotH, specularPower) * specular) * shadow + ambience;
    float fres = pow(clamp(1.0 - ndotV, 0.0, 1.0), 3.0) * materialIndex * 0.4;

    return lighting * albedo + fres;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * (fragCoord.xy / iResolution.xy - 0.5);
    uv.x *= iResolution.x / iResolution.y;
    const vec3 cameraLookAt = vec3(0.0, 0.0, 0.0);
    vec3 cameraPosition = vec3(0.0, 0.4, 2.0);
    vec3 cameraForward = normalize(cameraLookAt - cameraPosition);
    vec3 cameraRight = cross(cameraForward, vec3(0.0, 1.0, 0.0));
    vec3 cameraUp = cross(cameraRight, cameraForward);
	vec3 rayDirection = normalize(uv.x * cameraRight + uv.y * cameraUp + 3.0 * cameraForward);

    vec3 marchResult = march(cameraPosition, rayDirection, 0.001);
    if (marchResult.y > 0.0) {
        fragColor = vec4(0.0); // ‚Äúsky‚Äù color
    } else {
        vec3 position = cameraPosition + rayDirection * marchResult.x;
        fragColor = vec4(lightSurface(position, gradient(position, marchResult.y), -rayDirection, marchResult.z), 1.0);
    }
}

void DrawPoint(vec2 pos, vec2 uv,float size, vec3 dotColor, inout vec3 sceneColor){

    float d    = distance(uv, pos);
    sceneColor = mix(dotColor, sceneColor, smoothstep(size, size + 0.01, d));

}

void DrawLine(float m, float c, vec2 uv, float size, vec3 lineColor, inout vec3 sceneColor){

    vec2  xy   = vec2(uv.x, uv.x * m + c); 
    float d    = distance(xy, uv);
    sceneColor = mix(lineColor, sceneColor, smoothstep(size, size + 0.01, d));

}

void DrawVector(vec2 origin, vec2 vector, vec2 uv, float size, vec3 lineColor, inout vec3 sceneColor){

          uv  -= origin;
    float v2   = dot(vector, vector);
    float vUv  = dot(vector, uv);
    vec2  p    = vector * vUv/v2;
    float d    = distance(p, uv);
    sceneColor = mix(lineColor, sceneColor, smoothstep(size, size + 0.01, d));

}

void DrawHalfVector(vec2 origin, vec2 vector, vec2 uv, float size, vec3 lineColor, inout vec3 sceneColor){

          uv  -= origin;
    float v2   = dot(vector, vector);
    float vUv  = dot(vector, uv);
    vec2  p    = vector * vUv/v2;
    float d    = distance(p, uv);
    float m    = 1. - step(0.,vUv/v2);
    sceneColor = mix(lineColor, sceneColor, clamp(smoothstep(size, size + 0.01, d)+ m, 0. ,1.)); 
}

void DrawHalfVectorWithLength(vec2 origin, vec2 vector, float len, vec2 uv, float size, vec3 lineColor, inout vec3 sceneColor){

          uv  -= origin;
    float v2   = dot(vector, vector);
    float vUv  = dot(vector, uv);
    vec2  p    = vector * vUv/v2;
    float d    = distance(p, uv);
    float m    = 1. - step(0.,vUv/v2);
          m   += step(len, vUv/v2);
    sceneColor = mix(lineColor, sceneColor, clamp(smoothstep(size, size + 0.01, d)+ m, 0. ,1.)); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // ---------------------------------------------------------
    // ---COORDINATE SETUP

    vec2  uvCoordinate    =  fragCoord/iResolution.xy;
    float aCorreection    =  iResolution.x/iResolution.y;

          uvCoordinate.x *=  aCorreection;
          uvCoordinate   -=  vec2(aCorreection/2., 0.5);

    // ---------------------------------------------------------

    vec3  col             = vec3(0.6,0.6,0.6);
    vec3  mainLinesColor  = vec3(0.8,0.2,0.1);
    vec3  eLinesColor     = vec3(0.6,0.8,0.1);
    vec3  dotColors       = vec3(0.1,0.3,0.);

    vec2  graphOrigin     = vec2(0., 0.);
    float sideLengths     = 0.5;
    vec2  side1           = vec2(cos(iTime*0.4), sin(iTime*0.4));
    vec2  side2           = vec2(2.,0.);
    float side2Sqr        = dot(side2, side2);

    float lineWidth       = 0.0003;

    float numberOfPoints  = 5.;

    DrawHalfVectorWithLength( graphOrigin, normalize( side1), sideLengths,uvCoordinate, lineWidth, mainLinesColor, col);
    DrawHalfVectorWithLength( graphOrigin - vec2(sideLengths, 0.), side2, sideLengths,uvCoordinate, lineWidth, mainLinesColor, col);



    for(float i = 1. ; i <= numberOfPoints; i ++){

        float f    = (i / numberOfPoints);

        vec2 point = graphOrigin + normalize(side1) * f*sideLengths;

        DrawPoint(point, uvCoordinate, 0.01,dotColors, col); 

        // projection on the other line



        float sP  = dot(side2, point - graphOrigin);
        vec2  p   = side2 * sP/side2Sqr;
              p  += graphOrigin;
        DrawPoint(p , uvCoordinate,  0.01,dotColors, col); 

        vec2 e    = point - p; 

        DrawHalfVectorWithLength(p, normalize(e), length(e), uvCoordinate,lineWidth*0.1, eLinesColor, col);
    }




    // Output to screen
    fragColor = vec4(col,1.0);
}

