#define wr vec2(1.,0.8)
#define time iTime
#define PI 3.141592

mat2 rot(float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

void moda (inout vec2 p, float rep)
{
    float per = (2.*PI)/rep;
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a,per)-per*0.5;
    p = vec2(cos(a),sin(a))*l;
}

float wool (vec2 uv)
{
    uv.x = abs(uv.x);
    float c = 0.5-abs(uv.x-uv.y);
    return c;
}

vec4 wool_grid (vec2 uv)
{
    uv *= 10.;
    vec2 ga = mod(uv,wr)-wr*0.5;
    vec2 gb = mod(uv-wr*0.5,wr)-wr*0.5;

    vec2 guv = (dot(ga,ga) < dot(gb,gb)) ? ga : gb;
    guv.y = wool(guv);
    vec2 id = uv-guv;
    return vec4(guv,id);
}


float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

float SDF (vec3 p)
{ 
    p.z += wool_grid(p.xy*0.8).y*0.1;

    p.xz *= rot(p.y*0.5);
    moda(p.xz, 7.);
    p.x -= .8 + sin(p.y*2.+time)*0.5;

    p.xz *= rot(p.y+time);
    moda(p.xz, 5.);
    p.x -= 0.12+cos(p.y*5.-time)*0.15;

    return cyl(p.xzy, 0.1,5.);
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01,0.);
    return normalize(vec3( SDF(p+eps.xyy)-SDF(p-eps.xyy),
                         SDF(p+eps.yxy)-SDF(p-eps.yxy),
                         SDF(p+eps.yyx)-SDF(p-eps.yyx)
                         )
                    );
}

float fre (vec3 n, vec3 rd)
{return pow(clamp(1.-dot(n, -rd),0.,1.),1.5);}

float spe (vec3 l, vec3 n, vec3 rd)
{
    vec3 h = normalize(-rd);
    return pow(max(dot(n,h),0.),10.);
}

vec3 palette (float t, vec3 a, vec3 b, vec3 c, vec3 d)
{return a+b*cos(2.*PI*(c*t+d));}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
	uv *= 0.5;

    vec3 ro = vec3(0.001,0.001,-3.),
        p = ro,
        rd = normalize(vec3(uv,1.)),
        col = vec3(0.);

    bool hit = false;
	float dmax = 10.;

    for (float i=0.; i<64.; i++)
    {
        float d = SDF(p);
        if (d < 0.001)
        {
            hit = true;
            break;
        }
        p += d*rd*0.5;
    }

    if (hit)
    {
        vec3 n = getnorm(p);
        col = palette (fre(n,rd),
                       vec3(0.5),
                       vec3(0.5),
                       vec3(.5),
                       vec3(0.1,0.37,0.43));      
        col += spe(ro,n,rd) * vec3(0.,0.5,0.);
    }

    col *= smoothstep(0.1,0.4,wool_grid(uv).y)*1.-(abs(sin(length(uv*wool_grid(uv).zw)-time))-0.1);

    fragColor = vec4(col,1.);
}

float sdRhombus( in vec2 p, in vec2 b ) 
{
    vec2 q = abs(p);
    float h = clamp((-2.0*dot(q,b)+dot(b,b))/dot(b,b),-1.0,1.0);
    float d = length( q - 0.5*b*vec2(1.0-h,1.0+h) );
    return d * sign( q.x*b.y + q.y*b.x - b.x*b.y );
}

float expImpulse( float k, float x )
{
    float h = k*x;
    return h*exp(1.0-h);
}

float map (in vec2 p) {
    float dist = 10.;

    const float spacer = .01;
    const vec2 rhwid = vec2(.1,.2);

    const vec2 xoffs = vec2(rhwid.x + spacer, 0.);
    for (int i = 0; i < 20; i++) {
    vec2 yoffs = vec2(0., (rhwid.y + spacer) * float(i));
        if (i % 2 == 0) {
        	dist = min(dist, sdRhombus(p + yoffs + xoffs, rhwid));
			dist = min(dist, sdRhombus(p + yoffs - xoffs, rhwid));
        } else {
        	dist = min(dist, sdRhombus(p + yoffs, rhwid));
        	dist = min(dist, sdRhombus(p + yoffs + xoffs * 2., rhwid));
			dist = min(dist, sdRhombus(p + yoffs - xoffs * 2., rhwid));
        }
    }

    return dist;
}

mat2 rot(float a) {
    return mat2(
        cos(a), sin(a),
        -sin(a), cos(a)
        );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (
        fragCoord * 2.)/iResolution.y;
    uv.y -= 2.;
    uv.x += .5;

    // Time varying pixel color
    vec3 col = vec3(1.,.8,0.);
    const vec3 GRAY = vec3(.1);

    for (int i = 0; i < 7; i++) {
        uv.x -= .6;

        vec2 uv_r = rot(.1) * uv;
        if (map(uv_r) < .0 && abs(uv_r.x) < .25) {
            float pulse = expImpulse(1., fract(iTime - .1 * float(i) + uv_r.y * .1));
            col = mix(col, GRAY, 1.-pulse);
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

