    st += rotate2D(st,iMouse.y /100.);
    color = mix(color, Flower(rotate2D(st, PI * .1), vec3(0.70,1.00,0.73), 8.), sin(time));
    st += rotate2D(st, iMouse.x /50.);
    color = mix(color, Flower(rotate2D(st, PI * -.1), vec3(0.61,0.05,0.05), 6.), cos(time / 4.));
