void mainImage( out vec4 O, vec2 U )
{
    vec2 uv = U / iResolution.xy * vec2(4,2) + vec2(-2,1);
    float pi = 3.1415,
        time = iTime * .1,
           t = time*pi,
           d = length(uv)-.3;

    uv *= mat2( cos(t), -sin(t)*d*.5, sin(t), cos(t));

    float  a = atan(uv.y, uv.x),
          tt = fract(iTime) *.5 -.5,
  timeWobble = cos(t+d) *.01*d;
    float v = 0.;

    for(float j = 0.; j<10.; j+=.5){
        float q = j==0. ? tt+.5 : 1.;
        for(float i = 0.; i<6.28; i += pi/8. ){
            vec2 u = uv + vec2(sin(i),cos(i)) * (j-timeWobble+tt-.25) *q;
            v += smoothstep( .0, .05* sin(time)*(1.-d)*time, 
                             length(u)- (smoothstep(0., 3.,j+tt)+timeWobble*3.) * q );
        } 
    }
    O = .5+ .5* sin(  pi/2.* ( mod(v+iMouse, 4.) + sin(vec4(10,5,-10,0)*time) )
                     -(d*3.-a*3.) + t );
}

//aaStep by liqwidice
float aaStep(float edge, float gradient)
{
    float halfPix = fwidth(gradient) / 2.0;
    float low = edge - halfPix;
    float hi  = edge + halfPix;
    return clamp((gradient - low) / (hi - low), 0.0, 1.0);
}

mat2 rotMat(float angle)
{
    float s = sin(angle);
    float c = cos(angle);

    mat2 rotmat = mat2(c, -s, s, c);

    return rotmat;
}
float middleSquare(vec2 uv)
{
    return aaStep(dot(abs(uv), vec2(1, 1)), 0.27);
}

float sideThingy(vec2 uv, bool mirrored)
{
    if(mirrored)
        uv *= -1.0;

    uv *= 1.2;
    uv.x += abs(sin(iTime * 0.4) * 0.2) + 0.3;
    float piece = middleSquare(uv);

    uv.x -= 0.20;
    piece -= middleSquare(uv);

    return clamp(piece, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 tex = texture(iChannel0, fragCoord / iResolution.xy).rgb;

    float ms = middleSquare(vec2(uv));       
    float sides = sideThingy(uv, false);
    sides += sideThingy(vec2(uv.x, uv.y), true);

    ms += sides;

    vec3 result = mix(vec3(ms), tex, ms);
    result = mix(result, vec3(1), 1.0 - ms);

    fragColor = vec4(result, 1.0);
}

float hash1( int n ) {
    // integer hash copied from Hugo Elias
	n = (n << 13) ^ n;
    n = n * (n * n * 15731 + 789221) + 1376312589;
    return float( n & ivec3(0x7fffffff))/float(0x7fffffff);
}

//cross
float tile1(vec2 uv){
    float d = min(abs(uv.x),abs(uv.y));
    return d;
}

//two circles
float tile2(vec2 uv){
    if(uv.y<-uv.x) uv.xy = -uv.xy;
	float d = abs(distance(uv,vec2(1,1))-1.);  
    return d;
}

//rotation of tile2
float tile3(vec2 uv){
    return tile2(vec2(-uv.x,uv.y));
}

float truchet(vec2 p){
    vec2  tile = floor(p);
    float id   = tile.x*10000.+tile.y;
	      p   = (fract(p)-.5)*2.;

    float t = hash1(int(id));
    float d = 1e20;
    if(t<0.33){
        d = tile1(p);
    }else if(t<0.67){
        d = tile2(p);
    }else{
        d = tile3(p);
    }

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = cos(iTime*.5)*2.+3.;
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y*zoom;

    float e = zoom*2./min(iResolution.x,iResolution.y);
   	vec3 col = vec3(0);

  	float d = truchet(uv);

    col = vec3(smoothstep(e*5.,e,d));

    fragColor = vec4(col,1.0);
}

