// Asteroid flythrough
// Use the mouse; switch forward/backward views when mouse in small frame.
// Based on "Asteroid Field" with Voronoi displacements from "Rainbow Cavern".

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCylAnDf (vec3 p, float r, float w, float h);
float PrCapsDf (vec3 p, float r, float h);
float SmoothMin (float a, float b, float r);
float Minv3 (vec3 p);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 HsvToRgb (vec3 c);
float Hashfv2 (vec2 p);
vec3 Hashv3v3 (vec3 p);
float Fbm1 (float p);
float Fbm3 (vec3 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

mat3 flMat, flyerMat;
vec4 astV, astCs;
vec3 bGrid, trkAx, trkAy, trkFx, trkFy, flPos, flyerPos, qHit, ltDir, cId, cHash;
float tCur, dstFar, spd, szFacFl, astOcc;
int idObj;
const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

vec3 TrackPath (float t)
{
  return vec3 (dot (trkAx, cos (trkFx * t)), dot (trkAy, cos (trkFy * t)), t);
}

vec3 TrackVel (float t)
{
  return vec3 (- dot (trkFx * trkAx, sin (trkFx * t)),
     - dot (trkFy * trkAy, sin (trkFy * t)), 1.);
}

vec3 TrackAcc (float t)
{
  return vec3 (- dot (trkFx * trkFx * trkAx, cos (trkFx * t)),
     - dot (trkFy * trkFy * trkAy, cos (trkFy * t)), 0.);
}

float VPoly (vec3 p)
{
  vec3 ip, fp, a, g, w;
  ip = floor (p);
  fp = fract (p);
  a = vec3 (4.);
  for (float gz = -1.; gz <= 1.; gz ++) {
    for (float gy = -1.; gy <= 1.; gy ++) {
      for (float gx = -1.; gx <= 1.; gx ++) {
        g = vec3 (gx, gy, gz);
        w = g + 0.7 * Hashv3v3 (ip + g) - fp;
        a.z = dot (w, w);
        if (a.z < a.x) a.xy = a.zx;
        else a.y = min (a.z, a.y);
      }
    }
  }
  return sqrt (a.y - a.x);
}

void AstState ()
{
  float s, r;
  cHash = Hashv3v3 (cId);
  if (cHash.x > astOcc) {
    s = fract (64. * length (cHash));
    s *= s;
    r = 0.5 * bGrid.x * min (0.8 + 0.3 * cHash.x * (1. - s) * abs (sin (3. * pi * cHash.y * (1. - s))), 1.);
    astV = vec4 ((r - 1.1 * (0.15 - 0.07 * s)) * vec3 (sin ((cHash.z * tCur + cHash.x) +
       vec2 (0.5 * pi, 0.)), 0.), 0.15 - 0.07 * s);
    astCs = vec4 (sin (2. * pi * (cHash.x + 0.09 * cHash.z * tCur) + vec2 (0.5 * pi, 0.)),
       sin (2. * pi * (cHash.y + 0.11 * cHash.z * tCur) + vec2 (0.5 * pi, 0.)));
  }
}

vec3 AstRot (vec3 p)
{
  p.xy = Rot2Cs (p.xy, astCs.xy);
  p.zy = Rot2Cs (p.zy, astCs.zw);
  return p;
}

vec3 AstRotT (vec3 p)
{
  p.zy = Rot2Cs (p.zy, vec2 (astCs.z, - astCs.w));
  p.xy = Rot2Cs (p.xy, vec2 (astCs.x, - astCs.y));
  return p;
}

vec3 AstCell (vec3 p)
{
  cId.xy = floor (p.xy / bGrid.xy);
  p.z += 0.2 * spd * tCur * Hashfv2 (cId.xy) * smoothstep (6., 10., length (cId.xy));
  cId.z = floor (p.z / bGrid.z);
  return p;
}

float AstDf (vec3 p)
{
  float d;
  d = PrRoundBoxDf (p, 0.6 * astV.w * (1. - 0.5 * cHash), 0.4 * astV.w);
  if (d < 0.07) d -= 0.04 * VPoly ((8. + 2. * cHash.y) * p);
  return 0.7 *  d;
}

float AstRay (vec3 ro, vec3 rd)
{
  vec3 cIdP, p, s, rdi;
  float dHit, d, da, eps;
  eps = 0.0005;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  rdi = 1. / rd;
  cIdP = vec3 (-999.);
  dHit = eps;
  for (int j = 0; j < 80; j ++) {
    p = ro + dHit * rd;
    p = AstCell (p);
    if (cId != cIdP) {
      AstState ();
      cIdP = cId;
    }
    s = (bGrid * (cId + step (0., rd)) - p) * rdi;
    d = abs (Minv3 (s)) + eps;
    if (cHash.x > astOcc && length (cId.xy - floor (TrackPath (bGrid.z * cId.z).xy / bGrid.xy)) >= 1.)
       d = min (d, AstDf (AstRot (p - (bGrid * (cId + 0.5) + astV.xyz))));
    dHit += d;
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 AstNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.005, -0.005);
  v = vec4 (- AstDf (p + e.xxx), AstDf (p + e.xyy), AstDf (p + e.yxy), AstDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 AstCol (vec3 ro, vec3 rd)
{
  vec3 vn, p, col;
  p = AstCell (ro);
  AstState ();
  p = AstRot (p - (bGrid * (cId + 0.5) + astV.xyz));
  vn = AstNf (p);
  vn = VaryNf (32. * (0.5 + 0.5 * cHash.x) * p, vn, 16. * (0.5 + 0.5 * cHash.y));
  vn = AstRotT (vn);
  col = HsvToRgb (vec3 (0.02 + 0.12 * cHash.x, 0.1 + 0.3 * cHash.y,
     min (0.6 + 0.3 * cHash.z + 0.5 * Fbm3 (4. * p), 1.)));
  col = col * (0.1 + 0.1 * max (- dot (vn, ltDir), 0.) + 0.8 * max (dot (vn, ltDir), 0.)) +
     0.2 * vec3 (1.) * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
  return col;
}

float FlyerEngDf (vec3 p)
{
  vec3 q;
  float d;
  p = flyerMat * (p - flyerPos) / szFacFl;
  q = p - vec3 (0., 0.3, -0.6);
  q.xz = abs (q.xz) - vec2 (1.6, 1.3);
  q.xy = Rot2D (q.xy, -32. * tCur);
  d = max (PrCylDf (q, 0.4, 0.4), 0.05 - min (abs (q.x), abs (q.y)));
  return szFacFl * d;
}

float FlyerEngRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 50; j ++) {
    d = FlyerEngDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0002 || dHit > dstFar) break;
  }
  return dHit;
}

float FlyerDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d, h;
  dMin = dstFar / szFacFl;
  p = flyerMat * (p - flyerPos) / szFacFl;
  q = p;
  h = 0.5 - 0.05 * (q.z + 0.3) * (q.z + 0.3);
  q.y -= h;
  d = PrRoundBoxDf (q, vec3 (0.9 - 0.06 * (q.z + 0.5) * (q.z + 0.5), h, 2.7), 0.15);
  q = p + vec3 (0., -0.3, 0.6);
  qq = q;
  qq.xz = abs (qq.xz) - vec2 (1.6, 1.3);
  d = min (min (min (d, PrCylAnDf (qq, 0.44, 0.04 - 0.03 * (qq.z / 0.48) * (qq.z / 0.48), 0.48)),
     PrCylDf (qq, 0.15 - 0.12 * (qq.z / 0.45) * (qq.z / 0.45), 0.45)),
     PrRoundBoxDf (qq, vec3 (0.4, 0.01, 0.1), 0.01));
  q = p;
  q.y -= 0.3;
  qq = q;
  qq.z = abs (qq.z + 0.6) - 1.3;
  d = SmoothMin (d, PrRoundBoxDf (qq, vec3 (1.15, 0.03 - 0.05 * abs (qq.z), 0.3), 0.01), 0.05);
  DMINQ (1);
  q = p;
  q.yz -= vec2 (0.52, 0.5);
  d = PrCapsDf (q, 0.6, 1.);
  DMINQ (2);
  return 0.7 * szFacFl * dMin;
}

float FlyerRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = FlyerDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0002 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 FlyerNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (- FlyerDf (p + e.xxx), FlyerDf (p + e.xyy), FlyerDf (p + e.yxy), FlyerDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 FlyerCol ()
{
  vec4 col4;
  float ax;
  col4 = vec4 (0.9, 1., 0.9, 0.3);
  ax = abs (qHit.x);
  if (idObj == 1) {
    if (qHit.z > 2. && length (vec2 (ax - 0.2, qHit.y + 0.3)) < 0.12) {
      if (length (vec2 (ax - 0.2, qHit.y + 0.3)) < 0.1) col4 = vec4 (1.1, 1.1, 0.8, -2.);
      else col4 = vec4 (0.5, 0.5, 0.7, 0.3);
    } else if (qHit.z < -2. && length (vec2 (abs (ax - 0.3) - 0.15, qHit.y + 0.1)) < 0.1) {
      if (length (vec2 (abs (ax - 0.3) - 0.15, qHit.y + 0.1)) < 0.08)
         col4 = vec4 (0.8 + 0.2 * sin (2. * pi * tCur), 0., 0., -2.);
      else col4 = vec4 (0.5, 0.5, 0.7, 0.3);
    } else if (abs (abs (qHit.z + 0.15) - 1.3) < 0.1 && length (vec2 (ax - 1.6, qHit.y)) < 0.2) {
      col4 = vec4 (1., 0., 0., 0.3);
    } else if (ax < 0.02 || qHit.z < -2.8 && mod (8. * qHit.x + 0.1, 1.) < 0.2 ||
       qHit.z > 2.8 && mod (12. * qHit.y + 0.1, 1.) < 0.2 ||
       abs (qHit.z + 1.3) < 0.9 && ax < 0.8 && mod (6. * qHit.x + 0.1, 1.) < 0.15 ||
       abs (qHit.z + 0.7) < 0.5 && abs (qHit.y - 0.2) < 0.3 && mod (12. * qHit.y + 0.1, 1.) < 0.15) {
      col4 = vec4 (0.6, 0.6, 0.6, 0.3);
    } else if (abs (abs (qHit.z + 0.15) - 1.3) < 0.1 && ax > 1.12) {
      col4 = vec4 (0.6, 0.6, 0.6, 0.3);
    } else if (length (vec2 (abs (qHit.z + 0.6) - 1.3, ax - 1.6)) < 0.1 && abs (qHit.y) > 0.44) {
       col4 = mix (0.8 * col4, vec4 (0., 1., 0., -2.), step (0.5, mod (0.5 * tCur, 1.)));
    }
  } else if (idObj == 2) {
    if (min (ax, abs (qHit.z - 1.)) > 0.03) col4 = vec4 (0.1, 0.1, 0.2, -1.);
  }
  return col4;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstAst, dstFlyer, dstEng, refFac;
  dstEng = FlyerEngRay (ro, rd);
  dstFlyer = FlyerRay (ro, rd);
  if (dstFlyer < dstEng) dstEng = dstFar;
  dstAst = AstRay (ro, rd);
  col = vec3 (0.1, 0.07, 0.03);
  if (min (dstFlyer, dstAst) < dstFar) {
    if (dstFlyer < dstAst) {
      ro += dstFlyer * rd;
      vn = FlyerNf (ro);
      col4 = FlyerCol ();
      col = col4.rgb;
      refFac = 0.;
      if (col4.a >= 0.) {
        col = col * (0.2 + 0.1 * max (dot (vn, - ltDir), 0.) + 0.7 * max (dot (vn, ltDir), 0.)) +
           col4.a *  pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
        refFac = 0.3;
      } else if (col4.a == -1.) refFac = 0.7;
      if (refFac > 0.) {
        rd = reflect (rd, vn);
        ro += 0.01 * rd;
        dstAst = AstRay (ro, rd);
        ro += dstAst * rd;
        col = mix (col, AstCol (ro, rd), refFac);
      }
    } else if (dstAst < dstFar) {
      ro += dstAst * rd;
      col = AstCol (ro, rd);
    }
  }
  col = mix (vec3 (0.1, 0.07, 0.03), col, 
     exp2 (- 10. * max (min (min (dstFlyer, dstAst), dstFar) / dstFar - 0.7, 0.)));
  if (dstEng < min (dstAst, dstFar)) col = mix (col, vec3 (0.4, 0.6, 1.) *
     (0.8 + 0.4 * Fbm1 (32. * tCur)), 0.7);
  return clamp (col, 0., 1.);
}

void FlyerPM (float s, float rl, float vu)
{
  vec3 vel, ort, ca, sa;
  float el, az;
  flPos = TrackPath (s);
  vel = normalize (TrackVel (s));
  el = - asin (vel.y);
  az = atan (vel.z, vel.x) - 0.5 * pi;
  rl = clamp (rl - 3. * TrackAcc (s).x, -0.4 * pi, 0.4 * pi);
  ort = (vu >= 0.) ? vec3 (el, az, rl) : vec3 (- el, az + pi, - rl);
  ca = cos (ort);
  sa = sin (ort);
  flMat = mat3 (ca.z, - sa.z, 0., sa.z, ca.z, 0., 0., 0., 1.) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
          mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col, ori, ca, sa;
  vec2 canvas, uv, mMid, ut, mSize;
  float el, az, rl, zmFac, asp, vuDir;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  mSize = (1./6.) * vec2 (asp, 1.);
  mMid = vec2 (1. / mSize.y, 1. / mSize.y - 1.) * mSize * vec2 (-1. + 1./6., 1.);
  ut = abs (uv - mMid) - mSize;
  vuDir = 1.;
  if (max (ut.x, ut.y) < 0.) {
    uv = (uv - mMid) / mSize.y;
    vuDir = -1.;
  }
  szFacFl = 0.005;
  spd = 1.;
  astOcc = 0.5;
  tCur = mod (tCur, 10800.) + 10.;
  az = 0.;
  el = 0.;
  rl = 0.;
  if (mPtr.z > 0.) {
    if (mPtr.x > -0.5 + 1./6. || mPtr.y < 0.5 - 1./6.) {
      az = 2. * pi * mPtr.x;
      el = 0.6 * pi * mPtr.y;
    } else vuDir *= -1.;
  }
  el += 0.005 * pi * (Fbm1 (0.1 * tCur) - 0.5);
  az += 0.005 * pi * (Fbm1 (0.1 * tCur + 1.) - 0.5);
  rl += 0.02 * pi * (Fbm1 (0.3 * tCur + 2.) - 0.5);
  ori = vec3 (el, az, rl);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
          mat3 (ca.z, - sa.z, 0., sa.z, ca.z, 0., 0., 0., 1.);
  bGrid = vec3 (1.);
  trkAx = vec3 (0.7, 0.8, 1.2);
  trkAy = vec3 (1., 1.1, 0.9);
  trkFx = vec3 (0.43, 0.33, 0.23);
  trkFy = vec3 (0.41, 0.31, 0.12);
  FlyerPM (spd * tCur + ((vuDir > 0.) ? 0.5 + 0.45 * sin (0.2 * tCur) :
     - (0.25 + 0.2 * sin (0.2 * tCur))), 0.03 * pi * (Fbm1 (1.3 * tCur) - 0.5), 0.);
  flyerPos = flPos;
  flyerMat = flMat;
  FlyerPM (spd * tCur, 0., vuDir);
  ro = flPos;
  zmFac = 3.;
  rd = normalize (vec3 (uv, zmFac));
  rd = rd * flMat;
  rd = vuMat * rd;
  ltDir = normalize (vec3 (0., 1., -1.));
  ltDir.xz = Rot2D (ltDir.xz, 0.2 * pi * sin (0.1 * pi * tCur));
  ltDir.yz = Rot2D (ltDir.yz, 0.1 * pi * sin (0.06 * pi * tCur));
  if (vuDir < 0.) ltDir *= -1.;
  dstFar = 25.;
  col = ShowScene (ro, rd);
  if (max (ut.x, ut.y) < 0. && min (abs (ut.x), abs (ut.y)) * canvas.y < 2.)
     col = vec3 (0.1, 0.5, 0.1);
  fragColor = vec4 (pow (col, vec3 (0.8)), 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., h * clamp (p.z / h, -1., 1.))) - r;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec3 Hashv3v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec3 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3))) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec4 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3), dot (p + e.xxy, cHashVA3))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

float sphere(vec3 p, float r)
{
	return length(p) - r;
}

float cylinder(vec2 p, float r)
{
	return length(p) - r;
}

float box(vec3 p, vec3 s)
{
	vec3 ap = abs(p) - s;
    return length(max(vec3(0.0), ap)) + min(0.0, max(ap.x, max(ap.y, ap.z)));
}

float roundBox(vec3 p, vec3 s, float r)
{
  vec3 rb = abs(p) - s;
  return length(max(rb, 0.0)) + min(max(rb.x, max(rb.y, rb.z)), 0.0) - r;
}

mat2 rot(float a)
{
	float ca = cos(a);
    float sa = sin(a);
	return mat2(ca, sa, -sa, ca);
}

float smin( float a, float b, float k )
{
    float h = max(k - abs(a - b), 0.0) / k;
    return min(a, b) - h * h * k * (1.0 / 4.0);
}

float ship(vec3 p) {
    float s = sphere(p, 0.4);
    float b = max(cylinder(p.yz + vec2(0.1, 0.0), 0.2), abs(p.x) - 0.8);
    float d = min(s, b);

    float s2 = sphere(p + vec3(1.0, 0.1, 0.0), 0.3);
    float s3 = sphere(p + vec3(-1.0, 0.1, 0.0), 0.3);

    float sideSpheres = min(s2, s3);
    d = min(d, sideSpheres);

    float c1 = roundBox(p + vec3(1.0, 0.1, -0.8), vec3(0.11, 0.1, 0.8), 0.075);
    float c2 = roundBox(p + vec3(1.0, -0.10, -1.2), vec3(0.1, 0.3, 0.4), 0.075);
    d = min(d, smin(c1, c2, 0.01));

    float c3 = roundBox(p + vec3(-1.0, 0.1, -0.8), vec3(0.11, 0.1, 0.8), 0.075);
    float c4 = roundBox(p + vec3(-1.0, -0.10, -1.2), vec3(0.1, 0.3, 0.4), 0.075);
    d = min(d, smin(c3, c4, 0.01));

    return d;
}

vec3 tunnel(vec3 p) {
	vec3 off = vec3(0.0);
    off.x += sin(p.z * 0.3);
    off.y += cos(p.z * 0.5) * 0.1;
    return off;
}

float map(in vec3 p)
{
    p += tunnel(p);

    vec3 p1 = p;
    float m = iTime * 4.0;
    p1.z -= m;

    p1.zy *= rot(0.1);
    float t = clamp(sin(-p.z * 0.3), -1.0, 1.0) * 0.35;
   	p1.yx *= mat2(1.0, t, -t, 1.0);
    float s = ship(p1);


    vec3 bp = p;
    bp.z = fract(bp.z / 2.0 - 0.5) - 0.5;
    float bPlane = box(bp + vec3(0.0, -0.75, 0.0), vec3(2.0, 0.2, 1.0));

    vec3 sp = p;
    sp.xy *= rot(1.0);
    sp.z = fract(sp.z / 0.0 - 0.5);
    float rPlane = box(sp + vec3(1.4, -2.0, 1.0), vec3(1.0, 0.2, 1.0));

    vec3 sp2 = p;
    sp2.yx *= rot(1.0);
    sp2.z = fract(sp2.z / 0.0 - 0.5);
    float lPlane = box(sp2 + vec3(-1.4, -2.0, 1.0), vec3(1.0, 0.2, 1.0));

    return min(s, min(bPlane, min(rPlane, lPlane)));
}

vec3 norm(vec3 p)
{
	vec2 off = vec2(0.01, 0.0);
    return normalize(map(p) - vec3(map(p - off.xyy), map(p - off.yxy), map(p - off.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1.0);

    vec3 s = vec3(0.0, -2.0, -4.0);
    vec3 t = vec3(0.0, 0.0, 3.0);

    float m = iTime * 4.0;
    s.z += m;
    t.z += m;

    s -= tunnel(s);
    vec3 offT = tunnel(t);
    t -= offT;

    vec3 cz = normalize(t - s);
    vec3 cx = normalize(cross(cz, vec3(0.0, 1.0, 0.0) - offT * 0.2));
    vec3 cy = normalize(cross(cz, cx));

    vec3 r = normalize(cx * uv.x + cy * uv.y + cz);

    vec3 col = vec3(0.0);
    vec3 l = normalize(vec3(0.0, -1.0, 0.0));

    vec3 p = s;
    for(int i = 0; i < 100; ++i)
    {
    	float d = map(p);
        if(d < 0.001) {
            vec3 n = norm(p);
            float f = pow(1.0 - dot(n, -r), 1.0);
            col += max(0.0, dot(n, l));

            break;
        }
        if(d > 100.0) break;
        p += d * r;
    }

    fragColor = vec4(col, 1.0);
}

//CC0 1.0 Universal https://creativecommons.org/publicdomain/zero/1.0/
//To the extent possible under law, Blackle Mori has waived all copyright and related or neighboring rights to this work.

vec3 erot(vec3 p , vec3 ax, float ro) {
  return mix(dot(p,ax)*ax, p, cos(ro)) + sin(ro)*cross(ax,p);
}

#define FK(k) floatBitsToInt(cos(k))^floatBitsToInt(k)
float hash(vec2 p) {
  int x = FK(p.x);int y = FK(p.y);
  return float((x*x+y)*(y*y-x)-x)/2.147e9;
}

vec2 linedist(vec2 p, vec2 a, vec2 b) {
  vec2 pa = p-a;
  float k = dot(p-a,b-a)/dot(b-a,b-a);
  float s = sign(dot(vec2(-pa.y,pa.x),b-a));
  float d = distance(p, mix(a,b,clamp(k,0.,1.)));
  return vec2(d,s);
}

float triangle(vec2 p, vec2 dim) {
  vec2 a = vec2(0,dim.y);
  vec2 b = vec2(dim.x,-dim.y);
  vec2 ldx = min(linedist(p, a, b), linedist(p, b, vec2(-b.x,b.y)));
  return -ldx.x*ldx.y;
}

float torus(vec3 p, vec2 di) {
  vec2 crd = vec2(length(p.xy), p.z);
  return length(crd-vec2(di.x,0)) - di.y;
}

float cylinder(vec3 p, vec2 di) {
  vec2 crd = vec2(length(p.xy), p.z);
  crd.y = abs(crd.y)-di.y;
  crd.x -= di.x;

  float sg = sign(crd.x)+sign(crd.y);
  return sg == 2. ? length(crd) : max(crd.x,crd.y);
}

float cone(vec3 p, vec2 di) {
  vec2 crd = vec2(length(p.xy), p.z);
  return triangle(crd, di);
}

float scene(vec3 p) {
  vec3 tor = p;
  tor = erot(tor, vec3(0,0,1), iTime);
  tor = erot(tor, vec3(0,1,0), 0.6);

  vec3 cyl = p - vec3(0,-1,0);
  cyl = erot(cyl, vec3(0,1,0), iTime);

  vec3 con = p - vec3(0,1,0);
  con = erot(con, vec3(0,1,0), -iTime);

  float best = cylinder(cyl, vec2(0.3, 0.8))-0.01;
  best = min(best, torus(tor, vec2(0.8,0.2)));
  best = min(best, cone(con, vec2(0.5,0.7)))-0.01;
  return min(best, p.z+0.9);
}

bool flor(vec3 cam, vec3 init, inout vec3 inter) {
  vec3 flooror = vec3(0,0,-0.9);
  vec3 floornor = vec3(0,0,1);
  float d = dot(cam, vec3(0,0,1));
  if (d > 0.) return false;

  float t = dot(flooror-init, floornor)/d;
  inter = t*cam+init;
  return true;
}

mat3 eps = mat3(0.001);
#define AP(f,k) vec3(f(k[0]),f(k[1]),f(k[2]))
vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p)-eps;
  return normalize(scene(p) - AP(scene, k));
}

vec3 shade(float d) {
  float str = cos(d*200.+3.1415)*0.2+0.8 * pow((exp(0.)-exp(-abs(d))), 0.3);
  return d > 0. ? vec3(1,0.5,0)*str : vec3(0,0.5,1)*str;
}

float skycol(vec3 angl) {
  return sqrt(max(angl.z,0.))*0.5+0.5;
}

float skyshade(vec3 norm) {
  float d = pow(norm.z+0.3,2.)*pow(norm.z*0.5+0.5,1.)/pow(1.3,2.)*0.9+0.1;
  return sqrt(sqrt(d))*0.8+0.2;
}

float ao(vec3 p, vec3 n, float sc) {
  float d1 = 0.001;
  float d2 = scene(p+n*sc);
  return sqrt(((d2-d1)/sc)*0.5+0.5);
}

float comp(float p) {
  //return p;
  return pow(abs(p),5.)*sign(p);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 cam = normalize(vec3(1.4, uv));
  vec3 init = vec3(-4.,0.,0.);
  bool hit = false;
  vec3 p = init;
  for (int i = 0; i < 150; i++) {
    float dist = scene(p);
    if ( abs(dist) < 0.001) { hit = true; break; }
    if (distance(p, init) > 100.) break;
    p+=cam*dist;
  }
  vec3 inter;
  if (flor(cam, init, inter)) {
    if (distance(inter, init) < distance(init, p) || !hit) {hit=true; p = inter;}
  }
  float noise = comp(hash(uv*iTime));
  if (!hit) {
    fragColor.xyz = vec3(skycol(cam)) + noise*0.05;
    return;
  }
  vec3 n = norm(p);
  float fog = exp(-distance(init,p)*0.03)/exp(0.)*0.4+0.6;
  float aoo = pow(ao(p, n, 0.1)*ao(p, n, 0.2)*ao(p, n, 0.33)*ao(p, n, 0.66)*ao(p, n, 1.),1./5.);
  fragColor.xyz = vec3(skyshade(n)*0.8*aoo)*fog;
  fragColor.xyz += noise*0.05;
}

// flownoise variant of https://shadertoy.com/view/WdGXWG
// curve variant of 2D https://shadertoy.com/view/wdKXzd

// gradient noise derived from https://www.shadertoy.com/view/XdXGW8

float _z;
vec2 hash( vec2 x ) 
{
 // float s = 0.;                      // standard Perlin noise
    float s = mod(x.x+x.y,2.)*2.-1.;   // flow noise checkered rotation direction
 // float s = 5.;                      // flow noise universal rotation direction
 // s *= iTime;                        // same rotation speed at all scales
    s *= iTime/ _z;                    // rotation speed increase with small scale
    const vec2 k = vec2( .3183099, .3678794 );
    x = x*k + k.yx;
    return ( -1. + 2.*fract( 16. * k*fract( x.x*x.y*(x.x+x.y)) ) ) 
        *  mat2(cos( s + vec4(0,33,11,0))); // rotating gradients. rot: https://www.shadertoy.com/view/XlsyWX
}

float noise( vec2 p )
{
    vec2 i = floor( p ),
         f = fract( p ),
	     u = f*f*(3.-2.*f);

#define P(x,y) dot( hash( i + vec2(x,y) ), f - vec2(x,y) )
    return mix( mix( P(0,0), P(1,0), u.x),
                mix( P(0,1), P(1,1), u.x), u.y);
}

float perlin( vec2 p )  //fractal noise
{	
    mat2 m = mat2(2.); // mat2( 1.6,  1.2, -1.2,  1.6 );
    float v  = 0.,s = 1.;
    for( int i=0; i < 7; i++, s /= 2. ) { _z = s; // for flownoise
        v += s*noise( p ); p *= m;
    }
    return v;
}

