mat2 rotate2d(float _angle, float speed){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float box(in vec2 _st, in vec2 _size){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size,
                        _size+vec2(0.001),
                        _st);
    uv *= smoothstep(_size,
                    _size+vec2(0.001),
                    vec2(1.0)-_st);
    return uv.x*uv.y;
}

float crossN(in vec2 _st, float _size){
    return  box(_st, vec2(_size,_size/4.)) +
            box(_st, vec2(_size/4.,_size));
}


vec3 radar(vec2 st, float radius, vec3 radarColor, float speed){
    st -= vec2(0.5);
    float num = fract(iTime*speed) *2.*PI;
    st *= rotate2d(num,speed);
    st += vec2(0.5);
    float intensity = smoothstep(0.16, 0.3, distance(st, vec2(0.5)))
        *smoothstep(distance(vec2(0.5),st), distance(vec2(0.5),st)+.005, radius+.01);
    intensity +=  ((smoothstep(0.4,0.5,st.x) - smoothstep(0.5,0.53,st.x))*
        smoothstep(distance(st, vec2(0.5)), distance(st, vec2(0.5))+.05, radius+.02))
        *smoothstep(0.45, 0.51, st.y);
    return vec3(intensity*radarColor);
}

vec3 graientRing(vec2 st, float radius, vec3 radarColor){
    float intensity = smoothstep(0.1, 0.3, distance(st, vec2(0.5)))
        *smoothstep(distance(vec2(0.5),st), distance(vec2(0.5),st)+.005, radius+.01);
    return vec3(intensity*radarColor);
}

vec3 ring(vec2 st, float radius, float thickness, vec3 ringColor){
    float intensity = smoothstep(radius*.93-thickness, radius+0.01-thickness, distance(st, vec2(0.5)))
         - smoothstep(radius, radius+0.01, distance(st, vec2(0.5)));

    return vec3(intensity*ringColor);
}

vec3 ringWithGaps(vec2 st, float radius, float thickness, vec3 ringColor){
    vec3 ring = ring(st, radius, thickness, ringColor);
    float gap = crossN(st, radius*3.) * (step(distance(st, vec2(0.5)), radius+.01)
          -step(distance(vec2(0.5),st), radius-.01));
    ring = ring - gap;
    if(ring.x < .000001){
        ring = vec3(0.);
    }
    return ring;
}

vec3 spinningRing(vec2 st, float radius, float thickness, vec3 ringColor, float time, float speed, bool clockwise){
    vec3 color = ringWithGaps(st, radius, thickness, ringColor);
    //return color;
    st -= vec2(0.5);
    float modifier = 1.;
    if(!clockwise){modifier=-1.;}
    // rotate the space
    float num = fract(u_time*speed) *2.*PI * modifier;
    st *= rotate2d(num,speed);
    st += vec2(0.5);
    color = ringWithGaps(st, radius, thickness, ringColor);

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.y;
    st.x -= .45;
    vec3 color = vec3(0.362,0.172,0.960)*st.y*abs(sin(iTime));

    color += spinningRing(st, 0.4, 0.01, vec3(0.995,0.502,0.149), iTime, .1, false);
    color += spinningRing(st, 0.3, 0.01, vec3(0.387,0.552,0.970), iTime, .3, true);
    color += graientRing(st, 0.15*abs(sin(iTime)), vec3(0.995,0.100,0.462));
    color += radar(st, .2, vec3(0.312,0.995,0.503), .6);

    fragColor = vec4(color,1.0);
}

vec2 N22(vec2 p)
{
	vec3 a = fract(p.xyx*vec3(123.34,234.34,345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
