// https://en.wikipedia.org/wiki/Specular_reflection#Law_of_reflection
// https://en.wikipedia.org/wiki/Lambertian_reflectance

#define M_PI 3.14159265358979


struct Hit {
	vec3 c;
    vec3 p;
    bool h;
};

vec3 traceAmbient()
{
    // ambient
    float ambientStrength = 0.1;
    vec3 ambient = ambientStrength * vec3(1.0);
    return ambient;
}

vec3 traceDiffuse(vec3 lightDir, vec3 n)
{
    // diffuse
    float diff = max(dot(n, lightDir), 0.0);
    vec3 diffuse = diff * vec3(1.0);

    vec3 c = vec3(1.0);

    return diffuse * c;
}

vec3 traceSpecular(vec3 lightDir, vec3 hit, vec3 l0, vec3 n)
{
    // specular
    vec3 viewDir = normalize(hit - l0);
    vec3 reflectDir = reflect(lightDir, n); 
    float spec = pow(max(dot(viewDir, reflectDir), 0.0), 64.0);
    return vec3(spec);
}

vec3 getLightPos()
{
    float t0 = iTime;
    vec3 lightPos = vec3(0.0, 6.0, -10.0);
    lightPos.z += cos(t0) * 8.0;
    lightPos.x += sin(t0) * 8.0;
    return lightPos;
}


vec3 traceLight(vec3 n, vec3 hit, vec3 l0)
{
    vec3 lightDir = normalize(getLightPos() - hit);

    vec3 ambient = traceAmbient();

    vec3 diffuse = traceDiffuse(lightDir, n);

    vec3 spec = traceSpecular(lightDir, hit, l0, n);

    return ambient + diffuse + spec;
}

// https://en.wikipedia.org/wiki/Line%E2%80%93plane_intersection
Hit tracePlane(vec3 l0, vec3 l)
{
 	// plane equation
    // (p - p0) . n = 0

    // t = ((p0 - l0) . n) / (l . n)

    vec3 n = vec3(0.0, 1.0, 0.0);
    vec3 p0 = vec3(-1.0, -1.0, -1.0);

    float denom = dot(n, l);
    if (denom < 1e-6) {
    	float t = dot((p0 - l0), n) / denom;
        vec3 hit = l0 + l * t;

        vec3 light = traceLight(n, hit, l0);

        vec3 c = vec3(1.0);
        c += -1.0 * texture(iChannel0, hit.xz / 7.0).xyz * vec3(0.4, 0.9, 0.9);

        // checker board
        float u = mod(hit.x, 2.0);
        float v = mod(hit.z, 2.0);

        if ((u > 1.0 && v > 1.0) || (u < 1.0 && v < 1.0)) {
            c -= 0.5;
        }

        return Hit(c * light, hit, true);
    } else {
    	return Hit(vec3(0.0), vec3(0.0), false);
    }
}


// https://en.wikipedia.org/wiki/Line%E2%80%93sphere_intersection
Hit traceSphere(vec3 l0, vec3 l, vec3 o, float r, bool isFlat)
{
    // sphere: dot((p - c), (p - c)) = r * r
    // line = a + t * b

    // t = (-b -+ sqrt(b*b - 4*a*c)) / 2*a

    // a = dot (b, b)
    // b = 2 * dot (b, a-c)
    // c = dot (a - c, a - c) - r * r

    float yOffset = cos(iTime / 3.0) * 2.0;
    float zOffset = sin(iTime / 3.0) * 2.0;
    float xOffset = sin(iTime / 3.0) * 2.0;

    //o.y += yOffset;
    //o.z += zOffset;
    //o.x += xOffset;

    float a = dot(l, l);
    float b = 2.0 * dot(l, l0 - o);
    float c = dot(l0 - o, l0 - o) - r * r;
    float dis = b * b - 4.0 * a * c;

    if (dis < 0.) {
        return Hit(vec3(0.0), vec3(0.0), false);
    }

    float t = (-b - sqrt(dis)) / (2.0*a);

    vec3 hit = l0 + l * t;
    vec3 n = normalize(hit - o);
    vec3 light = traceLight(n, hit, l0);

    if (isFlat) {
    	return Hit(vec3(1.0), hit, true);
    }

    vec3 c0 = vec3(1.0);

    // https://en.wikipedia.org/wiki/UV_mapping#Finding_UV_on_a_sphere
    // u = 0.5 + (arctan2(dz, dx)) / 2 * pi
    // v = 0.5 - arcsin(dy) / pi
    vec3 d = normalize(hit - o);
    float u = mod(atan(d.z, d.x) / (2.0 * M_PI), 1.0);
    float v = mod(asin(d.y) / M_PI, 1.0);
    c0 = texture(iChannel1, vec2(u + iTime / 8.0, v)).xyz * 1.5;

    c0 = c0 * light;

    return Hit(c0, hit, true);
}

vec3 render(vec2 uv)
{
    // ray equation
    // p = l0 + l * t

	vec3 l0 = vec3(0.0, 0.0, 1.0);
    vec3 l = normalize(vec3(uv, -1.0));

    float yOffset = sin(iTime / 2.0) * 2.0 + 2.0;
    float zOffset = cos(iTime / 2.0) * 3.0 + 3.0;
    float xOffset = cos(iTime / 2.0) * 4.0 + 4.0;

    l0.y += yOffset;
    l0.z += zOffset;
    l0.x += xOffset;

    vec3 c = vec3(0.0);

    Hit hits[3];

    hits[0] = tracePlane(l0, l);

    hits[1] = traceSphere(l0, l, vec3(3.0, 0.0, -4.0), 1.0, false);

    hits[2] = traceSphere(l0, l, getLightPos(), 0.3, true);

    Hit closest = Hit(vec3(0.0), vec3(0.0), false);

    for (int i = 0; i < 3; i++) {
        if (hits[i].h && distance(hits[i].p, closest.p) > 0.0) {
        	closest = hits[i];
        }
    }

    // Very naive and dumb way to calculate dynamic shadows I pulled out of the hat.
    // Cast from hit point to light source, if intersects with any object it's in dark
    if (closest.h) {
    	vec3 hit = closest.p;
        vec3 light = getLightPos();

        // Add 1e-3 to the sphere radius size to get rid of an artifact (not sure).
        Hit occluded = traceSphere(hit, light, vec3(3.0, 0.0, -4.0), 1.0 + 1e-3, false);

        if (occluded.h) {
            closest.c *= 0.5;
        }

    }

    return closest.c;
}

