#define PI 3.14159265359

float saturate(float x){
    return clamp(x,0.0,1.0);
}

//Screen space UV
vec2 SSUV(vec2 uv){
    return vec2(uv.x/ iResolution.x, uv.y/ iResolution.y);
}

float Circle(float radius, vec2 center, vec2 uv){
    vec2 centeredUV = uv - center;
    centeredUV.x *= iResolution.x/iResolution.y;
    float circle = floor((1.0 - length(centeredUV)) + radius);

    return saturate(circle);
}


float Mouth(vec2 uv, vec2 center){
    vec2 coordinate = uv - center;
    float mouth = atan(coordinate.y, coordinate.x) / PI;
    mouth = 1.0 - abs(mouth);

    float t = iTime * 30.0;

    float mouthPercentage = 0.38;
    mouth = floor(mouth + (sin(t) + 1.0) / 2.0 * mouthPercentage);

    return mouth;
}


float Balls(vec2 fragCoord, float scrolling, float spacing, float scale){
    vec2 scrollinUV = vec2(fragCoord.x + scrolling, fragCoord.y);

    float heightMiddle = (iResolution.y/2.0) - scale/2.0;

    float circleSize = scale;
    vec2 circleGrid = vec2(fract(scrollinUV.x / circleSize) - 0.5, fract((scrollinUV.y - heightMiddle)/ circleSize) - 0.5);
    float c = 1.0 - length(circleGrid) * 2.0;

    float halfHeight = (fragCoord.y / iResolution.y) / 2.0;
    float halfMask = (fragCoord.y - heightMiddle) / circleSize;
    float ballsMask = -2.0 * (halfMask * halfMask) + 2.0 * halfMask;
    ballsMask = ceil(saturate(ballsMask) * 2.0);
    c = ceil(saturate(ballsMask * c));



    float verticalGrid = 1.0 - scrollinUV.x / circleSize;
    verticalGrid /= spacing;

    verticalGrid = fract(verticalGrid);
    verticalGrid = ceil(saturate(1.0 - (verticalGrid * spacing)));
    c *= verticalGrid;

    float halfWidthMask = ceil((fragCoord.x / iResolution.x) - 0.25);

    return halfWidthMask * c;
} 


vec4 Background(vec2 fragCoord){
    float horizontalSineVal = SSUV(fragCoord).y * PI;
    float background = sin(horizontalSineVal * 30.0) + 1.0 - sin(horizontalSineVal);
    background = max(background, 0.0);

    float verticalSineVal = SSUV(fragCoord).x * 50.0 + iTime * 18.0;
    float parralax = (1.0 - sin(verticalSineVal * PI) * 1.0);
    parralax *= ceil(1.0 - fract((verticalSineVal - 1.0)/ 14.0)* 14.0);
    parralax -= sin(horizontalSineVal);
    parralax = saturate(parralax);

    return vec4(0.0, 0.0, 0.15, 1.0) * (max(background, parralax));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float t = iTime * 6.0;
    vec2 imageUV = fragCoord - vec2(sin(t) * 15.0, cos(t * 0.5) * 5.0) - vec2(-190,0.0);
    vec2 screenSpaceUV = SSUV(imageUV);
    vec4 color = vec4(1.0, 0.95, 0.0, 0.0);

    vec4 circle1  = vec4(vec3(Circle(0.3, vec2(0.5, 0.5), screenSpaceUV)), 1.0) * color;

    float eye = Circle(0.05, vec2(0.44, 0.6), screenSpaceUV);
    float innerEye = Circle(0.04, vec2(0.44, 0.6), screenSpaceUV);


    float mouth = Mouth(screenSpaceUV, vec2(0.5, 0.5));

    vec4 pacMan = circle1 - eye * 0.7 - innerEye * 0.15 - mouth;
    pacMan = clamp(pacMan,0.0,1.0);

    float pacManMask = ceil(length(pacMan));

    vec4 balls = vec4(vec3(Balls(fragCoord, iTime * 1200.0,4.0, 70.0)), 0.0);
    balls *= color;
    balls *= saturate(1.0 - pacManMask);

    vec4 result = pacMan + balls;

    //Calculate background
    if(length(result) <= 0.0){
        result = Background(fragCoord);
    }

    fragColor = result;

}

#define barCount 15
#define borderSize 0.1
#define speed 1.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r,g,b;
    float delay = 0.125;
    float barWidth = 20.0;

    float offset = 0.0;
    float brightness = 0.0;

    if((uv.y > borderSize) && (uv.y < 1.0 - borderSize))
    {
        for(int i=0; i< barCount; i++)
        {        
             float yBarPos = (sin((iTime*speed)+offset)*.35)+0.5;
            offset += delay;

            if(brightness < 0.001)
                brightness = clamp(1.0-(abs(yBarPos-uv.y)*barWidth),0.0,1.0);

           r = brightness*abs(sin(offset+iTime));
           g = brightness*abs(cos(offset+iTime));
           b = brightness*0.25;           
        }
    }
    else
    {
        r = g = 0.0;
        b = 0.25;
    }


    // Output to screen
    fragColor = vec4(r,g,b,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t;
	t = iTime * 0.91;
    vec2 r = iResolution.xy,
    o = gl_FragCoord.xy - r/2.0;
    o = vec2(length(o) / r.y - .3, atan(o.y,o.x));    
    vec4 s = 0.08*cos(1.5*vec4(0,1,2,3) + t + o.y + sin(o.y) * cos(t)),
    e = s.yzwx, 
    f = max(o.x-s,e-o.x);
    fragColor = dot(clamp(f*r.y,0.,1.), 72.*(s-e)) * (s-.1) + f;
}

