mat2 Rot2(float a ) {
    float c = cos( a );
    float s = sin( a );
    return mat2( c, -s, s, c );

}    


float pathterrain(float x,float z){
    // Common height function for path and terrain
    return 
        sin(x*.5 )*1.+cos(z*.3 )*0.3
        +cos(x*3.+z )*0.1+sin(x-z*.2 )*0.2;
}    


