// input: h in [0,360] and s,v in [0,1] - output: r,g,b in [0,1]
vec3 hsl2rgb(vec3 c){return mix(c.bbb,mix(clamp(vec3(-1,2,2)-abs(c.r-vec3(3,2,4))*vec3(-1,1,1),0.,1.),vec3(c.b>.5),abs(.5-c.b)*2.),c.g);}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0)) - r;
//         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf
}

float sdSphere( in vec3 pos, float rad )
{
    return length(pos) - rad;
}

float sdStick( in vec3 p, vec3 a, vec3 b, float ra, float rb )
{
    vec3 ba = b-a;
    vec3 pa = p-a;

    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0,1.0 );
    float r = mix( ra, rb, h );
    return length(pa - h*ba) - r;
}

float sdEllipsoid( in vec3 pos, vec3 rad )
{
    float k0 = length(pos/rad);
    float k1 = length(pos/rad/rad);
    return k0*(k0-1.0)/k1;
}

float smin( in float a, in float b, float k )
{
    float h = max( k - abs(a-b), 0.0 );
    return min(a,b) - h*h/(k*4.0);
}

float smax( in float a, in float b, float k )
{
    float h = max( k - abs(a-b), 0.0 );
    return max(a,b) + h*h/(k*4.0);
}

vec4 char_evil( in vec3 q, float atime, float sy, float sz, float cc, float ss, float y, float y2, float y3 )
{
    float d = 999.0;
    vec4 res = vec4(d,-1.0,0.0,0.0);

    // Body coordinate ref
    vec3 b = q;
    vec3 sb = vec3( abs(b.x), b.yz );

    // body
    {
    vec3 rad = vec3(0.25,0.25*sy,0.25*sz);
    float d = sdEllipsoid(q, rad);
    if( d<res.x ) res = vec4(d,18.0,1.0,0.0);
    }

    // legs
    {
    vec3 legBase = vec3(0.15,-0.25,0.0);
    legBase.y += 0.1/sy;
    float la = -sign(b.x)*cos(3.1416*atime);
    float ccc = cos(la);
    float sss = sin(la);
    vec3 legEnd = legBase + 0.2*vec3(0.2, -ccc, sss);
    float d2 = sdStick( sb,
                  legBase,
                  legEnd,
                  0.03,
                  0.06 );
    d = smin(res.x,d2,0.02);
    if( d<res.x ) res = vec4(d, 16.0,1.0,0.0);
    }

    // Head coordinate ref
    vec3 neck = vec3(0.0,0.0,0.12);
    vec3 h = q - neck;

    // Head rotation
    float hr = -1.0+2.0*smoothstep( -0.2,0.2, -0.4*sin(atime * 1.7) );
    float hp = -1.0+2.0*smoothstep( -0.4,0.4, 0.1+0.15*cos(atime * 3.3) );
    //hr = -5.0*(iMouse.x/iResolution.x - 0.5); // mouse control
    //hp = -2.0*(iMouse.y/iResolution.y - 0.5); // mouse control
    cc = cos(hr);
    ss = sin(hr);
    h.xz = mat2(cc,-ss,ss,cc)*h.xz;
    cc = cos(hp);
    ss = sin(hp);
    h.yz = mat2(cc,-ss,ss,cc)*h.yz;

    vec3 sh = vec3( abs(h.x), h.yz );

    // pants (using head coordinates so they angle when the head does)
    if (h.y < 0.0) { res.y = 6.0; }

    // head
    {

    float d2 = sdEllipsoid(h - vec3(0.0,0.28,0.0), vec3(0.15,0.2,0.23));
    float d3 = sdEllipsoid(h - vec3(0.0,0.28,-0.05), vec3(0.23,0.2,0.2));

    d2 = smin(d2,d3, 0.05);
    d = smin(res.x,d2, 0.15);
    if( d<res.x ) res = vec4(d, 12.0,1.0,0.0);
    }

    // eyebrows
    {
    vec3 eb = sh - vec3(0.04,0.33,0.16);
    float er = -0.8;
    cc = cos(er);
    ss = sin(er);
    eb.xy = (mat2(cc,ss,-ss,cc))*eb.xy;
    float d2 = sdEllipsoid(eb, vec3(0.06,0.03,0.07));
    d = smin(d,d2, 0.02);
    //d = min(d,d2);
    if( d<res.x ) res = vec4(d, 12.0,1.0,0.0);
    }

    // wrinkles
    {
    float ry = (h.y-0.03) - 1.3*h.x*h.x;
    d += 0.001*sin(ry*160.0) * (1.0 - smoothstep(0.0,0.05,abs(ry)));
    if( d<res.x ) res = vec4(d, 12.0,1.0,0.0);
    }

    // arms
    {
    float d2 = sdStick( sh + neck,
                  vec3(0.20,0.25,0.1+-0.05),
                  vec3(0.3+0.1*y3,-0.1+0.4*y3,0.1+0.05),
                  0.03,
                  0.06 );
    d = smin(d,d2,0.02);
    if( d<res.x ) res = vec4(d, 12.0,1.0,0.0);
    }

    // ears
    {
    float d2 = sdStick( sh,
                  vec3(0.1,0.4,-0.01),
                  vec3(0.2,0.4+0.15*y2,0.02-0.1*y2),
                  0.01,
                  0.03 );
    d = smin(d,d2,0.04);
    if( d<res.x ) res = vec4(d, 12.0,1.0,0.0);
    }

    // unicorn horn
    {
    float ro = -0.6;
    float cc = cos(ro);
    float ss = sin(ro);
    vec3 hh = (sh - vec3(0.1, 0.4, 0.1));
    hh.xy = mat2(cc,-ss,ss,cc)*hh.xy;
    float d = sdStick(hh,
        vec3(0.0, 0.0, 0.0),
        vec3(0.0, 0.2, 0.0),
        0.03,
        0.01);
    if( d<res.x ) res = vec4(d, 17.0,1.0,0.0);
    }

    // mouth
    {
    float d = sdEllipsoid(h - vec3(0.0,0.15 - 2.0*h.x*h.x*h.x,0.15), vec3(0.2,0.04 + 0.02 * sin(atime * 1.3),0.2));
    float occ = 1.0-smoothstep( -0.01,0.001, -d );
    d = smax(res.x,-d,0.01);
    if( d>res.x ) res = vec4(d, 12.0,occ,0.0);
    }

    // tooth
    {
    //float d = sdRoundBox( h - vec3(0.0,0.19 + 0.005 * sin(atime * 1.3),0.16), vec3(0.02,0.011,0.01), 0.005 );
    float y = 0.005 * sin(atime * 1.3);
    float d = sdRoundBox( sh - vec3(0.018,0.19 + y,0.16), vec3(0.01,0.011,0.01), 0.005 );
//  d = min(d, sdRoundBox( sh - vec3(0.058,0.185 + y,0.15), vec3(0.01,0.011,0.01), 0.005 ));
    d = min(d, sdStick( sh, vec3(0.058,0.19 + y,0.15), vec3(0.058,0.16 + y,0.15), 
                       0.01, 0.001 ));
    if( d<res.x ) res = vec4(d,20.0,1.0,0.0);
    }

    // eye
    {
    // Eye rotation
    vec3 e = h - vec3(0.08,0.28,0.16);

    // Maybe there's a way to get the eyes to repeat without
    // symmetry, but if so I don't know what it would be.
    // The "limited repetition" example's opRepLim works,
    // but I don't know how to do get two eyes with it instead
    // of three:
    //
    //    float rep_s = 0.16;
    //    vec3 rep_lim = vec3(1.0, 0.0, 0.0);
    //    vec3 e_rep = e - rep_s*clamp( round(e/rep_s), -rep_lim,rep_lim );
    //    e = e_rep;
    //
    // So I'm just going to do each eye separately with its own coordinate
    // basis instead*:
    //
    // (*also lets me rotate the eyes differently, which is better anyway!)

    vec3 e0 = e;
    vec3 e1 = e - vec3(-0.16, 0.0, 0.0);

    // Horizontal eye movement beyond -0.7..0.7 looks weird. 
    // Vertical eye movement beyond -0.5..0.8 looks weird.
    // But we don't use the full range here, because we're also
    // tilting the head.
    const float es = 0.05; // eye smoothing
    float etime = atime + 0.1; // eyes lead the head movement by a moment.
    float er = -0.5*(-1.0 + 2.0*smoothstep( -es, es, sin(etime * 1.7) ));
    float ep = 0.1 + 0.45*(-1.0 + 2.0*smoothstep( -es, es, cos(etime * 3.3) ));
    //er = clamp( -5.0*(iMouse.x/iResolution.x - 0.5), -0.7,0.7 ); // mouse control
    //ep = clamp( -2.0*(iMouse.y/iResolution.y - 0.5), -0.5,0.8 ); // mouse control

    // BUG: eyes ought to look at something in world coordinates,
    // and remain stable even when the head and body move. But
    // that's hard... I'm not set up to do the inverse transforms :(

    // The eyes rotate less when moving towards the nose.
    float er0 = (er < 0.0 ? er : 0.4 * er);
    float cc0 = cos(er0);
    float ss0 = sin(er0);
    e0.xz = mat2(cc0,-ss0,ss0,cc0)*e0.xz;
    float er1 = (er > 0.0 ? er : 0.4 * er);
    float cc1 = cos(er1);
    float ss1 = sin(er1);
    e1.xz = mat2(cc1,-ss1,ss1,cc1)*e1.xz;
    // But both eyes pitch identically.
    cc = cos(ep);
    ss = sin(ep);
    e0.yz = mat2(cc,-ss,ss,cc)*e0.yz;
    e1.yz = mat2(cc,-ss,ss,cc)*e1.yz;

    // Eye occlusion
    float eo = 1.0-5.0*smoothstep( 0.06,0.4, sh.x );

    // Left eye (e0)
    d = sdSphere( e0, 0.05 );
    if( d<res.x ) res = vec4(d,13.0,eo,0.0);
    // pupil
    d = sdSphere( e0 - vec3(0.00,0.00,0.04), 0.02 );
    if( d<res.x ) res = vec4(d,14.0,1.0,0.0);

    // Right eye (e1)
    d = sdSphere( e1, 0.05 );
    if( d<res.x ) res = vec4(d,13.0,eo,0.0);
    // pupil
    d = sdSphere( e1 - vec3(0.00,0.00,0.04), 0.02 );
    if( d<res.x ) res = vec4(d,14.0,1.0,0.0);
    }

    // --- wings ---

    {
    float ws = 2.3;
    vec3 wo = mix(sb, sh, 0.8) - vec3(0.0,0.1,-0.16);

    //float wf = 0.5 + 0.6 * sin(ws * atime);
    //float cc = cos(wf);
    //float ss = sin(wf);
    //wo.yz = mat2(cc,ss,-ss,cc) * wo.yz;

    float wr = -0.1 - 0.8 * pow(abs(sin(ws * atime)), 0.5);
    cc = cos(wr);
    ss = sin(wr);
    wo.xy = mat2(cc,ss,-ss,cc) * wo.xy;

    float d = sdEllipsoid( wo - vec3(0.5,0.0 -1.6*pow(wo.x-0.3,2.0),0.0), vec3(0.3,0.15,0.01) );

    if( d<res.x ) res = vec4(d,19.0,0.0,0.0);
    }

    return res;
}

