vec4 terrain_evil( in vec3 pos, float atime, vec3 cen )
{
    float d = 999.0;
    vec4 res = vec4(d,-1.0,0.0,0.0);

    // terrain
    float fh = -0.1 + 0.09*(sin(atime+2.0*pos.x)+ 0.8 * sin(atime*1.7+1.3*pos.z));
    float l = length((pos - cen).xz);

#if TERRAIN_DETAIL && 0
    float gt = fract(atime);
    fh -= 0.1
        *sin(gt*8.0+l*2.0)
        *exp( -1.0*l*l )
        *exp( -1.0*gt )
        *smoothstep(0.0,0.1,gt);
#endif

    d = pos.y - fh;
    res = (d<res.x) ? vec4(d,11.0,1.0,0.0) : res;

#if TERRAIN_DETAIL
    // lava trees
    {
    vec3 q = vec3( mod(abs(pos.x), 3.0)-1.5,
                   pos.y,
                   mod(pos.z+1.5, 3.0)-1.5 );
    vec2 id = vec2( floor(pos.x/3.0), floor((pos.z+1.5)/3.0) );
    float fid = id.x*31.1 + id.y*31.7;
    float fy = fract(id.x*1.72+atime*1.225);
    float y = 0.9*pow(fy, 0.5);
    vec3 rad = vec3(0.7,1.0+0.5*sin(fid*431.19),0.8);
    // rad -= 0.05*(sin(pos.x*3.0)+sin(pos.y*6.0)+sin(pos.z*5.0));
    rad *= clamp(2.0*fy*(1.0-fy), 0.001, 1.0);
    float d2 = sdEllipsoid( q-vec3(0.5,y,0.0), rad );
    float f = smoothstep( -0.2,0.2,
        sin(18.0*pos.x)+sin(18.0*pos.y)+sin(18.0*pos.z) );
    d2 -= 0.007*f; // non-euclidean distortion of surface.
    d = smin(d, d2, 0.7);

    // Reduce step size for terrain to increase accuracy
    // (preventing artifacts when the raymarcher goes inside
    // the terrain because of the noneuclidean distortions).
    // (this is a hacky solution)
//    d *= 0.9;

    res = (d<res.x) ? vec4(d,11.0,1.0,0.0) : res;
    }

    // candy
    {
    vec3 q = vec3( mod(pos.x+0.25, 0.5)-0.25,
                   pos.y - fh,
                   mod(pos.z+0.25, 0.5)-0.25);

    vec2 id = floor((pos.xz+0.25)/0.5);
    vec2 fid = vec2( id.x*21.1 + id.y*11.7,
                      id.x*13.1 + id.y*51.7 );
    vec2 dis = cos(fid);
    vec3 ofs = vec3( dis.x*0.1,
                     -0.25+0.2*fract(fid.x)+0.1*sin(atime*1.2+fid.x),
                     dis.y*0.1);
    vec3 group = vec3( 0.0,
                       -0.05*(1.0 - cos(pos.x)*cos(pos.x)),
                       0.0 );
    vec3 so = q - ofs - group;

    float angle = 0.0 + sin(fid.x);
    float cc = cos(angle);
    float ss = sin(angle);
    so.xz = mat2(cc, ss, -ss, cc) * so.xz;


    vec3 sso = vec3(abs(so.x), so.yz);
    float d = sdSphere( so, 0.1 );

    float co = 0.5+0.5*clamp( (pos.y-fh)/0.03, 0.0,1.0 );

    res = (d<res.x) ? vec4(d,15.0,co,0.0) : res;

    d = sdSphere( sso - vec3(0.034, 0.02, 0.09), 0.025);
    d = max(res.x,-d);
    res = (d>res.x) ? vec4(d,19.0,0.01,0.0) : res;
    }
#endif

    return res;
}

vec4 terrain_happy( in vec3 pos, float atime, vec3 cen )
{
    float d = 999.0;
    vec4 res = vec4(d,-1.0,0.0,0.0);

    // terrain
    float fh = -0.1 + 0.05*(sin(2.0*pos.x)+sin(2.0*pos.z));
    float l = length((pos - cen).xz);

#if TERRAIN_DETAIL && 0
    float gt = fract(atime);
    fh -= 0.1
        *sin(gt*8.0+l*2.0)
        *exp( -1.0*l*l )
        *exp( -1.0*gt )
        *smoothstep(0.0,0.1,gt);
#endif

    d = pos.y - fh;
    res = (d<res.x) ? vec4(d,1.0,1.0,0.0) : res;

#if TERRAIN_DETAIL
    // trees
    {
    vec3 q = vec3( mod(abs(pos.x), 3.0)-1.5,
                   pos.y,
                   mod(pos.z+1.5, 3.0)-1.5 );
    vec2 id = vec2( floor(pos.x/3.0), floor((pos.z+1.5)/3.0) );
    float fid = id.x*31.1 + id.y*31.7;
    float fy = fract(id.x*1.72+atime*0.125);
    float y = 2.0*fy;
    vec3 rad = vec3(0.7,1.0+0.5*sin(fid*431.19),0.8);
    rad -= 0.05*(sin(pos.x*3.0)+sin(pos.y*6.0)+sin(pos.z*5.0));
    rad *= clamp(4.0*fy*(1.0-fy), 0.001, 1.0);
    float d2 = sdEllipsoid( q-vec3(0.5,y,0.0), rad );
    float f = smoothstep( -0.2,0.2,
        sin(18.0*pos.x)+sin(18.0*pos.y)+sin(18.0*pos.z) );
    d2 -= 0.007*f; // non-euclidean distortion of surface.
    d = smin(d, d2, 0.3);

    // Reduce step size for terrain to increase accuracy
    // (preventing artifacts when the raymarcher goes inside
    // the terrain because of the noneuclidean distortions).
    // (this is a hacky solution)
//    d *= 0.9;

    res = (d<res.x) ? vec4(d,1.0,1.0,0.0) : res;
    }

    // candy
    {
    vec3 q = vec3( mod(pos.x+0.25, 0.5)-0.25,
                   pos.y - fh,
                   mod(pos.z+0.25, 0.5)-0.25 );
    vec2 id = floor((pos.xz+0.25)/0.5);
    vec2 fid = vec2( id.x*21.1 + id.y*11.7,
                      id.x*13.1 + id.y*51.7 );
    vec2 dis = cos(fid);
    vec3 ofs = vec3( dis.x*0.1,
                     -0.25+0.3*fract(fid.x),
                     dis.y*0.1);
    vec3 group = vec3( 0.0,
                       -0.05*(1.0 - cos(pos.x)*cos(pos.x)),
                       0.0 );
    float d = sdSphere( q - ofs - group, 0.1 );
    float co = 0.5+0.5*clamp( (pos.y-fh)/0.03, 0.0,1.0 );

    res = (d<res.x) ? vec4(d,5.0,co,0.0) : res;
    }
#endif

    return res;
}

vec4 map( in vec3 pos, float atime )
{
    float t = fract(atime);
    float y = 4.0*t*(1.0 - t);
    float dy = 4.0*(1.0 - 2.0*t);
    // delayed parabolas
    float t2 = fract(atime - 0.2);
    float y2 = 4.0*t2*(1.0 - t2);
    float t3 = fract(atime + 0.15);
    float y3 = 4.0*t3*(1.0 - t3);

    vec2 uu = normalize(vec2(1.0,-dy));
    vec2 vv = vec2(-uu.y, uu.x);

    float itime = floor(atime);
    float ftime = fract(atime);

    // position
    float x = -1.0+2.0*abs(fract(0.5*atime) - 0.5)/0.5;
    vec3 cen = vec3( 0.6 * sin(atime),
                     //0.8*pow(pow(y,2.0-y), 0.4)+0.15, // temp
                     0.7 + 0.3 * sin(atime),
                     itime+pow(ftime,0.7) );
    float sy = 0.7 + 0.3*y;
    float sz = 1.0/sy;

    // ref coords
    vec3 q = pos - cen;

    float ro = 0.3*x;
    float cc = cos(ro);
    float ss = sin(ro);
    q.xy = mat2(cc,-ss,ss,cc)*q.xy;


    // Swap the character and the terrain
    float ttime = mod(atime+2.0, 10.0);
    float ctime = mod(atime+1.0, 10.0);
    float tx = 0.0;
    const float ts = 20.0;
    if( ttime < 2.0 ) tx = ts*(ttime - 1.0);
    else if( ttime < 5.0 ) tx = ts;
    else if( ttime < 7.0 ) tx = -ts*((ttime - 5.0) - 1.0);
    else tx = -ts;


    vec4 res, res2;

    if( ctime<5.0 ) {
        res = char_happy( q, atime, sy, sz, cc, ss, y, y2, y3 );
    } else {
        res = char_evil( q, atime, sy, sz, cc, ss, y, y2, y3 );
    }
    if( pos.x < tx) {
        res2 = terrain_happy( pos, atime, cen );
    } else {
        res2 = terrain_evil( pos, atime, cen );
    }
    res = (res2.x < res.x) ? res2 : res;

    return res;
}

vec3 calcNormal( in vec3 pos, float atime )
{
    vec2 e = vec2(0.0001,0.0);
    return normalize( vec3( map(pos+e.xyy,atime).x-map(pos-e.xyy,atime).x,
                            map(pos+e.yxy,atime).x-map(pos-e.yxy,atime).x,
                            map(pos+e.yyx,atime).x-map(pos-e.yyx,atime).x ) );
}

float calcOcclusion( in vec3 pos, in vec3 nor, float atime )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; ++i )
    {
        float h = 0.01 + 0.11*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = map( opos, atime ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( (1.0 - 2.0*occ), 0.0,1.0 );
}

float castShadow( in vec3 ro, in vec3 rd, float atime )
{
    float res = 1.0;
    // Start a bit out to prevent self-shadowing "onion rings" due
    // to noneuclidean distances (this is a hacky solution, and it
    // will prevent any real small self-shadowing that we want, that
    // we were getting at t=0.01)
    float t = 0.05;
    for( int i=0; i<100; ++i )
    {
        vec3 pos = ro + t*rd;
        float h = map( pos,atime ).x;
        // Divide by t to get cone tracing.
        // A smaller constant factor gives softer shadows.
        res = min( res, 16.0*h/t );
        if( h<0.0001 ) break;
        t += h * 0.5; // Step shadows slower to reduce banding.
        if( t>20.0 ) break;
    }

    return clamp( res, 0.0,1.0 );
}

vec4 castRay( in vec3 ro, in vec3 rd, float atime )
{
    vec3 m = vec3(-1.0,1.0,0.0);
    float t = 0.01;
    for( int i=0; i<512; ++i )
    {
        vec3 pos = ro + t*rd;
        vec4 h = map( pos,atime );
        m = h.yzw;
        if( abs(h.x)<(0.001*t) ) break;
        t += h.x;
        if( t>20.0 ) break;
    }
    if( t>20.0 ) m = vec3(-1.0,1.0,0.0);
    return vec4(t,m);
}

vec3 sky_evil(vec3 rd, float atime)
{
    // sky (the default colour)
    vec3 col = vec3(0.35,0.2,0.1) - 0.7*rd.y; // sky base
    col = clamp( col, 0.0,1.0 );

#if SKY_DETAIL
    // clouds
    vec2 uv = rd.xz/rd.y; // How is this derived??
    vec2 uv0 = uv + atime*vec2(0.1, -0.08);
    vec2 uv1 = uv + atime*vec2(0.2, -0.10);
    float cl = 0.6*( sin(1.0*uv0.x) + sin(1.0*uv0.y) )
             + 0.3*( sin(2.0*uv1.x) + sin(2.0*uv1.y) );
    col = mix( col, vec3(1.0,0.5,0.5) * 0.0002,
               smoothstep( -0.4,0.4, -0.5+cl ) );

    // horizon glow/fog
    col = mix( col, vec3(0.95,0.7,0.5), exp(-10.0*rd.y) );
#endif
    return col;
}

vec3 sky_happy(vec3 rd, float atime)
{
    // sky (the default colour)
    vec3 col = vec3(0.4,0.75,1.0) - 0.7*rd.y; // sky base
    col = clamp( col, 0.0,1.0 );

#if SKY_DETAIL
    // clouds
    vec2 uv = rd.xz/rd.y; // How is this derived??
    vec2 uv0 = uv + atime*vec2(0.1, -0.08);
    vec2 uv1 = uv + atime*vec2(0.2, -0.10);
    float cl = 0.6*( sin(1.0*uv0.x) + sin(1.0*uv0.y) )
             + 0.3*( sin(2.0*uv1.x) + sin(2.0*uv1.y) );
    col = mix( col, vec3(0.3,0.7,0.95),
               smoothstep( -0.15,0.15, -0.5+cl ) );

    // horizon glow/fog
    col = mix( col, vec3(0.5,0.7,0.9), exp(-10.0*rd.y) );
#endif
    return col;
}

vec3 sky(vec3 rd, float atime)
{
    // Swap the character and the terrain
    float ttime = mod(atime+1.5, 10.0);
    float tx = 0.0;
    if( ttime < 1.0 ) tx = 1.0-ttime;
    else if( ttime < 5.0 ) tx = 0.0;
    else if( ttime < 6.0 ) tx = (ttime - 5.0);
    else tx = 1.0;

    vec3 sky1 = sky_happy(rd, atime);
    vec3 sky2 = sky_evil(rd, atime);
    return mix(sky1, sky2, tx);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;

    // time
    float atime = mod(iTime, 60.0);
//    atime = 134.7; // paused in sunlight
//    atime = 4.0; // paused in shadow
//    atime = 40.8; // paused 3/4 view
//    atime = 110.25; // paused looking almost at camera
//    atime = 20.13; // paused half in shadow

    // character pos
    float t = fract(atime-0.1);
    float y = 4.0*t*(1.0 - t); // from character y, slightly advanced

    // camera angle
    float an = -0.2+0.4*sin(0.15*atime);
//    an = 10.0*(iMouse.x/iResolution.x - 0.5); // mouse control

    // camera distance
    float cd = 2.5-0.9*sin(0.1*atime)*sin(0.1*atime);
    // camera offset
    vec3 co = vec3(cd*sin(an),1.0,cd*cos(an)); // temp
    // target
    vec3 ta = vec3(0.0,0.7+0.0*0.2*y,atime); // temp

    // float above floor
    vec3 ro = ta + co;
    float fh = -0.1 + 0.05*(sin(2.0*ro.x)+sin(2.0*ro.z)); // from terrain
    ro.y = fh + 0.22 + 0.5*sin(0.2*atime)*sin(0.2*atime);

    // camera shake when bouncing
    // temp
//    float bt = -1.0+2.0*abs(fract(atime-0.1) - 0.5)/0.5;
//    float bou = 0.8*smoothstep(0.2,1.0,bt);
//    ro += bou*vec3(0.05,-0.12,0.05)*sin(atime*12.0 + vec3(2.0,2.0,4.0));

    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0,1,0)) );
    vec3 vv = normalize( cross(uu,ww) );

    vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

    vec3 col = sky(rd, atime);

    vec4 tm = castRay( ro, rd, atime );
    if( tm.y>0.0 )
    {
        float t = tm.x;
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, atime );

        // albedo
        vec3 al = vec3(0.18);
        // specular
        float ks = 1.0;

        // happy materials
        if( tm.y<1.5 )
        {
            // terrain
            al = vec3(0.05,0.1,0.02);
            float f = -1.0+2.0*smoothstep( -0.2,0.2,
                sin(18.0*pos.x)+sin(18.0*pos.y)+sin(18.0*pos.z) );
            al += 0.2*f*vec3(0.06,0.06,0.02);
            // get darker and redder as it grows higher
            al = mix( al, vec3(0.005, 0.0005, 0.0), 0.3*pos.y );
            // specular is only high near ground level
            ks = clamp( 2.0 - 1.5*pos.y, 0.0,3.0 );
        }
        else if( tm.y<2.5 || (tm.y >= 7.5 && tm.y < 8.5) )
        {
            // body
            al = vec3(0.02,0.1,0.3);
            ks = 0.5;

            // polka dot pattern
            float f = -1.0+2.0*smoothstep( -2.0,2.0,
                sin(30.0*pos.x*0.1)+sin(30.0*pos.y*0.1)+sin(30.0*pos.z*0.5) );
            al = mix(al, vec3(0.4, 0.1, 0.2), 1.0 - f);
        }
        else if( tm.y<3.5 )
        {
            // eyes
            al = vec3(0.4,0.4,0.4);
            ks = 2.0;
        }
        else if( tm.y<4.5 )
        {
            // pupils
            al = vec3(0.02);
            ks = 10.0;
        }
        else if( tm.y<5.5 )
        {
            // candy
            al = vec3(0.1, 0.02, 0.01);
            vec2 id = floor((pos.xz+0.25)/0.5);
            float fid = id.x*11.1 + id.y*31.7;
            al += vec3(0.01,0.06,0.02)*cos( fid*10.0 + vec3(0.0, 0.2, 0.4) );
            ks = 1.0;
        }
        else if( tm.y<6.5 )
        {
            // pants
            al = vec3(0.4, 0.1, 0.2);
            ks = 3.0;
        }
        else if( tm.y<7.5 )
        {
            // horn
            float hue = fract(atime * 3.0) * 6.28;
            al = hsl2rgb(vec3(hue, 0.9, 0.4));
            ks = 10.0;
        }
        else if( tm.y<9.5 )
        {
            // wings
            al = vec3(0.4, 0.1, 0.2);
            ks = 5.0;
            // polka dot pattern
            float f = -1.0+2.0*smoothstep( -0.2,0.2,
                sin(80.0*pos.x)+sin(80.0*pos.y)+sin(2.0) );
            al += 0.2*f*vec3(0.06,0.06,0.02);
        }
        else if( tm.y<10.5 )
        {
            // everything else
            al = vec3(0.0,0.0,0.0);
            ks = 0.0;
        }
        // evil materials
        else if( tm.y<11.5 )
        {
            // terrain
            al = vec3(0.4,0.03,0.01);
            float f = -1.0+2.0*smoothstep( -0.2,0.2,
                sin(18.0*pos.x)+sin(18.0*pos.y)+sin(18.0*pos.z) );
            al += 0.2*f*vec3(0.06,0.06,0.02);
            // get darker and redder as it grows higher
            al = mix( al, vec3(0.007, 0.0003, 0.0), 0.15*(pos.y + 6.0) );
            // specular is only high near ground level
            ks = clamp( 2.0 - 1.5*pos.y, 0.0,3.0 );
        }
        else if( tm.y<12.5 || (tm.y >= 17.5 && tm.y < 18.5) )
        {
            // body
            al = vec3(0.02,0.1,0.3);
            ks = 0.5;

            // polka dot pattern
            float f = -1.0+2.0*smoothstep( -2.0,2.0,
                sin(30.0*pos.x*0.1)+sin(30.0*pos.y*0.1)+sin(30.0*pos.z*0.5) );
            al = mix(al, vec3(0.4, 0.1, 0.2), 1.0 - f);
        }
        else if( tm.y<13.5 )
        {
            // eyes
            al = vec3(0.6,0.0,0.0);
            ks = 2.0;
        }
        else if( tm.y<14.5 )
        {
            // pupils
            al = vec3(0.02);
            ks = 10.0;
        }
        else if( tm.y<15.5 )
        {
            // candy
            al = vec3(0.1, 0.1, 0.1);
            vec2 id = floor((pos.xz+0.25)/0.5);
            float fid = id.x*11.1 + id.y*31.7;
            al += vec3(0.01,0.06,0.02)*cos( fid*10.0 + vec3(0.0, 0.2, 0.4) );
            ks = 1.0;
        }
        else if( tm.y<16.5 )
        {
            // pants
            al = vec3(0.4, 0.1, 0.2);
            ks = 3.0;
        }
        else if( tm.y<17.5 )
        {
            // horn
            float hue = fract(atime * 0.1) * 6.28;
            al = hsl2rgb(vec3(hue, 0.9, 0.4));
            ks = 10.0;
        }
        else if( tm.y<19.5 )
        {
            // wings
            al = vec3(0.0, 0.0, 0.0);
            ks = 0.3;
            // polka dot pattern
            float f = -1.0+2.0*smoothstep( -0.2,0.2,
                sin(80.0*pos.x)+sin(80.0*pos.y)+sin(2.0) );
            //al += 0.2*f*vec3(0.06,0.06,0.02);
        }
        else if( tm.y<20.5 )
        {
            // eyes
            al = vec3(0.5,0.5,0.1);
            ks = 3.0;
        }
        else
        {
            // everything else
            al = vec3(0.0,0.0,0.0);
            ks = 0.0;
        }
        al = clamp( al, vec3(0.0), vec3(1.0) );

        float occ = calcOcclusion( pos, nor, atime )*tm.z;
        vec3 sun_dir = normalize( vec3(0.8,0.4,0.7) );
        float sun_dif = clamp( dot(nor,sun_dir), 0.0,1.0 );
        // push specular out of place a little
        vec3 sun_hal = normalize(sun_dir - rd + vec3(0.0, 0.25, -0.5));
        float sun_sha = castShadow( pos + nor*0.001, sun_dir, atime );
        vec3 sun_sha_pen = vec3(sun_sha,
                                mix( sun_sha, sun_sha*sun_sha, 0.5 ),
                                sun_sha*sun_sha); // for orangey penumbras.
        float sun_spe = ks*pow( clamp(dot(nor,sun_hal),0.0,1.0), 8.0 )*sun_dif
                          *(0.04+0.96*pow(clamp(1.0+dot(sun_hal,rd),0.0,1.0),5.0));
        float sky_dif = clamp( 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0)), 0.0,1.0 );
        float sky_ref = smoothstep( 0.0,1.5/ks, reflect(rd,nor).y );
        float bou_dif = clamp( 0.01 + 0.99*dot(nor,vec3(0.0,-1.0,0.0)), 0.0,1.0 );
        float fre = clamp( 1.0 + dot(rd,nor), 0.0,1.0 );

        // Check if the guy is generally in shadow (so we can reduce
        // sss when he is. And only apply this to the guy's body, not
        // the whole scene!
        // (Previously it was being applied when he looked down :D
        // and it made the whole terrain flicker when applied D: )
        float guy_sha = 1.0;
        if (tm.y > 1.5 && tm.y <= 2.5) {
        float itime = floor(atime);
        float ftime = fract(atime);
        float y = 4.0*ftime*(1.0 - ftime);
        float x = -1.0+2.0*abs(fract(0.5*atime) - 0.5)/0.5;
        vec3 cen = vec3( x*0.4,
                         pow(y,2.0-y)+0.15,
                         itime+pow(ftime,0.7) );
        // offset the body raycast by a bunch to prevent it intersecting
        // the body/head. We only want to consider environment shadows!
        guy_sha = castShadow( cen+0.8*sun_dir, sun_dir, atime );
        }

        // diffuse (sun, sky, bounce)
        vec3 lin = vec3(0.0);
        lin += sun_dif*vec3(9.1,6.0,2.2)*sun_sha_pen;
        lin += 1.5*sky_dif*vec3(0.5,0.8,3.9)*occ;
        lin += 0.75*bou_dif*vec3(0.3,1.08,0.2)*occ;
        // fake sss
        lin += 2.0*fre*vec3(1.5,0.5,0.4)
                  *(0.5+0.5*sun_dif) // reduce a little in unlit areas.
                  *(0.15+0.85*guy_sha) // reduce a lot when guy in shadow.
                  *(0.5+0.5*tm.z); // reduce a ton when in forced-occlusion places.
        col = al*lin;

        // specular
        col += sun_spe*vec3(8.10,6.00,4.20)*sun_sha;
        col += sky_ref*0.1*vec3(0.6,0.9,2.0)*sky_dif*occ;

        // haze
        col = mix( col, vec3(0.95,0.7,0.5), 1.0-exp(-0.00005*t*t*t) );

#if COLOR_GRADING
        // Inigo hack: boost reds in the lighting colours.
        // Like a curves adjustment. Artistic more than realistic.
        col = pow( col, vec3(0.8,0.9,1.0) );
#endif

//        col = vec3(tm.z*tm.z);
//        col = vec3(occ*occ);
//        col = vec3( guy_sha );
        }

    // Convert to gamma space.
    col = pow( col, vec3(0.4545) );

#if COLOR_GRADING
    // Exposure and contrast adjustment:
    // (Normally would do this in linear space, but this s-curve is only
    // good between 0 and 1.)
    col = clamp( 1.05*col, 0.0,1.0 ); // 5% exposure gain & clamp to prevent burn.
    vec3 contrasty = col*col*(3.0 - 2.0*col); // shallow s-curve.
    col = mix( col, contrasty, 0.25 ); // Just use a bit of the contrasty colours.
#endif

    fragColor = vec4(col,1.0);
}


void mainImage2(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;

    // time
    float atime = mod(iTime, 60.0);
//    atime = 134.7; // paused in sunlight
//    atime = 4.0; // paused in shadow
//    atime = 40.8; // paused 3/4 view
//    atime = 110.25; // paused looking almost at camera
//    atime = 20.13; // paused half in shadow

    // character pos
    float t = fract(atime-0.1);
    float y = 4.0*t*(1.0 - t); // from character y, slightly advanced

    // camera angle
    float an = -0.2+0.4*sin(0.15*atime);
//    an = 10.0*(iMouse.x/iResolution.x - 0.5); // mouse control

    // camera distance
    float cd = 2.5-0.9*sin(0.1*atime)*sin(0.1*atime);
    // camera offset
    vec3 co = vec3(0.0,0.0,cd);//vec3(cd*sin(an),1.0,cd*cos(an)); // temp
    // target
    vec3 ta = vec3(0.0,0.7+0.0*0.2*y,atime); // temp

    // float above floor
    vec3 ro = ta + co;
    float fh = -0.1 + 0.05*(sin(2.0*ro.x)+sin(2.0*ro.z)); // from terrain
    ro.y = fh + 0.22 + 0.5*sin(0.2*atime)*sin(0.2*atime);

    // camera shake when bouncing
    // temp
//    float bt = -1.0+2.0*abs(fract(atime-0.1) - 0.5)/0.5;
//    float bou = 0.8*smoothstep(0.2,1.0,bt);
//    ro += bou*vec3(0.05,-0.12,0.05)*sin(atime*12.0 + vec3(2.0,2.0,4.0));

    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0,1,0)) );
    vec3 vv = normalize( cross(uu,ww) );

    vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

    // sky (the default colour)
    vec3 col = vec3(0.4,0.75,1.0) - 0.7*rd.y; // sky base
    col = clamp( col, 0.0,1.0 );

#if SKY_DETAIL
    // clouds
    vec2 uv = rd.xz/rd.y; // How is this derived??
    vec2 uv0 = uv + atime*vec2(0.1, -0.08);
    vec2 uv1 = uv + atime*vec2(0.2, -0.10);
    float cl = 0.6*( sin(1.0*uv0.x) + sin(1.0*uv0.y) )
             + 0.3*( sin(2.0*uv1.x) + sin(2.0*uv1.y) );
    col = mix( col, vec3(0.3,0.7,0.95),
               smoothstep( -0.15,0.15, -0.5+cl ) );

    // horizon glow/fog
    col = mix( col, vec3(0.5,0.7,0.9), exp(-10.0*rd.y) );
#endif

    vec4 tm = castRay( ro, rd, atime );
    if( tm.y>0.0 )
    {
        float t = tm.x;
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, atime );

        // albedo
        vec3 al = vec3(0.18);
        // specular
        float ks = 1.0;

        if( tm.y<1.5 )
        {
            // terrain
            al = vec3(0.05,0.1,0.02);
            float f = -1.0+2.0*smoothstep( -0.2,0.2,
                sin(18.0*pos.x)+sin(18.0*pos.y)+sin(18.0*pos.z) );
            al += 0.2*f*vec3(0.06,0.06,0.02);
            // get darker and redder as it grows higher
            al = mix( al, vec3(0.005, 0.0005, 0.0), 0.3*pos.y );
            // specular is only high near ground level
            ks = clamp( 2.0 - 1.5*pos.y, 0.0,3.0 );
        }
        else if( tm.y<2.5 || (tm.y >= 7.5 && tm.y < 8.5) )
        {
            // body
            al = vec3(0.02,0.1,0.3);
            ks = 0.5;

            // polka dot pattern
            float f = -1.0+2.0*smoothstep( -2.0,2.0,
                sin(30.0*pos.x*0.1)+sin(30.0*pos.y*0.1)+sin(30.0*pos.z*0.5) );
            al = mix(al, vec3(0.4, 0.1, 0.2), 1.0 - f);
        }
        else if( tm.y<3.5 )
        {
            // eyes
            al = vec3(0.4,0.4,0.4);
            ks = 2.0;
        }
        else if( tm.y<4.5 )
        {
            // pupils
            al = vec3(0.02);
            ks = 10.0;
        }
        else if( tm.y<5.5 )
        {
            // candy
            al = vec3(0.1, 0.02, 0.01);
            vec2 id = floor((pos.xz+0.25)/0.5);
            float fid = id.x*11.1 + id.y*31.7;
            al += vec3(0.01,0.06,0.02)*cos( fid*10.0 + vec3(0.0, 0.2, 0.4) );
            ks = 1.0;
        }
        else if( tm.y<6.5 )
        {
            // pants
            al = vec3(0.4, 0.1, 0.2);
            ks = 3.0;
        }
        else if( tm.y<7.5 )
        {
            // horn
            float hue = fract(atime * 3.0) * 6.28;
            al = hsl2rgb(vec3(hue, 0.9, 0.4));
            ks = 10.0;
        }
        else if( tm.y<9.5 )
        {
            // wings
            al = vec3(0.4, 0.1, 0.2);
            ks = 5.0;
            // polka dot pattern
            float f = -1.0+2.0*smoothstep( -0.2,0.2,
                sin(80.0*pos.x)+sin(80.0*pos.y)+sin(2.0) );
            al += 0.2*f*vec3(0.06,0.06,0.02);
        }
        else
        {
            // everything else
            al = vec3(0.0,0.0,0.0);
            ks = 0.0;
        }
        al = clamp( al, vec3(0.0), vec3(1.0) );

        float occ = calcOcclusion( pos, nor, atime )*tm.z;
        vec3 sun_dir = normalize( vec3(0.8,0.4,0.7) );
        float sun_dif = clamp( dot(nor,sun_dir), 0.0,1.0 );
        // push specular out of place a little
        vec3 sun_hal = normalize(sun_dir - rd + vec3(0.0, 0.25, -0.5));
        float sun_sha = castShadow( pos + nor*0.001, sun_dir, atime );
        vec3 sun_sha_pen = vec3(sun_sha,
                                mix( sun_sha, sun_sha*sun_sha, 0.5 ),
                                sun_sha*sun_sha); // for orangey penumbras.
        float sun_spe = ks*pow( clamp(dot(nor,sun_hal),0.0,1.0), 8.0 )*sun_dif
                          *(0.04+0.96*pow(clamp(1.0+dot(sun_hal,rd),0.0,1.0),5.0));
        float sky_dif = clamp( 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0)), 0.0,1.0 );
        float sky_ref = smoothstep( 0.0,1.5/ks, reflect(rd,nor).y );
        float bou_dif = clamp( 0.01 + 0.99*dot(nor,vec3(0.0,-1.0,0.0)), 0.0,1.0 );
        float fre = clamp( 1.0 + dot(rd,nor), 0.0,1.0 );

        // Check if the guy is generally in shadow (so we can reduce
        // sss when he is. And only apply this to the guy's body, not
        // the whole scene!
        // (Previously it was being applied when he looked down :D
        // and it made the whole terrain flicker when applied D: )
        float guy_sha = 1.0;
        if (tm.y > 1.5 && tm.y <= 2.5) {
        float itime = floor(atime);
        float ftime = fract(atime);
        float y = 4.0*ftime*(1.0 - ftime);
        float x = -1.0+2.0*abs(fract(0.5*atime) - 0.5)/0.5;
        vec3 cen = vec3( x*0.4,
                         pow(y,2.0-y)+0.15,
                         itime+pow(ftime,0.7) );
        // offset the body raycast by a bunch to prevent it intersecting
        // the body/head. We only want to consider environment shadows!
        guy_sha = castShadow( cen+0.8*sun_dir, sun_dir, atime );
        }

        // diffuse (sun, sky, bounce)
        vec3 lin = vec3(0.0);
        lin += sun_dif*vec3(9.1,6.0,2.2)*sun_sha_pen;
        lin += 1.5*sky_dif*vec3(0.5,0.8,3.9)*occ;
        lin += 0.75*bou_dif*vec3(0.3,1.08,0.2)*occ;
        // fake sss
        lin += 2.0*fre*vec3(1.5,0.5,0.4)
                  *(0.5+0.5*sun_dif) // reduce a little in unlit areas.
                  *(0.15+0.85*guy_sha) // reduce a lot when guy in shadow.
                  *(0.5+0.5*tm.z); // reduce a ton when in forced-occlusion places.
        col = al*lin;

        // specular
        col += sun_spe*vec3(8.10,6.00,4.20)*sun_sha;
        col += sky_ref*0.1*vec3(0.6,0.9,2.0)*sky_dif*occ;

        // haze
        col = mix( col, vec3(0.5,0.7,0.9), 1.0-exp(-0.00005*t*t*t) );

#if COLOR_GRADING
        // Inigo hack: boost reds in the lighting colours.
        // Like a curves adjustment. Artistic more than realistic.
        col = pow( col, vec3(0.8,0.9,1.0) );
#endif

//        col = vec3(tm.z*tm.z);
//        col = vec3(occ*occ);
//        col = vec3( guy_sha );
        }

    // Convert to gamma space.
    col = pow( col, vec3(0.4545) );

#if COLOR_GRADING
    // Exposure and contrast adjustment:
    // (Normally would do this in linear space, but this s-curve is only
    // good between 0 and 1.)
    col = clamp( 1.05*col, 0.0,1.0 ); // 5% exposure gain & clamp to prevent burn.
    vec3 contrasty = col*col*(3.0 - 2.0*col); // shallow s-curve.
    col = mix( col, contrasty, 0.25 ); // Just use a bit of the contrasty colours.
#endif

    fragColor = vec4(col,1.0);
}

// V-Drop - Del 19/11/2019 - (Tunnel mix - Enjoy)
// vertical version: https://www.shadertoy.com/view/tdGXWm
#define PI 3.14159

float vDrop(vec2 uv,float t)
{
    uv.x = uv.x*128.0;						// H-Count
    float dx = fract(uv.x);
    uv.x = floor(uv.x);
    uv.y *= 0.05;							// stretch
    float o=sin(uv.x*215.4);				// offset
    float s=cos(uv.x*33.1)*.3 +.7;			// speed
    float trail = mix(95.0,35.0,s);			// trail length
    float yv = fract(uv.y + t*s + o) * trail;
    yv = 1.0/yv;
    yv = smoothstep(0.0,1.0,yv*yv);
    yv = sin(yv*PI)*(s*5.0);
    float d2 = sin(dx*PI);
    return yv*(d2*d2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float d = length(p)+0.1;
	p = vec2(atan(p.x, p.y) / PI, 2.5 / d);
    if (iMouse.z>0.5)
    	p.y *= 0.5;
    float t =  iTime*0.4;
    vec3 col = vec3(1.55,0.65,.225) * vDrop(p,t);	// red
    col += vec3(0.55,0.75,1.225) * vDrop(p,t+0.33);	// blue
    col += vec3(0.45,1.15,0.425) * vDrop(p,t+0.66);	// green
	fragColor = vec4(col*(d*d), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cm = distance(iMouse.xy,fragCoord.xy)/iResolution.x;
 	vec2 center = iResolution.xy/2.;
    float dist = distance(center,fragCoord);
    float cl;
    float cr;
    float R = iResolution.x/2.*((sin(iTime/3.)+2.)/2.)+iResolution.x/10.;
    vec3 col=vec3((dist>R && dist<R+1.)?1.:0.);
    cl = sqrt((R*R)-pow(fragCoord.y-center.y,2.));
    cr = center.x+cl;
    cl = center.x-cl;
    cl = abs(fragCoord.x+cl)/iResolution.x;
    cr = abs(fragCoord.x+cr)/iResolution.x;
    fragColor = vec4(cm,cl,cr,1.0);
}

float Hash21(vec2 p)
{
	p = fract(p*vec2(234.34, 1311.12));
    p += dot(p, p+211.42);  
    return fract(p.x*p.y);
}


vec3 bubbles(vec2 uv)
{
    vec3 col = vec3(0.0);
    uv *= 100.0;

    vec2 gv = fract(uv);  
    vec2 id = floor(uv);

    uv += iTime;

    float n = Hash21(id);

    if(n < 0.01)
    {
    	return col = vec3(smoothstep(0.6, 0.60000001, length(gv - 0.5) * -1.0 + 1.0));

    }

}


float circle(vec2 uv, float r)
{
	return smoothstep(r, r-0.00001, length(uv));
}


#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //UV and ST Coords
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 st = (fragCoord-0.5*iResolution.xy)/iResolution.y;




    //Warping ST Coords
    st *= -2.0;
    st.x += iTime*0.2;
    float bounce = iTime*0.2;

    float cd;
    float freq = 2.0;
    float wind;

    st.y *= 10.0;
    st.y += cos(st.x * (PI * freq))*-1.0;

    //Creating Ocean
    if(st.y < 0.0) cd = 1.0;
    //Creating Wind
    st.y += 3.0;
    if(st.y > 1.0 && st.y < 1.11) wind = 1.0;
    wind *= cos(uv.x * 3.2 + 0.1 + iTime);
    st.y += 2.0;
    uv.x += 2.0;
    if(st.y > 1.0 && st.y < 1.1) wind += 1.0;
    wind *= cos(uv.x * 4.3 + 0.1 + iTime)*2.0;
    st.y += 2.0;
    uv.x += 3.0;
    if(st.y > 1.0 && st.y < 1.09) wind += 1.0;
    wind *= cos(uv.x * 5.1 + 0.1 + iTime)*4.0;
    st.y += 2.0;
    uv.x += 4.0;
    if(st.y > 1.0 && st.y < 1.08) wind += 1.0;
    wind *= cos(uv.x * 2.6 + 0.1 + iTime)*6.0;


    uv.x -= 9.0;


    //Creating Bubbles
    vec3 bubble = 
    	 bubbles(vec2(uv.y + (cos(bounce * freq * PI) * 0.02 + 0.6), uv.x));

    //FloatingBall
 	uv *= 22.0;
    uv.y += cos(bounce * freq * PI);
    float c = circle(uv, 1.0);

    //Masking
    vec3 wave = vec3(cd);
    vec3 ballT = clamp(vec3((wave * -1.0 + 1.0 / c)*-1.0 + 1.0), 0., 1.);
    vec3 ballB = clamp(vec3(c - cd), 0., 1.);
    bubble = vec3(clamp(bubble.x + wave.x, 0.0, 1.0));

    vec3 col = vec3(bubble - ballT + ballB - vec3(wind));

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}

// license: CC BY-NC https://creativecommons.org/licenses/by-nc/4.0/

vec2 r(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return vec2(c * p.x - s * p.y, s * p.x + c * p.y);
}

vec2 opReflect(vec2 v, vec2 normal) {
	v -= 2. * min(0., dot(v, normal)) * normal;
	return v;
}

const vec2 axisH = vec2(0,1);
const vec2 axisV = normalize(vec2(-sqrt(3.), 1));
const vec2 axisH2 = vec2(-axisV.x, axisV.y);

// this does not work as intended, but the result looks interesting enough and I don‚Äôt have time to debug it
float sdHexBox(vec2 position, vec2 halfSize) {
	vec2 absPosition = abs(position);
return max(dot(absPosition, axisH) - halfSize.y, dot(absPosition, axisV) - halfSize.x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	float t = iTime * 2.;

	vec2 uv = (fragCoord - iResolution.xy / 2.) / iResolution.y;
	uv = r(uv, t * 0.05);
	const int segmentCount = 12;
	for (int i = 0; i < segmentCount; i++) {
		float angle = 6.2832 * float(i) / float(segmentCount);
		uv = opReflect(uv, -vec2(cos(angle), sin(angle)));
	}

	float dist = dot(uv, axisH) - 0.08; // core

	float hexRadius1 = cos(t * 0.13) * 0.06 + 0.25;
	float hexDistance1 = sdHexBox(uv - vec2(sin(t * 0.43) * 0.05 + 0.05,hexRadius1), vec2(0.002,0.03 + cos(t * 0.31) * 0.005));

	float hexRadius2 = cos(t * 0.18 + 0.1) * 0.1 + 0.15;
	float hexDistance2 = sdHexBox(uv - vec2(sin(t * 0.51 + 0.2) * 0.04 + 0.04, hexRadius2), vec2(0.002,0.03 + cos(t * 0.35 + 1.) * 0.01));

	float hexRadius3 = cos(t * 0.21 + 0.2) * 0.05 + 0.2;
	float hexDistance3 = sdHexBox(uv - vec2(sin(t * 0.61 + 0.4) * 0.04 + 0.06, hexRadius3), vec2(0.002,0.03));

	dist = min(dist, min(hexDistance1, min(hexDistance2, hexDistance3)));

	dist = min(dist, max(dot(uv, axisV) - 0.02, dot(uv, axisH) - max(hexRadius1,max(hexRadius2, hexRadius3)))); // arms

	float mask = smoothstep(fwidth(dist), 0., dist);
	float value = mask * pow(min(1.0,abs(dist + 0.2) * 5.), 60.);

	vec3 background = mix(vec3(0.1,0.6,0.7), vec3(0.1,0.2,0.5), length(uv) * 2.);

	vec3 refractedBackground = mix(vec3(0.,0.6,0.7), vec3(0.,0.2,0.5), min(1., length(uv) * 3.));

	fragColor = vec4(mix(background, value + refractedBackground, mask),1.);
}

#define PI 3.14159
#define TAU PI*2.
#define t iTime

mat2 rz2 (float a) { float c=cos(a), s=sin(a); return mat2(c,s,-s,c); }
float cyl (vec2 p, float r) { return length(p)-r; }
float cube (vec3 p, vec3 r) { return length(max(abs(p)-r,0.)); }

vec2 path(float z){
    float x = sin(z) - 1.0 * cos(z * 1.) - 4. * sin(z * 0.12345);
    float y = cos(z) - 1. * sin(z * 1.) - 1. * cos(z * 2.12345);
    return vec2(x,y);
}

vec2 path2(float z){
    float x = sin(z) + 1.0 * cos(z * 1.) + .2 * sin(z * 0.12345);
    float y = cos(z) + 1. * sin(z * 1.) + .5 * cos(z * 2.12345);
    return vec2(x,y);
}

vec2 modA (vec2 p, float count) {
    float an = TAU/count;
    float a = atan(p.y,p.x)+an*.5;
    a = mod(a, an)-an*.5;
    return vec2(cos(a),sin(a))*length(p);
}
float smin (float a, float b, float r)
{
    float h = clamp(.5+.5*(b-a)/r,0.,1.);
    return mix(b, a, h) - r*h*(1.-h);
}

float GetDist (vec3 p)
{

    vec3 q =p;    
    vec2 o = path(p.z) /5.0;
    p = vec3(p.x,p.y,p.z)-vec3(o.x,o.y,0.);
    vec2   o2 = path2(p.z) / 5.0;
    q = vec3(q.x,q.y,q.z)-vec3(o2.x,o2.y,0.);
    p.xy *= rz2(p.z*0.3)*0.7;
    q.xy *= rz2(q.z*0.1)*.5;
    p.zy = mod(p.zy ,5.) - 2.5;
    q.zy = mod(q.zy ,5.) - 2.5;
    q.zx = mod(q.zx ,5.) - 2.5;   
    p.xy *= rz2(p.z*sin(t*.3+250.)*2.); 
    q.xy *= rz2(q.z*sin(-t*0.02+250.));
    float cyl2wave = .3+1.5*(sin(p.z+t*0.5)*.1);
    float cylfade = 2.-smoothstep(.0,8.,abs(p.z));
    float cyl2r = 0.02*cyl2wave*cylfade;
    float cylT = 1.;
    float cylC = 1.;
    vec2 cyl2p = modA(p.xy, 8.)-vec2(cyl2wave, 0)*cylfade;
    float cyl2 = cyl(cyl2p, cyl2r);
    cyl2p = modA(p.xy*rz2(-p.z*cylT), cylC)-vec2(cyl2wave, 0)*cylfade;
    vec3 cubP = p;
    float cubC = 0.1;
    cubP.z = mod(cubP.z, cubC)-cubC*1.;
    cubP.xy *= rz2(t*3.);
    float cyl2a = smin(cyl2, cube(cubP,vec3(.1*cyl2wave*cylfade)),.5);
    cyl2wave = .3+1.5*(sin(q.z+t*0.5)*.2);
    cylfade = 2.-smoothstep(.0,8.,abs(q.z));
    cyl2r = 0.06*cyl2wave*cylfade;
    cylT = 1.;
    cylC = 1.;
    cyl2p = modA(q.xy, (abs(sin(t*0.1)+3.)))-vec2(cyl2wave, 0)*cylfade;
    cyl2 = cyl(cyl2p, cyl2r);
    cyl2p = modA(q.xy*rz2(-q.z*cylT), cylC)-vec2(cyl2wave, 0)*cylfade;
    cubP = q;
    cubC = 0.1;
    cubP.z = mod(cubP.z, cubC)-cubC*1.;
    cubP.xy *= rz2(t*3.);  
    float cyl22 = smin(cyl2, cube(cubP,vec3(.1*cyl2wave*cylfade)),.5);
	return smin(cyl2a,cyl22,0.1)*.6;
}

float marchCount;

float RayMarch (vec3 ro, vec3 rd) 
{
 float dO = 0.;        
 marchCount = 0.0;
 for (int i=0; i<68; i++) 
  {
   vec3 p = ro + dO * rd;
   float dS = GetDist (p);
   dO += dS;
   if (dS<0.001 || abs(dO)>100.) break;
   marchCount+= 2./dS*.0009;
  }
 return dO;
}

vec3 GetNormal(vec3 p){
    float d = GetDist(p);
    vec2 e = vec2(0.1,0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
    	GetDist(p-e.yyx));

    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5*iResolution.xy) / iResolution.y;
    vec3 col = vec3(0);    
    vec3 ro = vec3(0.,0.,-t*2.);
    vec3 rd = normalize(vec3(uv.x,uv.y,.8));
    float the = iTime *0.1;
    rd.yx *= mat2(cos(the), -sin(the), sin(the), cos(the));
    float d = RayMarch (ro,rd);
    vec3 p = ro + rd *d;
	float fog = 1. / (2. + d * d *.1);
    vec3 fc = vec3(fog);
   fc *= marchCount * vec3(1., (sin(t +-p.z)*.5),(tan(t +-p.z)*.5));
    fc *= vec3(fog);
    vec3 sky = vec3(0., .1, 0.1);
    fc = mix(sky, fc, 1.5/(d*d/1./1.*.01));
    // Output to screen
    fragColor = vec4(fc,1.0);
}

