const float PI = 3.14159265;

float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }

float sat(float a)
{
  return clamp(a,0.0,1.0);
}

float lenny(vec2 v)
{
  return abs(v.x)+abs(v.y);
}

vec3 negate(vec3 col, bool yes)
{
  if (yes)
     return vec3(1.)-col;
  return col;
}

bool range(float a, float mi, float ma)
{
  return a > mi && a <ma;
}

vec3 drawCircle(vec2 uv, vec2 pos, float rad)
{
  vec3 col;
  col += vec3(0.5,0.,0.34);
  if (rad<0.)
    col = vec3(1.)-col;
    rad = abs(rad);
  float thick = 0.01;
  return col*float(range(length(uv-pos), rad-thick, rad));
}

vec3 drawCir(vec2 uv, vec2 pos, float rad, float diag)
{
  uv = uv-pos;

  float luv = mix(length(uv),lenny(uv),(0.5+0.5*sin(iTime))*diag);
  float grad = (abs(luv - rad)/0.15);
  vec3 col = vec3(pow(sat(1.-grad),5.))*vec3(1.);
  float a = atan(uv.y,uv.x)/PI;
  col += (luv/rad)*1000.*pow(abs(mod((0.3*iTime*PI+(luv*2.*PI)+a+sin(75.*pow(luv,4.2)+15.*iTime+a)*0.05),0.1)-0.05),2.5)*float(luv <rad);
  return col;
}

vec3 rdrMain(vec2 uv)
{
  vec3 colGrad = vec3(0.45,0.34,0.78).yzz;
  vec3 acc;

  acc = mix(colGrad, vec3(0.3), 1.-length(uv));
  acc *=min(pow(max(length(uv),0.1),3.5),1.);
  acc += drawCir(uv, vec2(sin(iTime)*0.2,0.15*cos(iTime*0.4)),(0.4*sin(iTime)+0.6),1.);
  return acc;
}

vec3 rdrLines(vec2 uv)
{
  const int lines = 16;
  vec3 acc;

  for (int i = 0; i< lines;++i)
  {
    float fi = float(i);
    vec2 pos= vec2(rand(vec2(fi, 8.-fi)), rand(vec2(8.-fi,fi)));
    pos-= vec2(0.5);
    pos += (fi/8.)*vec2(sin(iTime*fi/8.),cos(iTime));
    acc += drawCir(uv, pos,0.05*max(1.-pos.x,0.2),0.)*vec3(fi/12.,pos.y,fi/7.);
  }
  return acc;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xx;
  vec2 center = vec2(0.5)*iResolution.xy/iResolution.xx;
  uv = uv - center;
  uv *= 2.5;


  vec3 col = rdrMain(uv);
  float inMask = float(uv.y< (sin(uv.x*10.+iTime*3.)*0.05+0.3*sin(iTime+uv.x)));
  col = negate(col,bool(inMask));
  col *= 1.-pow(length(uv),2.5);
  col += rdrLines(uv);

  fragColor = vec4(col, 1.0);
}

