struct RayCastInfo
{
	int sphereid;//-1 if not hit
	float t0;
    float t1;
};

RayCastInfo calculateRay(vec3 pos, vec3 dir)
{
    int sphereid = -1;
    float truet0=999999999999.;
    float truet1=999999999999.;

    for(int i = -1;i<nrofspheres;i++)
    {
        vec4 sphere = getSphere(i);

        vec3 L = sphere.xyz - pos; 
        float tca = dot(L,dir); 
        if (tca >= 0.)
        {
            float d2 = dot(L,L) - tca * tca; 
            if (d2 <= sphere.w)
            {
                float thc = sqrt(sphere.w - d2); 
                float t0 = tca - thc; 
                float t1 = tca + thc;

                if(t0<truet0)
                {
                    sphereid = i;
                    truet0 = t0;
                    truet1 = t1;
                }

            }
        }
    }

    RayCastInfo tmp;
    tmp.sphereid = sphereid;
    tmp.t0 = truet0;
    tmp.t1 = truet1;
  	return tmp;
}


const int nrofrays = 15;

vec3 calculateMainRay(vec3 startpos, vec3 startdir)
{
    vec3 pos[nrofrays];
    vec3 dir[nrofrays];
    vec3 color[nrofrays];
    float count[nrofrays];
    bool finished[nrofrays];
    int raycount = 1;

    pos[0] = startpos;
    dir[0] = startdir;
    color[0] = vec3(1.0);
    finished[0] = false;
    count[0] = 1.;


    for(int i = 0;i<nrofrays-1;i++)
    {
        for(int o = 0;o<raycount;o++)
        {
            if(!finished[o])
            {
                RayCastInfo tmp = calculateRay(pos[o],dir[o]);

                if(tmp.sphereid!=-1)
                {
                    vec4 sphere = getSphere(tmp.sphereid);
                    vec3 spherecolor = getSphereColor(tmp.sphereid);
					float sphereopacity = getSphereOpacity(tmp.sphereid);

                    pos[o] = pos[o] + dir[o]*tmp.t0;
                    vec3 normal = normalize(pos[o] - sphere.xyz);


                    //refraction
                    if(raycount<nrofrays&&sphereopacity>0.001)
                    {
                        raycount++;

                        float size = tmp.t1 - tmp.t0;

                        vec3 shiftdir = cross(normal, dir[o]);

                        pos[raycount-1] =  pos[o] + dir[o]*tmp.t1 + shiftdir*size;

                        dir[raycount-1] = dir[o];
                        color[raycount-1] = (spherecolor/(size+0.1))*color[o];
                        color[raycount-1] *= sphereopacity;

                        finished[raycount-1] = false;
                        count[raycount-1] = count[o];
                    }

                    //reflection
                    {
                        dir[o] = reflect(dir[o], normal);

                        color[o] *= spherecolor;

                        count[o]++;
                    }


                }
                else 
                {
                    finished[o] = true;
                    color[o] *= skyColor(dir[o]);
                }
            }
        }
    }

    //if(raycount==2) return vec3(0.0, 1.0, 0.0);


    vec3 finalcolor = vec3(0.0);
    for(int i = 0;i<raycount;i++)
    {
        finalcolor += color[i]/count[i];
    }

    return finalcolor/float(raycount);
}

