// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

vec3 N33(vec3 p) {
    vec3 a = fract(p*vec3(123.34,234.34,345.65));
    a+=dot(a,a+34.45);
    return fract(vec3(a.x*a.y,a.y*a.z,a.z*a.x));
}

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec4 combine(vec4 val1, vec4 val2 ){
    return (val1.w < val2.w)?val1:val2;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float metaball(vec3 p, float i, float t) {
    vec3 n = N33(vec3(i));
    vec3 p2 = sin(n*t)*1.0;
    p2.y += 2.1;
    vec3 spp = p-p2;
    float sp = length(spp)-(0.5+(i*0.1));
    return sp;
}

vec4 metaBalls(vec3 p) {
    float res = 1.0;
    float t = iTime*3.0;
    vec4 mb = vec4(1.0);

    float k = 0.9;
    res = smin(res,metaball(p,1.0, t),k); 
    res = smin(res,metaball(p,2.0, t),k); 
    res = smin(res,metaball(p,3.0, t),k); 
    res = smin(res,metaball(p,4.0, t),k); 
    res = smin(res,metaball(p,5.0, t),k); 
    res = smin(res,metaball(p,6.0, t),k); 

    mb = vec4(mix(vec3(p.x,0.0,p.z),vec3(1.0,1.0,1.0),0.5),res*2.0);

    return mb;
}

vec4 GetDist(vec3 p) {

    float _floor = p.y;

    vec4 mb = metaBalls(p);
    vec4 f = vec4(vec3(0.5,0.8,0.5),_floor*0.6);

    vec4 model = combine(mb,f);
    return model;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 dO= vec4(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.w;
        vec4 dS = GetDist(p);
        dO.w += dS.w;
        dO.xyz = dS.xyz;
        if(dO.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = GetDist(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

float GetAmbientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - RayMarch(p + n*d,n).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(2,8,3);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float ao = GetAmbientOcclusion(p,n);

    float lambert = max(.0, dot( n, l))*0.1;

    float shadow = shadowMap(p + n * 0.001, l);

    return vec2((lambert+dif*ao),max(0.9, shadow)) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, -5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.xz *= Rot(radians(iTime*20.0));
    ro.yz *= Rot(radians(-20.0));
    #endif

    vec3 rd = R(uv, ro, vec3(0,1,0), 1.);

    vec4 d = RayMarch(ro, rd);

    if(d.w<MAX_DIST) {
        vec3 p = ro + rd * d.w;

        vec2 dif = GetLight(p);
        col = vec3(dif.x)*d.xyz;

        // This only floor gets shadow effect.
        if(p.y<0.005 && dif.y>=0.899 && dif.y<=0.9){
            float hatch_y_offset = 12.0;
            float lum_threshold_1 = 2.0;
            float lum_threshold_2 = 1.7;
            float lum_threshold_3 = 1.5;
            float lum_threshold_4 = 1.3;

            float lum = length(col.rgb);
            vec3 tc = vec3(1.0);
            vec3 hatchColor  = vec3(0.0, 0.8, 1.0);

            if (lum < lum_threshold_1) 
            {
              if (mod(fragCoord.x + fragCoord.y, hatch_y_offset) == 0.0) 
                tc = hatchColor;
            }  

            if (lum < lum_threshold_2) 
            {
              if (mod(fragCoord.x - fragCoord.y, hatch_y_offset) == 0.0) 
                tc = hatchColor;
            }  

            if (lum < lum_threshold_3) 
            {
              if (mod(fragCoord.x + fragCoord.y - hatch_y_offset, hatch_y_offset) == 0.0) 
                tc = hatchColor;
            }  

            if (lum < lum_threshold_4) 
            {
              if (mod(fragCoord.x - fragCoord.y - hatch_y_offset, hatch_y_offset) == 0.0) 
                tc = hatchColor;
            }

            col *= tc;
        }

    } else {
        // background
        col = vec3(0.3,0.5,0.8);
    }

    fragColor = vec4(col,1.0);
}

