const int nSpheres = 5; 
const float sphereRadius = 3.0 / log2(float(nSpheres));
const float tau = 6.28318;
const float inf = 999.0;
struct Hit { 
    float t; 
    vec3 p; 
    vec3 n;
    int id;
};
Hit noHit;

Hit sphereHit(
    vec3 center, 
    float radius, 
    vec3 rayOrigin, 
    vec3 rayDirection, 
    int id) 
{
    radius += sin(dot(rayOrigin,rayDirection) * 5.0) / 2.0;
    float a = dot(rayDirection, rayDirection);
    vec3 L = rayOrigin - center;
    float b = 2.0 * dot(rayDirection, L);
    float c = dot(L, L) - radius * radius;
    float discrim = b * b - 4.0 * a * c;
    if (discrim < 0.0) return noHit;
    float t = (-b - sqrt(discrim)) / (2.0 * a);
    if (t < 0.0) {
        return noHit;
    }
    vec3 p = rayOrigin + t * rayDirection;
    return Hit(t, p, normalize(p - center), id);
}

Hit hitSomething(vec3 rayOrigin, vec3 rayDirection, float time) {
    const float fns = float(nSpheres);
    Hit minHit = noHit;
    for (int i = 0; i < nSpheres; i++) {
        float fi = float(i);
        float fni = fi / fns;
        float d = sin(time * fni + fi) + 2.5;
        vec3 center = vec3(
            d*cos(tau*fni+time*10.0*fni) + sin(time*10.0) + 1.0, 
            d*sin(tau*fni+time*10.0*fni) + 1.0,
            d*sin(0.5*tau*fni+time*fni*1.4142) + cos(time*10.0) + 1.0);
        Hit hit = sphereHit(
            center, 
            sphereRadius + sphereRadius * 2.0 * pow(float(i%10)/10.0, 5.0), 
            rayOrigin, 
            rayDirection, 
            0);
        if (hit.t <= 0.0 || hit.t > minHit.t) continue;
        minHit = hit;
    }
    Hit hit = sphereHit(vec3(0.0, -41.5, 2.0), 40.0, rayOrigin, rayDirection, 1);
    if (hit.t > 0.0 && hit.t < minHit.t) {
        minHit = hit;
    }
    return minHit;
}

vec3 colorSomething(
    Hit hit, 
    float time, 
    vec3 rayOrigin, 
    vec3 rayDirection, 
    mat3x3 cam) 
{
    if (hit.t > inf - 0.5) {
        return vec3(0.0, 0.75+0.25*sin(time), 1.0);
    }
    vec3 color;
    // diffuse
    if (hit.id == 0) {
        float dup    = 1.0 * dot(hit.n, cam[1]) + 0.0;
        float dright = 1.0 * dot(hit.n, cam[0]) + 0.0;
        float dfwd   = 1.0 * dot(hit.n, -cam[2] ) + 0.0;
        color = vec3(dright, dup, dfwd);
    } else if(hit.id == 1) {
        color = vec3(sin((hit.p.x+iTime)*3.0), cos(hit.p.y*20.0),0.0);
    }
    // specular
    vec3 refl = reflect(rayDirection, hit.n);
    float spec = pow(dot(refl, cam[1]), 20.0);
    if (spec > 0.0) {
        color += vec3(spec, spec, spec);
    }
    return color;
}

const int maxDepth = 4;
vec3 trace( 
    mat3x3 cam, 
    vec3 rayOrigin, 
    vec3 rayDirection, 
    float time)
{
    vec3 color = vec3(0.2);
    float ref = 1.0;
    for (int i = 0; i < maxDepth; i++) {
        Hit minHit = hitSomething(rayOrigin, rayDirection, time);
        if (minHit == noHit) {
            break;
        }
        color += ref * colorSomething(minHit, time, rayOrigin, rayDirection, cam);
        ref *= 0.6;
        rayOrigin = minHit.p;
        rayDirection = reflect(rayDirection, minHit.n);
    }
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    const vec3 zero = vec3(0.0, 0.0, 0.0);
    noHit = Hit(inf, zero, zero, -1);

    // make time optionally srubbable
    float time = (iMouse.z > 0.0) ? iMouse.x / iResolution.x : iTime / 15.0;
    float camDist = 8.0;
    vec3 rayOrigin = vec3(
        1.1 * camDist * sin(tau * time * 0.5), 
        0.0,
        0.9 * camDist * cos(tau * time * 0.5));
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - vec2(1.0, 1.0);
    uv.x *= iResolution.x / iResolution.y;
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 lookat = vec3(0.0, -1.0, 0.0);
    vec3 fwd   = normalize(lookat - rayOrigin);
    vec3 right = normalize(cross(fwd, up));
    vec3 camup = normalize(cross(right, fwd));
    vec3 rayDirection = normalize(fwd + 0.5 * (uv.x*right + uv.y*up));

    fragColor = vec4(trace(
        mat3x3(right, camup, fwd), rayOrigin, rayDirection, time), 1.0);
}

#define PI 3.14159265359
#define EPSILON 1e-4
#define INFTY 1e6

float surfaceSDF(vec3 p) {
    float t = iTime * 0.3;
    return (0.2 - 0.2 * pow(sin(cos(t)), 2.0)) 
        * length(
            sin(abs(sin(0.1 * t * PI) + 2.5) * PI * sin(0.2 * t) * p)
            - vec3(0.0, 0.0, 0.5)
        ) 
        - 0.1 * p.x * p.y * p.z 
        + 0.2 * p.z * p.z * p.z 
        - 0.2 * p.x * p.x;
    }
float getAngle(vec3 p, vec3 q) {
    return acos(dot(normalize(p), normalize(q)));
}
vec3 getNormal(vec3 p) {
    float d = EPSILON;
    return normalize(vec3(
            surfaceSDF(p + vec3(d, 0.0, 0.0)) - surfaceSDF(p),
            surfaceSDF(p + vec3(0.0, d, 0.0)) - surfaceSDF(p),
            surfaceSDF(p + vec3(0.0, 0.0, d)) - surfaceSDF(p)
        ));
}
float getDiff(vec3 p, vec3 dir) {
    float d = EPSILON;
    return (surfaceSDF(p + d*dir) - surfaceSDF(p)) / d;
}
mat3 rotX(float t) {
    return mat3(
        1.0, 0.0, 0.0,
        0.0, sin(t), cos(t),
        0.0, - cos(t), sin(t)
    );
}
mat3 rotY(float t) {
    return mat3(
        sin(t), 0.0, cos(t),
        0.0, 1.0, 0.0,
        - cos(t), 0.0, sin(t)
    );
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 lightDir = vec3(0.0, 4.0, 1.0);

    // fragment position
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    // camera
    vec3 cPos = vec3(0.0, 0.0, 1.0);
    vec3 cDir = vec3(0.0, - 0.0, - 1.0);
    vec3 cUp = vec3(0.0, 1.0, 0.0);
    float targetDepth = 1.0;
    float t = iTime * 0.2;
    cPos = rotX(t) * rotY(t) * cPos;
    cDir = rotX(t) * rotY(t) * cDir;
    cUp = rotX(t) * rotY(t) * cUp;
    lightDir = rotX(t) * lightDir;
    vec3 cSide = cross(cDir, cUp);

    // ray
    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir * targetDepth);
    float ang = getAngle(ray, - normalize(cPos));
    vec3 rPos = cPos + ray;

    // marching loop
    for(int i = 0; i < 120; i ++ ) {
        rPos += 0.015 * ray;
        if (surfaceSDF(rPos) < 0.01) {
            break;
        }
    }
    vec3 normal = getNormal(rPos);
    float diff = clamp(dot(normalize(lightDir - rPos), normal), 0.01, 1.0);

    // hit check
    if (surfaceSDF(rPos) < 0.01) {
        fragColor = vec4(diff * vec3(rPos.x, 1.0, rPos.p), 1.0);
    } else {
        fragColor = vec4(vec3(1.0, 1.0, 1.0), 1.0);
    }
}

vec2 union_objs(in vec2 obj0, in vec2 obj1) {
    if (obj0.x < obj1.x) return obj0;
    return obj1;
}

vec2 smoothUnion(in vec2 obj0, in vec2 obj1, in float k) {
    float h = clamp(0.5 + 0.5 * (obj1.x - obj0.x)/k, 0., 1.);
    return vec2(
        mix(obj1.x, obj0.x, h) - k * h * (1.0 - h),
        obj0.y
    );
}

vec2 plane(in vec3 p) {
    return vec2(p.y + 4., 0);
}

vec2 sphere(in vec3 p, in float r) {
    float d = length(p) - r;
    return vec2(d, 1.);
}

vec2 scene(in vec3 p) {
    return union_objs(
        plane(p),
        smoothUnion(
	        sphere(vec3(p.x, p.y+sin(2.*iTime)*1.5, p.z), 1.5),
            sphere(vec3(p.x+sin(iTime)*3., p.y, p.z), 1.0),
            0.3
        )
    );
}

vec3 floor_color(in vec3 p) {
    if (fract(p.x * 0.2) > 0.2) {
        if (fract(p.z * 0.2) > 0.2) return vec3(0., 1., 0.);
        return vec3(0.5, 0.6, 0.);
    }
    return vec3(0.5, 0.6, 0.);
}

vec3 prim_color(in vec3 p) {
    return vec3(0.6, 0.6, 0.8);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.y / iResolution.x;

    vec2 vPos = -1. + 2. * uv;

    // up vector
    vec3 vuv = vec3(0,1,0);

    // lookat
    vec3 vrp = vec3(0);

    // cam pos
    vec3 prp = vec3(-3, 2, -6);

    // light pos
    vec3 lrp = vec3(-1, 6.5, -8);


    // set up cam
    vec3 vpn = normalize(vrp - prp);
    vec3 u = normalize(cross(vuv, vpn));
    vec3 v = cross(vpn, u);
    vec3 vcv = (prp + vpn);

    vec3 scrCoord = vcv + vPos.x * u * 1. + vPos.y * v * ar;
    vec3 scp = normalize(scrCoord-prp);

    // raymarch!
    const vec3 e = vec3(0.02, 0, 0);
    const float maxd = 100.;
    vec2 d = vec2(0.02, 0.);
    vec3 p;

    float f = 1.;
    for (int i = 0; i < 256; i++) {
        if ((abs(d.x) < .001) || f > maxd) break;
        f += d.x;
        p = prp + scp*f;
        d = scene(p);
    }

    vec3 c;
    if (f < maxd) {
        if (d.y == 0.) {
            c = floor_color(p);
        } else {
            c = prim_color(p);
        }

        vec3 n = vec3(d.x - scene(p - e.xyy).x,
                      d.x - scene(p - e.yxy).x,
                      d.x - scene(p - e.yyx).x);
        vec3 N = normalize(n);
        float b = dot(N, normalize(lrp-p));

        // phong lighting
        c = vec3((b*c + pow(b,64.))*(1. - f*.02));
    } else {
	    c = vec3(.0, 0.1, 0.2);
    }

    fragColor = vec4(c, 1.);
}

// Experimenting with a grid of color combinations. The sides of the grid
// are assumed to be a power of 2.
//
// Similar to this GeoGebra demonstration:
// https://www.geogebra.org/m/eu75wg5x


#define r (sqrt(2.)/4.)
//#define r sqrt(1./(2.*acos(-1.)))

vec3 f2c(float f) // compute the RGB components of a hue value between 0 and 1 
{
    return vec3(smoothstep(1./3.,1./6.,f)+smoothstep(2./3.,5./6.,f),
                smoothstep(0.,1./6.,f)-smoothstep(1./2.,2./3.,f),
                smoothstep(1./3.,1./2.,f)-smoothstep(5./6.,1.,f));
}

float c2i(float c, float f1, float f2) // assign a unique number to every point in a square grid
{
    float s1=0.;
    float s2=0.;

    float p1 = 1.;
    float p2 = 1.;
    for(float i=0.;i<(log2(c));i++) {
    	s1 += p2*mod(floor(f1/p1),2.);
        s2 += 2.*p2*mod(floor(f2/p1),2.);
        p1 *= 2.;
        p2 *= 4.;
    }
	return s1+s2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c1 = 8.;
    vec2 R = iResolution.xy;
    float s = (max(R.x,R.y)-min(R.x,R.y))/2.;
    float AA = 0.01*(c1/8.)*(2000./max(R.x,R.y));
    vec2 uv1 = (fragCoord -.5* R.xy)/R.y;
    vec2 uv = fract(c1*uv1);
    float f1 = floor(c1*(fragCoord.x-s)/R.y);
    float f2 = floor(c1*fragCoord.y/R.y);
    float f3 = c2i(c1,f1,f2);

    float uvl = length(uv-.5);
    vec3 col = f2c(fract((iTime+5.)*.05+f3/(c1*c1)+(.25*(mod(f3,3.)+1.))+abs(floor(mod(f3,4.)/2.)-mod(f3,2.))/2.))*smoothstep(uvl,uvl+AA,r)+
        	   f2c(fract((iTime+5.)*.05+f3/(c1*c1)+abs(floor(mod(f3,4.)/2.)-mod(f3,2.))*(.25*(mod(f3,3.)+1.))))*smoothstep(uvl+AA,uvl,r);
    col *= vec3(step(abs(uv1.x),.5));
    fragColor = vec4(col,1.0);
}

