    vec4 grass = vec4(0.2, 0.8, 0.2, 1) * (-fbm(uv) + 1.0) + fbm(uv.yx + iTime / 3.0) / 5.0;
    vec4 ocean = vec4(0, 0.3, 1.0, 1) * (-fbm(uv) + 1.75);
    vec4 sand = vec4(0.929, 0.788, 0.68, 1);
    vec4 stone = vec4(0.75, 0.75, 0.75, 1) * (-fbm(uv) + 1.25);
    vec4 fire = vec4(1.0, 0.5, 0.0, 1);
    float f = fbm(uv);

    if (f < 0.4) {
        if (f > 0.37) {
            fragColor = mix(sand, ocean, remap(0.38, 0.4, 0.0, 1.0, f));
        } else {
            float f2 = fbm(vec2(uv.yx + 1.0)) / 2.25;
            fragColor = mix(grass, sand, remap(0.25, 0.38, 0.0, 1.0, f - 0.06));

            // Bad slope detector
            if (f < f2) {
            	fragColor = stone;
            } else {
				// Wildfires
