// Signed distance to a 2D rounded X shape. It is exact in the
// exterior, and a bound in the interior.

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm


float sdRoundedX( in vec2 p, in float w, in float r )
{
    p = abs(p);
    return length(p-min(p.x+p.y,w)*0.5) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    // width
	float wi = 0.5 + 0.3*cos( iTime + 2.0 );
    // radious
    float ra = 0.1 + 0.08*sin(iTime*1.2);

	float d = sdRoundedX( p, wi, ra );

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = sdRoundedX(m, wi, ra );
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

    fragColor = vec4(col,1.0);
}

#define VERTICAL_BRICKS 8.0
#define HORIZONTAL_BRICKS 4.0
#define GAP 0.05

#define BRICK_RATIO (HORIZONTAL_BRICKS / VERTICAL_BRICKS)
#define X_GAP (GAP * BRICK_RATIO)
#define BLUR ((2. / iResolution.y) * (VERTICAL_BRICKS / 2.0))
#define X_BLUR (BLUR * BRICK_RATIO)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    uv += fract(iTime) * 0.5 + 1.0;
    uv *= vec2(HORIZONTAL_BRICKS / 2.0, VERTICAL_BRICKS / 2.0);

    int layer_offset = abs(int(uv.y)) & 1;
    // TODO: Fix doubled bricks
    uv.x += float(layer_offset) * 0.5;
    vec2 fract_uv = fract(uv);

    fragColor = vec4(1);

    fragColor *= smoothstep(GAP, GAP + BLUR, fract_uv.y);
    fragColor *= smoothstep(1. - GAP, 1. - (GAP + BLUR), fract_uv.y);

    fragColor *= smoothstep(X_GAP, X_GAP + X_BLUR, fract_uv.x);
    fragColor *= smoothstep(1. - X_GAP, 1. - (X_GAP + X_BLUR), fract_uv.x);
}

float N11(float n ) {
    return fract(sin(n * 314.21)*521.53);
}

float N21(vec2 n) {
	return N11(n.y+N11(n.x));
}
float N31(vec3 n) {
	return N11(n.z+N11(n.y+N11(n.x)));
}
float ease(float n){
	return n*n*(3.-2.*n);
}
float smoothNoise(vec2 uv, float z) {
    vec2 gv = fract(uv * z);
    vec2 id = floor(uv * z);

    float tl = N21(id);
    float tr = N21(id+vec2(1., 0.));
    float bl = N21(id+vec2(0., 1.));
    float br = N21(id+vec2(1., 1.));
    return mix(mix(tl, tr, ease(gv.x)), mix(bl, br, ease(gv.x)), ease(gv.y));
}
float smoothNoise(vec3 xyz, float z) {
    vec3 gv = fract(xyz * z);
    vec3 id = floor(xyz * z);

    float ftl = N31(id);
    float ftr = N31(id+vec3(1., 0., 0.));
    float fbl = N31(id+vec3(0., 1., 0.));
    float fbr = N31(id+vec3(1., 1., 0.));
    float front = mix(mix(ftl, ftr, ease(gv.x)), mix(fbl, fbr, ease(gv.x)), ease(gv.y));

    float rtl = N31(id+vec3(0., 0., 1.));
    float rtr = N31(id+vec3(1., 0., 1.));
    float rbl = N31(id+vec3(0., 1., 1.));
    float rbr = N31(id+vec3(1., 1., 1.));
    float rear = mix(mix(rtl, rtr, ease(gv.x)), mix(rbl, rbr, ease(gv.x)), ease(gv.y));

    return mix(front, rear, ease(gv.z));
}
float noisySphere(vec2 uv, float t) {
    float m = 0.;
    float noise = smoothNoise(vec3(uv,  t),5.);
    float s = .7;
    float a = mix(s*-1., s, noise) *.5 + 1.; 
    uv *= vec2(a);
    m = distance(vec2(.0), uv);
    m = smoothstep(.30, .29, m) - smoothstep(.29, .28, m);
	return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(.5);
    uv.x /= iResolution.y/iResolution.x;

    // Time varying pixel color
    vec3 col = vec3(0.);
    float m = 0.;
    float z = 10.;

    for(float i = 0.; i<=1.; i+=1./256.) {
    	m += noisySphere(uv, i + iTime * .1) * .025;
    }
    // m = noisySphere(uv, .1);
    col = vec3(m);

    // apply color
    col = pow(min(vec3(.9, 1, 1.2)*m, 1.), vec3(4, 3, 12));

    // col.rb = suv;

    // Output to screen
    fragColor = vec4(col,1.0);
}

