float GetDist(vec3 p  ) {	



    float d, dif1, dif2, dif3, dif4;

    d=999.9;
    float planeDist = p.y;

   vec3 pp;
    pp=p;



    vec2 res;
    res = vec2(9999, 0);



     if (res.x < planeDist)
      { res.x = res.x;     	} 
    else
        { 
          res.x = planeDist;
        if (planeDist > 0.5 )
          { res=vec2(res.x,CSky); }
        else
         { 
            res=vec2(res.x, CPlane2);
         } 
     } 



    p.x=p.x+0.025;
    p.y=p.y+1.0;
    p.z=mod(p.z, 8.0)-2.0;



    float sdh1=sdHexPrism( p-vec3(0.0,4,0.0), vec2(2.0,1.0) );
    float sdh2=sdHexPrism( p-vec3(0.0,4,0.0), vec2(2.6,0.8) );

    float sdh3=sdHexPrism( p-vec3(0.0,4,2.0), vec2(2.0,1.0) );
    float sdh4=sdHexPrism( p-vec3(0.0,4,2.0), vec2(2.6,0.8) );

    float sdh5=sdHexPrism( p-vec3(0.0,4,4.0), vec2(2.0,1.0) );
    float sdh6=sdHexPrism( p-vec3(0.0,4,4.0), vec2(2.6,0.8) );

    float sdh7=sdHexPrism( p-vec3(0.0,4,6.0), vec2(2.0,1.0) );
    float sdh8=sdHexPrism( p-vec3(0.0,4,6.0), vec2(2.6,0.8) );



    float sdb1=sdBox(p-vec3(0.0,4,0.), vec3(3,5.0,5) );


    dif1=differenceSDF(sdh2, sdh1);
    dif2=differenceSDF(sdh4, sdh3);
    dif3=differenceSDF(sdh6, sdh5);
    dif4=differenceSDF(sdh8, sdh7);

    res=opU(res, vec2(dif1,7));
    res=opU(res, vec2(dif2,3));
    res=opU(res, vec2(dif3,4));
    res=opU(res, vec2(dif4,5));

    d = res.x;
    mObj.dist = res.x;
    mObj.tipo = res.y;

    return d;
}


float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.2;
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}


vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}



float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}


float GetLightv2(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(1, 2);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}


