const float FRAMES = 60.;

float wnoise (float ix, float iy) {
    iy = mod(iy, 20.*3./5.);
    return mod(ix*ix*26.+ix*iy*41.+ix*55.+iy*77., 16.)/15.;
}

float gnoise (float px, float py) {
    float mx = mod(px, 1.);
    float my = mod(py, 1.);
    mx = 3.*mx*mx-2.*mx*mx*mx;
    my = 3.*my*my-2.*my*my*my;

    px = floor(px);
    py = floor(py);
    return mix(
        mix(wnoise(px, py), wnoise(px+1., py), mx),
        mix(wnoise(px, py+1.), wnoise(px+1., py+1.), mx),
        my
    );
}

