float objSDF (vec3 p) {
    p.z = mod(p.z, 20.)-10.;
    float pyxr = length(p.xy);
    p.x = pyxr*cos(0.);
    p.y = pyxr*sin(0.);

    return length(p-vec3(8., 0., 0.) )-1.;
}

float waterSDF (vec3 p) {
    return p.y+1.+gnoise(p.x*.3, p.z*3./5.+p.x*.4)*.6;
}

