vec3 dobjSDF (vec3 p) {
    float objSDFp = objSDF(p);
    return normalize(
        vec3(
            objSDF(vec3(p.x+1e-3, p.y, p.z))-objSDFp,
            objSDF(vec3(p.x, p.y+1e-3, p.z))-objSDFp,
            objSDF(vec3(p.x, p.y, p.z+1e-3))-objSDFp
        )
    );
}

