    }
    float lighting = 1.;
    //lighting *= clamp(dot(dobjSDFp, sund()), 0., 1.);
    // lighting = .7+.3*lighting;
    return TEXp*lighting;
}

vec3 waterTEX (vec3 p, vec3 d, vec3 dwaterSDFp) {
    vec3 TEXp = vec3(.4, .8, 1.);
    // TEXp *= .8+.2*mod(floor(p.x*3.)+floor(p.y*3.), 2.);
    float lighting = 1.;
    lighting *= clamp(dot(dwaterSDFp, sund()), 0., 1.);
    lighting = .5+.5*lighting;
    return TEXp*lighting;
}

float objSPEX (vec3 p, vec3 d, vec3 dobjSDFp) {
    return pow(
        clamp(
            dot(
                reflect(
                    d,
                    dobjSDFp
                ),
            	sund()
            ),
        	0.,
        	1.
        ),
        10.
    );
}

float waterSPEX (vec3 p, vec3 d, vec3 dwaterSDFp) {
    return 0.*pow(
        clamp(
            dot(
                reflect(
                    d,
                    dwaterSDFp
                ),
            	sund()
            ),
        	0.,
        	1.
        ),
        1.
    );
}

vec3 march (vec3 p, vec3 d) {
    vec3 cam = p;
    float objSDFp;
    float waterSDFp;
    float auroraSDFp;
    float SDFp;

    vec3 dobjSDFp;
    vec3 dwaterSDFp;

    vec3 TEXp;
    vec3 finalcol = vec3(0.);
    float shiny = 1.;
    float rxcount = 0.;
    float closestaurora = 1e7;

    for (int i=0; i<460; ++i) {
        objSDFp = objSDF(p);
        waterSDFp = abs(waterSDF(p));
        // auroraSDFp = auroraSDF(p);
        SDFp = min(objSDFp, waterSDFp);
        // SDFp = min(SDFp, auroraSDFp);
        closestaurora = min(closestaurora, auroraSDFp);
        if (SDFp < mix(1e-4, 1e-2, min(length(p-cam)/20., 0.) )) {
            p = p+d*SDFp*.95;
            // if (auroraSDFp < waterSDFp && auroraSDFp < objSDFp) {
            //     break;
            // }
            // else {
                if (objSDFp < waterSDFp) {
                    dobjSDFp = dobjSDF(p);
                    TEXp = objTEX(p, d, dobjSDFp, arg);
                    finalcol = finalcol+clamp(TEXp*.4+objSPEX(p, d, dobjSDFp), 0., 1.)*shiny;
                    shiny *= .6;
                    d = reflect(d, dobjSDFp);
                    p = p+d*.2;
                    ++rxcount;
                    ++rxcount;
                    //break;
                }
                else {
                    dwaterSDFp = dwaterSDF(p);
                    TEXp = waterTEX(p, d, dwaterSDFp);
                    finalcol = finalcol+clamp(TEXp*.6+waterSPEX(p, d, dwaterSDFp), 0., 1.)*shiny;
                    shiny *= 1.-.6;
                    d = reflect(d, dwaterSDFp);
                    p = p+d*smoothstep(.05, 1., length(p-cam)/50.);
                    ++rxcount;
                }
            // }
            if (rxcount > 2.) {
                break;
            }
            if (p.y < -10. || p.y > 20.) {
                break;
            }
        }
        p = p+d*SDFp;
        if (length(p-cam) > 170.) {
            break;
        }
    }
    // add the sky color
    // float am = 1./(1.+closestaurora/3.)*clamp(1.-sqrt(p.y-30.+.1)/6., 0., 1.);
    // vec3 acol = vec3(0., .8, .9);
    // if (am > .8) {
    //     acol = mix(acol, vec3(1.), (am-.8)/.2);
    // }
    // am *= .7;
    // finalcol = finalcol+acol*shiny*am;
    // shiny *= 1.-am;

    finalcol = finalcol+shiny*skyTEX(d);

    return finalcol;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    vec2 screen = (fragCoord-iResolution.xy/2.)/iResolution.y;
    vec2 maus =   (iMouse.xy-iResolution.xy/2.)/iResolution.y;
    if (iMouse.x == 0.) {
        maus.x = 0.;
        maus.y = 0.;
    }
    vec3 d = normalize(vec3(screen.x, screen.y, 1.));
    // rotate
    float dzy = atan(d.z, d.y)+maus.y*PI*.5*3.;
    float dzyr = sqrt(d.z*d.z+d.y*d.y);
    d.y = dzyr*cos(dzy);
    d.z = dzyr*sin(dzy);
    float dzx = atan(d.z, d.x)+maus.x*PI*2.;
    float dzxr = sqrt(d.z*d.z+d.x*d.x);
    d.x = dzxr*cos(dzx);
    d.z = dzxr*sin(dzx);


    vec3 retina = march(vec3(0., 2., arg*20.), d);
    fragColor = vec4(retina, 1.);
}

