/*
  Provides coordinates useful for flexible shapes based on generalized toroidal
  sections (e.g. fish bodies, snakes, wings); preferable to sheared coordinates
  or multiple linear segements.
*/

float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float SmoothBump (float lo, float hi, float w, float x);

vec3 ltDir, qHit;
float tCur, dstFar, dLoop, sLoop, rCyl, sDir;
int idObj;
const int idTube = 1, idBase = 2, idGrnd = 3;
const float pi = 3.14159, sqrt3 = 1.73205;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 b;
  float dMin, d, hBase, aLoop, lb;
  dMin = dstFar;
  d = p.y;
  DMIN (idGrnd);
  hBase = 1.6;
  p.x *= sDir;
  aLoop = 0.25 * pi / sLoop;
  q = p;
  q.y -= 2. * hBase;
  d = PrSphDf (q, rCyl);
  q.xy = Rot2D (q.xy, 0.5 * pi);
  q.xy = Rot2D (vec2 (q.x, q.y - dLoop), aLoop - 0.5 * pi);
  b = vec2 (length (q.xy) - dLoop, q.z);
  lb = length (b);
  d = min (d, max (lb - rCyl, dot (vec2 (q.x, abs (q.y)), sin (aLoop + vec2 (0., 0.5 * pi)))));
  d = min (d, PrSphDf (vec3 (Rot2D (q.xy, aLoop) + vec2 (dLoop, 0.), q.z), rCyl));
  if (d < dMin) {
    dMin = d;
    idObj = idTube;
    qHit = vec3 (vec2 (atan (q.y, - q.x) * dLoop / (0.25 * pi), atan (b.x, b.y)) / pi, lb);
  }
  q = p;
  q.y -= hBase - 0.5 * rCyl;
  d = PrCylDf (q.xzy, rCyl, hBase - 0.5 * rCyl);
  DMIN (idBase);
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (sqrt3/2.) - p.x + 0.5 * min (p.x - sqrt3 * p.y, 0.);
}

vec3 ObjCol (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn;
  vec2 p, ip;
  float nDotL, sh, c;
  vn = ObjNf (ro);
  nDotL = max (dot (vn, ltDir), 0.);
  if (idObj == idTube) {
    if (qHit.z < 0.7 * rCyl) {
      col4 = vec4 (1., 1., 0., 0.2) * (0.2 + 0.8 * smoothstep (0.1, 0.2, qHit.z / rCyl));
    } else {
      p = qHit.xy * vec2 (2. * sqrt3, 3.);
      ip = PixToHex (p);
      c = mod (dot (mod (2. * ip + ip.yx, 3.), vec2 (1., 2.)), 3.);
      col4 = vec4 (1., 0., 0., 0.2);
      if (c == 1.) col4.rgb = col4.gbr;
      else if (c == 2.) col4.rgb = col4.brg;
      col4 = mix (vec4 (1., 1., 0., 0.2), col4, smoothstep (0.05, 0.1,
         HexEdgeDist (p - HexToPix (ip))));
    }
    nDotL *= nDotL;
  } else if (idObj == idBase) {
    col4 = vec4 (0.8, 0.8, 0.7, 0.1);
  } else if (idObj == idGrnd) {
    col4 = vec4 (0.4, 0.4, 0.45, 0.05);
  }
  sh = ObjSShadow (ro, ltDir);
  col4.rgb = col4.rgb * (0.2 + 0.8 * sh * nDotL) +
     0.2 * step (0.95, sh) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  return col4.rgb;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col;
  float dstObj, tCyc, t;
  tCyc = 10.;
  t = tCur / tCyc;
  sLoop = 1./3. + 200. * pow (1. - SmoothBump (0.25, 0.75, 0.24, mod (t, 1.)), 8.);
  sDir = 1.; // 2. * floor (mod (t, 2.)) - 1.;
  dLoop = 6. * sLoop;
  rCyl = 0.8;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    col = ObjCol (ro, rd);
   } else {
    col = vec3 (0.5);
  }
  return pow (clamp (col, 0., 1.), vec3 (0.8));
}

#define AA  1

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  el = 0.;
  az = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.1 * tCur;
  }
  el = clamp (el, -0.4 * pi, 0. * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 6., -25.);
  zmFac = 3.;
  rd = vuMat * normalize (vec3 (uv, zmFac));
  dstFar = 70.;
  ltDir = vuMat * normalize (vec3 (0.7, 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

#define T iTime
#define PI 3.141593
#define E 0.001
#define R iResolution
#define MD 100.
#define GS 10.

mat2 rot(float a){ float ca = cos(a); float sa = sin(a); return mat2(ca,sa,-sa,ca);}
float cylSDF(vec3 p, float r){return length(p.yz)-r;}

float kifs(vec3 p)
{
  for(int i = 0; i < 10; ++i)
  {    
    float t1 = T;

    p.xy *=rot(t1*0.2);
    p.yz *= rot(t1*.07);

    p = abs(p);
    p-= vec3(.1,.2,.1);
  }

  float g = 1.0;
  p.x = mod(p.x-g*.5,g)-g*.5;

  return cylSDF(p, .05);
}

float sceneSDF(vec3 p)
{
  return kifs(p); 
}

float raycast(in vec3 p,in vec3 dir,out float att)
{
  float d = 0.;
  for(int i= 0; i <= 256; ++i)
  {
   d = sceneSDF(p);
   if(d <= E) break;
   if(d > MD) break;
   p += dir * d;
   att+= .1/(abs(d)+.1);
  }

  return d;
}

void mainImage(out vec4 O, in vec2 U)
{
  vec2 uv = (2.*U-R.xy)/R.y;
  vec4 color = vec4(0);

  vec3 eye = vec3(0,0,-3.8);

  vec3 dir = normalize(vec3(uv,1));
  dir.xy *= rot(T*.2);

  float att = 0.;

  float d = raycast(eye, dir, att);

  color.rgb += att*.06 * vec3(.05,.03,.95);
  color.rgb = pow(color.rgb, vec3(0.4545));
  color *= 1.1-length(uv/GS); 

  O = color;
}

/*
	Simplified and adapted from:
	https://www.shadertoy.com/view/MlKBWw
*/

#define TAU 6.28318

// Uncomment the following line to get a flare in the center (Disney-style)
#define FLARE 1

// The way this shader works is by looking at the screen as if it were a disk and then
// this disk is split into a number of slices centered at the origin. Each slice renders
// a single trail. So this setting controls the overall density of the effect:
#define NUM_SLICES 125.0
//#define NUM_SLICES 50.0

// Each trail is rendered within its slice; but to avoid generating regular patterns, we
// randomly offset the trail from the center of the slice by this amount:
const float MAX_SLICE_OFFSET = 0.4;

// This is the length of the effect in seconds:
const float T_MAX = 2.0;
// T_JUMP is in normalized [0..1] time: this is the time when the 
// trails zoom out of view because we've jumped into hyperspace:
const float T_JUMP = 0.75;
// This is the speed during the final jump:
const float jump_speed = 15.0;

// I've noticed that the effect tends to have a bluish tint. In this 
// shader, the blue color is towards the start of the trail, and the 
// white color towards the end:
const vec3 blue_col = vec3(0.3, 0.3, 0.5);
//const vec3 white_col = vec3(0.95, 0.95, 1.0);
const vec3 white_col = vec3(0.85, 0.85, 0.9);
const vec3 flare_col = vec3(0.9, 0.9, 1.4);

float array_mix(in float[4] array, in int n, float index)
{
    float dec = fract(index);
    int i = int(floor(index));
    if (i >= n - 1)
        return array[i];
    else
    	return mix(array[i], array[i+1], dec);
}

float sdLine( in vec2 p, in vec2 a, in vec2 b, in float ring )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - ring;
}

float rand(vec2 co){
    return fract(sin(dot(co.xy, vec2(12.9898,78.233))) * 43758.5453);
}

/*
	Lens flare from: https://www.shadertoy.com/view/XdfXRX
	and: https://www.shadertoy.com/view/4sX3Rs 
*/
vec3 lensflare(vec3 uv, vec3 pos, float flare_size, float ang_offset)
{
    float z = uv.z / length(uv.xy);
	vec2 main = uv.xy - pos.xy;
    float dist = length(main);
	float num_points = 2.71;
    float disk_size = 0.2;
    float inv_size = 1.0 / flare_size;
	float ang = atan(main.y, main.x) + ang_offset;
    float fade = (z < 0.0) ? -z : 1.0;
    //if (z < 0.0) return vec3(0.0);

    float f0 = 1.0/(dist * inv_size + 1.0);
    f0 = f0 + f0 * (0.1 * sin((sin(ang*2.0 + pos.x)*4.0 - cos(ang*3.0 + pos.y)) * num_points) + disk_size);
    if (z < 0.0)
		return clamp(mix(vec3(f0), vec3(0.0), 0.75 * fade), 0.0, 1.0);
    else
        return vec3(f0);
}

vec3 cc(vec3 color, float factor, float factor2) // color modifier
{
	float w = color.x+color.y+color.z;
	return mix(color, vec3(w) * factor, w * factor2);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 color = vec3(0.0);
    float time = mod(iTime, T_MAX);
    float t = time / T_MAX;
    vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 mo = (2.0 * iMouse.xy - iResolution.xy) / min(iResolution.x, iResolution.y);
    p += vec2(0, -0.2); // In XWA the aiming HUD is not at the screen's center

    float ta = TAU * mod(iTime, 8.0) / 8.0;
    //ta = 12.0 * 0.01745;

    float ay = 0.0, ax = 0.0, az = 0.0;
    if (iMouse.z > 0.0) {
        ay = 3.0 * mo.x;
        ax = 3.0 * mo.y;
    }
    mat3 mY = mat3(
         cos(ay), 0.0,  sin(ay),
         0.0,     1.0,      0.0,
        -sin(ay), 0.0,  cos(ay)
    );

    mat3 mX = mat3(
        1.0,      0.0,     0.0,
        0.0,  cos(ax), sin(ax),
        0.0, -sin(ax), cos(ax)
    );

    /*
    mat3 mZ = mat3(
        cos(az), sin(az), 0.0,
       -sin(az), cos(az), 0.0,
            0.0,     0.0, 1.0
    );
    */
    mat3 m = mX * mY;
    //p += vec2(0.0, -0.1); // + 12.0*m;
    //p = (mX * vec3(p, 1.0)).xy;

    float p_len = length(p);
    vec3 v = vec3(p, 1.0);
    v = m * v;

    // The following line reverts the effect:
    //t = 1.0 - t;
    // DEBUG
    //t = 0.5;
    // DEBUG

    //float[4] flare_size;
    //flare_size[0] = 0.0

    float trail_start, trail_end;
    // Fade all the trails into view from black to a little above full-white:
    float fade = clamp(mix(0.1, 1.1, t * 2.0), 0.0, 2.0);
    //fade = 1.0;

    // Each slice renders a single trail; but we can render multiple layers of
    // slices to add more density and randomness to the effect:
	for(float i = 0.0; i < 80.0; i++) 
    //float i = 1.0;
    {
        vec3 trail_color = vec3(0.0);
		//float angle = (atan(v.y, v.x) / 3.141592 / 3.0 + 0.5);
        float angle = atan(v.y, v.x) / 3.141592 / 2.0 + 0.13 * i;

		float slice = floor(angle * NUM_SLICES);
        float slice_fract = fract(angle * NUM_SLICES);

        // Don't center the trail in the slice: wiggle it a little bit:
        float slice_offset = MAX_SLICE_OFFSET * 
            rand(vec2(slice, 4.0 + i * 25.0)) - (MAX_SLICE_OFFSET / 2.0);


        // Without dist, all trails get stuck to the walls of the
        // tunnel.
        //float dist = rand(vec2(slice, 1.0 + i * 10.0)) * (2.0 + i);
        float dist = 10.0 * rand(vec2(slice, 1.0 + i * 10.0)) - 5.0;
        float z = dist * v.z / length(v.xy);
        // When dist is negative we have to invert a number of things:
        float f = sign(dist);
        if (f == 0.0) f = 1.0;
        // This is the speed of the current slice
        float fspeed = f * (0.1 * rand(vec2(slice, 1.0 + i * 10.0)) + i * 0.01);
        float fjump_speed = f * jump_speed;

		//trail_start = 5.0 + 2.0 * rand(vec2(slice, 0.0 + i * 10.0));
        trail_start = 10.0 * rand(vec2(slice, 0.0 + i * 10.0)) - 5.0;

        // Accelerate the trail_start:
        trail_start -= mix(0.0, fjump_speed, smoothstep(T_JUMP, 1.0, t));
        trail_end = trail_start - t * fspeed;

        //float trail_x = smoothstep(trail_start, trail_end, p_len);
        float trail_x = smoothstep(trail_start, trail_end, z);
        trail_color = mix(blue_col, white_col, trail_x);

        // This line computes the distance from the current pixel, in "slice-coordinates"
        // to the ideal trail centered at the slice center. The last argument makes the lines
        // a bit thicker when they reach the edges as time progresses.
        float h = sdLine(
            vec2(slice_fract + slice_offset, z),
            vec2(0.5, trail_start), 
            vec2(0.5, trail_end),
            //0.0);
	       	mix(0.0, 0.015, t * z));

        //h = smoothstep(0.075, 0.01, abs(h) - 0.05);
        // This threshold adds a "glow" to the line. This glow grows with
        // time:
        //float threshold = mix(0.04, 0.175, smoothstep(0.0, 0.5, t));
        float threshold = 0.09;
        h = (h < 0.01) ? 1.0 : 0.85 * smoothstep(threshold, 0.0, abs(h));

        trail_color *= fade * h;
        // DEBUG
        // This part displays the size of each slice.
        //float r = 0.0;
        //if (trail_start <= p_len && p_len <= trail_end)
        //    r = 1.0;
        //trail_color = vec3(h, h, h);
        // DEBUG

        // Accumulate this trail with the previous ones
        color = max(color, trail_color);
	}

#ifdef FLARE
    // Add the disk at the center to transition into the hyperspace
    // tunnel
    float flare_size = mix(0.0, 0.1, smoothstep(0.35, T_JUMP + 0.2, t));
    flare_size += mix(0.0, 20.0, smoothstep(T_JUMP + 0.05, 1.0, t));
    vec3 flare = flare_col * lensflare(v, vec3(0.0), flare_size, t);
    color += cc(flare, 0.5, 0.1);
    //color += flare;
    // Whiteout
    color += mix(0.0, 1.0, smoothstep(T_JUMP + 0.1, 1.0, t));
#else
    // Whiteout
    color += mix(0.0, 1.0, smoothstep(T_JUMP - 0.0, 1.0, t));
#endif
	fragColor = vec4(color, 1.0);
}

/*
	Simplified and adapted from:
	https://www.shadertoy.com/view/MlKBWw
*/

#define TAU 6.28318

// The way this shader works is by looking at the screen as if it were a disk and then
// this disk is split into a number of slices centered at the origin. Each slice renders
// a single trail. So this setting controls the overall density of the effect:
#define NUM_SLICES 125.0
//#define NUM_SLICES 50.0

// Each trail is rendered within its slice; but to avoid generating regular patterns, we
// randomly offset the trail from the center of the slice by this amount:
const float MAX_SLICE_OFFSET = 0.4;

// This is the length of the effect in seconds:
const float T_MAX = 2.0;
// T_JUMP is in normalized [0..1] time: this is the time when the trails zoom out of view
// because we've jumped into hyperspace:
const float T_JUMP = 0.90;
// This is the speed during the final jump:
const float jump_speed = 5.0;

// I've noticed that the effect tends to have a bluish tint. In this shader, the blue color
// is towards the start of the trail, and the white color towards the end:
const vec3 blue_col = vec3(0.3, 0.3, 0.6);
const vec3 white_col = vec3(0.8, 0.8, 0.95);


float sdLine( in vec2 p, in vec2 a, in vec2 b, in float ring )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - ring;
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 color = vec3(0.0);
    float time = mod(iTime, T_MAX);
    float t = time / T_MAX;

    vec2 mo = (2.0 * iMouse.xy - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);
    float p_len = length(p);    
    p += vec2(0, -0.2); // In XWA the aiming HUD is not at the screen's center

    float ta = TAU * mod(iTime, 8.0) / 8.0;
    //ta = 12.0 * 0.01745;
    float ay = 0.0, ax = 0.0, az = 0.0;
    if (iMouse.z > 0.0) {
        ay = 3.0 * mo.x;
        ax = 3.0 * mo.y;
    }
    //ay += 135.0;
    mat3 mY = mat3(
         cos(ay), 0.0,  sin(ay),
         0.0,     1.0,      0.0,
        -sin(ay), 0.0,  cos(ay)
    );

    mat3 mX = mat3(
        1.0,      0.0,     0.0,
        0.0,  cos(ax), sin(ax),
        0.0, -sin(ax), cos(ax)
    );
    mat3 m = mX * mY;

    vec3 v = vec3(p, 1.0);
    v = m * v;

    float trail_start, trail_end, trail_length = 1.0, trail_x;
    // Fade all the trails into view from black to a little above full-white:
    float fade = mix(1.4, 0.0, smoothstep(0.65, 0.95, t));
    //float fade = 1.2;

    // Each slice renders a single trail; but we can render multiple layers of
    // slices to add more density and randomness to the effect:
	for(float i = 0.0; i < 60.0; i++) 
    //float i = 1.0;
    {
        vec3 trail_color = vec3(0.0);
		//float angle = (atan(v.y, v.x) / 3.141592 / 3.0 + 0.5);
        float angle = atan(v.y, v.x) / 3.141592 / 2.0 + 0.13 * i;

		float slice = floor(angle * NUM_SLICES);
        float slice_fract = fract(angle * NUM_SLICES);
        // Don't center the trail in the slice: wiggle it a little bit:
        float slice_offset = MAX_SLICE_OFFSET * 
            rand(vec2(slice, 4.0 + i * 25.0)) - (MAX_SLICE_OFFSET / 2.0);
        // Without dist, all trails get stuck to the walls of the
        // tunnel. Allowing dist to be negative gives a more homogeneous 
        // coverage of all the space, both in front and behind the 
        // camera.
        float dist = 10.0 * rand(vec2(slice, 1.0 + i * 2.0)) - 5.0;
        float z = dist * v.z / length(v.xy);

        // When dist is negative we have to invert a number of things:
        float f = sign(dist);
        if (f == 0.0) f = 1.0;
        // This is the speed of the current slice
        float fspeed = f * (rand(vec2(slice, 1.0 + i * 0.1)) + i * 0.01);
        float fjump_speed = f * jump_speed;
        float ftrail_length = f * trail_length;

        trail_end = 10.0 * rand(vec2(slice, i + 10.0)) - 5.0;
		trail_end -= t * fspeed;

        // Adding to the trail pushes it "back": Z+ is into the screen
        // away from the camera... unless f is negative, then we invert
        // the rules
        trail_start = trail_end + ftrail_length;
        if (f >= 0.0) {
            // Shrink the trails into their ends:
            trail_start = max(trail_end,
                              trail_start - (t * fspeed) - 
                                mix(0.0, fjump_speed, 
                                                smoothstep(0.5, 1.0, t))
                              );
            //float trail_x = smoothstep(trail_start, trail_end, p_len);
        } else {
            // Shrink the trails into their ends:
            trail_start = min(trail_end,
                              trail_start - (t * fspeed) -
                                mix(0.0, fjump_speed, 
                                                smoothstep(0.5, 1.0, t))
                              );
        }
        trail_x = smoothstep(trail_start, trail_end, z);
        trail_color = mix(blue_col, white_col, trail_x);

        // This line computes the distance from the current pixel, in "slice-coordinates"
        // to the ideal trail centered at the slice center. The last argument makes the lines
        // a bit thicker when they reach the edges as time progresses.
        float h = sdLine(
            vec2(slice_fract + slice_offset, z),
            vec2(0.5, trail_start), 
            vec2(0.5, trail_end),
        	mix(0.0, 0.015, z));

        // This threshold adds a "glow" to the line. This glow grows with
        // time:
        float threshold = mix(0.12, 0.0, smoothstep(0.5, 0.8, t));
        //float threshold = 0.12;
        h = (h < 0.01) ? 1.0 : 0.75 * smoothstep(threshold, 0.0, abs(h));

        trail_color *= fade * h;
        // DEBUG
        // This part displays the size of each slice.
        //float r = 0.0;
        //if (trail_start <= p_len && p_len <= trail_end)
        //    r = 1.0;
        //trail_color = vec3(h, h, h);
        // DEBUG

        // Accumulate this trail with the previous ones
        color = max(color, trail_color);
	}
    // Whiteout
    color += mix(1.0, 0.0, smoothstep(0.0, 0.2, t));
	fragColor = vec4(color, 1.0);
}

