// Draw line and circle functions from Loeizd: https://www.shadertoy.com/view/4ljfRD

float drawLine (vec2 p1, vec2 p2, vec2 uv, float a)
{
    float r = 0.;
    float one_px = 1. / iResolution.x; //not really one px

    // get dist between points
    float d = distance(p1, p2);

    // get dist between current pixel and p1
    float duv = distance(p1, uv);

    //if point is on line, according to dist, it should match current uv 
    r = 1.-floor(1.-(a*one_px)+distance (mix(p1, p2, clamp(duv/d, 0., 1.)),  uv));

    return r;
}

float drawCircle(vec2 p, float d, vec2 uv)
{
    return (distance(p, uv) <= d) ? 1. : 0.;
}

// Basic map function from: https://stackoverflow.com/questions/17134839/how-does-the-map-function-in-processing-work

float map(float value, 
                              float istart, 
                              float istop, 
                              float ostart, 
                              float ostop) {
    return ostart + (ostop - ostart) * ((value - istart) / (istop - istart));
}


vec3 bodycolor1 = vec3(1.0,0.8,0.8);
vec3 bodycolor2 = vec3(0.2,0.0,0.3);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime*45.; //Pro Tipâ¢: multipluy time to go faster!

    vec2 noise_vec = vec2(-t * 0.05, t * 0.05);

    float lines = 0.;

    vec2 init = vec2(0.5+sin(t*0.01) * 0.3, 0.5+cos(t*0.01) * 0.3);
    #ifdef DANCE
    init += noise(noise_vec)*0.15;
    #endif
    //vec2 noisebump = vec2(sin(noise(noise_vec)), cos(noise(noise_vec))) * 0.4;
    //init += noisebump;

    #ifndef REVERSED
    // Main fadeoff mapping
    float fadeoff = map(distance(init,uv),0.,LIMB_LENGTH*float(JOINTS),FATNESS,0.0);//((LIMB_LENGTH*float(JOINTS))-distance(init, uv));
	#else
    // Reversed fadeoff mapping (gives a cool widening effect)
    float fadeoff = map(distance(init,uv),0.,LIMB_LENGTH*float(JOINTS),0.0,FATNESS);
    #endif

    for(int i = 0; i < LIMBS; ++i) {
            vec2 prevpos = init;
        for(int j = 0; j < JOINTS; ++j) {
            float theta = map(noise(noise_vec), -1., 1., pi, -pi) + (t*0.01);
            //float theta = map(noise(noise_vec), -1., 1., pi/3.-(pi/4.), -pi/3.-(pi/4.)) + (t*0.01);
            noise_vec += float(j);
            vec2 nextpos = prevpos + (vec2(sin(theta), cos(theta)) * LIMB_LENGTH);
            if(lines == 0.)
            	lines += drawLine(prevpos, nextpos, uv, fadeoff*16.) * fadeoff;//* map(float(j), 0., float(JOINTS), 1.0, 0.0);
            prevpos = nextpos;
        }
    }

    if(lines == 0.)
        lines += drawCircle(init, .02+(sin(t*0.1) + 1.)*0.02 * noise(noise_vec), uv) * fadeoff;

    vec3 col = vec3(0.,0.,0.);
    col.r = map(lines, 1.0, 0.0, bodycolor1.r, bodycolor2.r);
    col.g = map(lines, 1.0, 0.0, bodycolor1.g, bodycolor2.g);
    col.b = map(lines, 1.0, 0.0, bodycolor1.b, bodycolor2.b);

	fragColor = vec4(col.r, col.g, col.b, 1.);
}

//Thanks IQ for distance functions
