#define pi 3.14159
#define SURF_DIST .001
#define MAX_DIST 100.
#define MAX_STEPS 100

const float eps = 0.001;

mat2 rot(float a)
{
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float aaStep(float edge, float gradient)
{
    float halfPix = fwidth(gradient) / 2.0;
    float low = edge - halfPix;
    float hi  = edge + halfPix;
    return clamp((gradient - low) / (hi - low), 0.0, 1.0);
}

vec2 KIFS(vec2 uv)
{
    uv *= 1.5;
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec3 col = vec3(0.);
    uv.x = abs(uv.x);
    float angle = 5./6. * pi;
    uv.y += tan(angle)*0.5;
    vec2 norm = vec2(sin(angle), cos(angle));
    float dist = dot(uv - vec2(0.5, 0.0), norm);
    uv -= norm * 2. * max(dist, 0.);
    angle = 2./3. * pi * (1. - mouse.y);
    norm = vec2(sin(angle), cos(angle));
	uv.x += 0.5;
    float scale = 1.0;
    for(int i = 0; i < 4; i++){
        scale *= 3.;
        uv *= 3.;
    	uv.x -= 1.5;
        uv.x = abs(uv.x);
        uv.x -= .5;
        uv *= rot(iTime*0.2 + mouse.x*10.);
        uv -= norm * 2. * min(dot(uv, norm), 0.);
    }
    uv/= scale;
    return uv;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdf(vec3 p)
{
    p *= 0.5;
    float f = max(min(abs(KIFS(p.xy).y), 1.0), sdBox(p, vec3(0.5, 0.5, 0.05))) - 0.0005;
	return min(f, -p.z + 0.1);
}

vec2 march(vec3 ro, vec3 rd)
{
	float t = 0.0 , d = MAX_DIST, it = 0.;
    for (int i = 0; i < MAX_STEPS; i++)
    {
        d = sdf(ro+t*rd);
     	t += d;
        if(abs(d) < SURF_DIST || t > MAX_DIST) break;
        it += 1.0;
    }
    return vec2(t, it/float(MAX_STEPS));
}

float getShadow(vec3 p, vec3 n, vec3 ld)
{
    p += 2. * eps * n;
    float t = 0.0, d = MAX_DIST;
    for(int i = 0; i < MAX_STEPS; i++)
    {
        d = sdf(p + t * ld);
        t += d;
        if (abs(d) < eps || t > 3.0) break;
    }
    return t <= 3.0 ? 0.05 : 1.0;
}

vec3 getNorm(vec3 p)
{
    vec2 e = vec2(eps, 0);
    return normalize(vec3(sdf(p+e.xyy)-sdf(p-e.xyy), sdf(p+e.yxy)-sdf(p-e.yxy), sdf(p+e.yyx)-sdf(p-e.yyx)));
}

vec3 light(vec3 p)
{
    vec3 col = vec3(0.0);
    vec3 ld = normalize(vec3(-1.0,1.0,-5.0));
    vec3 n = getNorm(p);
    float diff = max(dot(n, ld), 0.);
    diff *= getShadow(p, n, ld);
    col += diff;
    return col;
}

vec3 bg(vec2 uv)
{
    vec2 ouv = uv;
   	uv.x += sin(ouv.y*40. + 3.5*iTime) * 0.01;
    uv.y += sin(ouv.x*40. + 5.5*iTime) * 0.005;
    uv = KIFS(uv);
	float l = abs(sin(uv.y*10.+iTime));
    l = pow(0.1/l, 2.0);
    return vec3(l) * vec3(0.07,0.25, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
	vec3 rd = normalize(vec3(uv, 1.0));
    vec3 ro = vec3(0., 0., -1.5);
    vec3 col;
	vec2 m = march(ro, rd);
    vec3 p = ro + rd * m.x;
    col += p.z > 0.1 ? (light(p) * pow(0.01/(abs(KIFS(p.xy*0.5))).y, 2.2)) * vec3(0.2, 0.4, 2.3) + light(p) * 0.1 : light(p);
    col += (m.y*m.y*m.y*m.y*m.y)*vec3(0.2, 0.4, 2.3)*100.;
    fragColor = vec4(col,1.0);
}

#define CORRECT_STREAMLINES

#define saturate(x) clamp(x, 0., 1.)

const float PI = 3.1419;

float rand(float n){return fract(sin(n) * 43758.5453123);}

vec2 force(vec2 p, vec2 pole) {
    // return normalize(p - pole) / distance(p, pole);
    // optim by Fabrice:
  	p -= pole;
	return p / dot(p,p);
}

float calcVelocity(vec2 p) {
  	vec2 velocity = vec2(0);
  	vec2 pole;
    vec2 f;
  	float o, r, m;
 	float flip = 1.;
    float j = 0.;
  	const float limit = 15.;
  	for (float i = 0.; i < limit; i++) {
    	r = rand(i / limit) - .5;
    	m = rand(i + 1.) - .5;
    	m *= (iTime+(23.78 * 1000.)) * 2.;
    	o = i + r + m;
    	pole = vec2(
      		sin(o / limit * PI * 2.),
      		cos(o / limit * PI * 2.)
    	);
    	f = force(p, pole);
        flip *= -1.;
    	velocity -= f * flip;
    	j += atan(f.x, f.y) * flip;
  	}  
  	velocity = normalize(velocity);
    #ifdef CORRECT_STREAMLINES
    	return j;
   	#endif
    return atan(velocity.x, velocity.y);
}

vec2 dir(float a) {
	return vec2(sin(a), cos(a));
}

float calcDerivitive(float a, vec2 p) {
    vec2 v = dir(a);
    float n = 2. / iResolution.x;
    float d = 0.;
	d += length(v - dir(calcVelocity(p + vec2(0,n))));
    d += length(v - dir(calcVelocity(p + vec2(n,0))));
    d += length(v - dir(calcVelocity(p + vec2(n,n))));
	d += length(v - dir(calcVelocity(p + vec2(n,-n))));
    d /= 4.;
    return d;
}

float spacing = 1./30.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.x;
	p *= 3.;
    float a = calcVelocity(p);
    float deriv = calcDerivitive(a, p);
    a /= PI * 2.;
    //fragColor = vec4(vec2(sin(atan(v.x, v.y)), cos(atan(v.x, v.y))) * .5 + .5, 0, 0); return;
    //fragColor = vec4(1.-abs(a)*2.); return;
    //a = result.z;
    float lines = fract(a / spacing);
    // create stripes
    lines = min(lines, 1. - lines) * 2.;
    // thin stripes into lines
   	lines /= deriv / spacing;
    // maintain constant line width across different screen sizes
   	lines -= iResolution.x * .0005;
    // don't blow out contrast when blending below
    lines = saturate(lines);

    float disc = length(p) - 1.;
    disc /= fwidth(disc);
    disc = saturate(disc);
    lines = mix(1. - lines, lines, disc);
    lines = pow(lines, 1./2.2);
    fragColor = vec4(lines);
}

// Author: bitless
// Title: Infinite Cube Zoom
