#define PI 3.1415926
#define hash1( n ) fract(sin(n)*43758.5453)
#define hue(v) ( .6 + .6 * cos( 6.3*(v) + vec4(0,23,21,0) ) )

float grad(float a) {
    float  sm = .005;
    float f = 1./3.;
    a = mod(a+1.,2.);
    return mix(
                    mix(.45 //SIDE1
                       ,.65 //SIDE2
                       , smoothstep (sm,-sm,abs(1.+f-a)-f)) 
                    ,.95 // BOTTOM-TOP
                    , smoothstep (f+sm, f-sm, 1.-abs(1.-a))); 
}

void mainImage( out vec4 fragColor, in vec2 g)
{
    vec2 r = iResolution.xy
        ,st = (g+g-r)/r.y;
    st += st * length(st)*.1;

    float a = atan(st.x,st.y)/PI
		,T = iTime;

    float g1  = grad(a)
        ,g2 = grad(a+1.);

    float l = dot(abs(st),normalize(vec2(1.,1.73)));
    l = log2(max(l,abs(st.x)))-T;
    float fl = fract(l);

    float sm = fwidth(l)*1.5;

    vec4 c = hue(a+T*.1)
        ,c2 = mix(hue(hash1(floor(l)-1.)),c,.3)
        ,c3 = mix(hue(hash1(floor(l)+1.)),c,.3);
    c = mix(hue(hash1(floor(l))),c,.3);

    if (mod(l,2.)<1.) {
        c *= g1;
        c2 *= g2;
        c = mix(
                mix(c2,c,smoothstep(-sm,sm,fl-.005))
                ,c2*.75
                ,smoothstep (.4, 0., fl)*0.25)
           * (1.-smoothstep(.1,0.,abs(mod(a+1.,2./3.)-1./3.))*.25);}
    else {
        c *= g2;
        c2 *= g1;
        c3 *= g1;
        c = mix(
                mix(c2,c,smoothstep(-sm,sm,fl-.005))
                ,c3*.5
                ,smoothstep (.7, 1., fl)*.2);}

    fragColor = c;
}

// Pi, the ratio of a circle's circumference to its diameter.
const float M_PI = 3.14159265358979323846264338327950288;

float Circle(vec2 uv, vec2 p, float r, float blur) {
	float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float Fish(vec2 uv, vec2 p, float scale, float f) {
	float c = 0.0;

    uv /= scale;
    uv -= p;

    for (float a = 0.0 ; a < 1.0 ; a += 0.1) {
        vec2 p = vec2(a-0.5, (0.8-a)*0.1*sin(2.0*M_PI*f*iTime+a));
        float r = 0.27*sin(a*2.4) + max(0.25-a,0.0); 
        r *= r;
        c += Circle(uv, p, r, 0.01);
    }

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float c = 0.0;

    c += Fish(uv , vec2(0.0), .4, 1.0);
    c += Fish(uv , vec2(0.8), .38, 1.2);
    c += Fish(uv , vec2(-0.8), .36, 1.4);
    c += Fish(uv , vec2(-0.8,0.8), .34, 1.6);
    c += Fish(uv , vec2(0.8,-0.8), .32, 1.8);

    // Output to screen
    fragColor = vec4(vec3(c),1.0);
}

