    vec4 mouse = normalize(iMouse);
    float theta = sqrt((iMouse.x)*10.0)/10.0 + iTime; 	
    float alpha = sqrt((iMouse.y)*10.0)/10.0;
    float gama = sqrt((iMouse.y+iMouse.x))/10.0 + iTime;
    vec4 rotate = vec4(position, 1.0)* rotationX(alpha) * rotationY(theta) * rotationZ(gama);
    position = vec3(rotate.x, rotate.y, rotate.z);
    // end rotation support 

    //float time = normalize(iTime);
    //float theta = sqrt((time)*10.0)/10.0; 	
    //vec4 rotate = vec4(position, 1.0) * rotationX(theta) * rotationY(theta) * rotationZ(theta);
