    float timeRandom2 = (sin(iTime+10.0)+1.0)/4.0;
    float timeRandom3 = (cos(iTime*0.5)+1.0)/4.0;
    float timeRandom4 = (cos(iTime*2.0)+1.0)/4.0;
    float sphere = length(position) - timeRandmo1+0.05;
    float cube = sdCube(position, timeRandom2, 0.04);
    vec3 symetric = vec3(abs(position.x), abs(position.y), abs(position.z));
    vec3 symetric2 = vec3(abs(position.y), abs(position.x), abs(position.z));
    theta = 0.009;
    rotate = vec4(symetric, 1.0) * rotationX(theta) * rotationY(theta) * rotationZ(theta);
    vec3 symetric3 = vec3(abs(rotate.x), abs(rotate.y), abs(rotate.z));

    float triPrism = sdTriPrism(vec3(symetric.x, symetric.y, symetric.z), vec2(timeRandom3, 0.53));
    float triPrism2 = sdTriPrism(vec3(symetric.y, symetric.x, symetric.z), vec2(timeRandom2, 0.53));
    float triPrism3 = sdTriPrism(vec3(symetric3.z, symetric3.x, symetric3.y), vec2(timeRandom3, 0.53));
    //float triPrism4 = sdTriPrism(vec3(symetric3.x, symetric3.y, symetric3.z), vec2(0.50, 0.53));
    float triPrism5 = sdTriPrism(vec3(symetric3.z, symetric3.y, symetric3.x), vec2(timeRandom4, 0.53));


    //float ret = min(sphere, floor);
    vec2 result = vec2(0, cube);
    if (sphere < result.y)
      result = vec2(1,sphere);
    if (triPrism < result.y)
      result = vec2(2,triPrism);  
    if (triPrism2 < result.y)
      result = vec2(3,triPrism2); 
    if (triPrism3 < result.y)
      result = vec2(4,triPrism3);
    //if (triPrism4 < result.y)
    //  result = vec2(5,triPrism4);
    if (triPrism5 < result.y)
      result = vec2(5,triPrism5);

    //float ret = min(sphere, cube);
    //ret = min(ret, triPrism);
    //return ret;
    return result;
}

vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(0.0001, 0.0);
	return normalize(vec3(map(pos+e.xyy).y-map(pos-e.xyy).y,
                          map(pos+e.yxy).y-map(pos-e.yxy).y,
                          map(pos+e.yyx).y-map(pos-e.yyx).y));
}

// rematcher
vec2 castRay(in vec3 cameraOrigin, in vec3 cameraDirection)
{
    float m = -1.0;
    float t = 0.0;
    for (int i = 0; i < 100; i++) {
    	vec3 pos = cameraOrigin + t*cameraDirection;
        vec2 map = map(pos);
        float h = map.y;
        m = map.x;
        if (h < 0.001)
            break;

        t += h;
        if (t>20.0) 
            break;
    }
    if (t > 20.0)
        t = -1.0;

    return vec2(t, m);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 pixel2dNormalized = (2.0*fragCoord-iResolution.xy)/iResolution.y; // we devide by only y to keep domain as square

    //float f = smoothstep(0.2, 0.3, length(pixel2dNormalized))*0.5;
    float an = 1.0;//10.0*iTime/iResolution.x;
    float bn = 1.0;//10.0*iTime/iResolution.y;
    vec3 cameraOrigin = vec3(0, 0,5.5);
    vec3 ta = vec3(0.0, 0.0, 0.0);
    vec3 ww = normalize(ta-cameraOrigin);
    vec3 uu = normalize(cross(ww, vec3(0,1,0)));
    vec3 vv = normalize(cross(uu, ww));
    vec3 cameraDirection = normalize(pixel2dNormalized.x*uu + pixel2dNormalized.y*vv + 5.0*ww);

    // Sky color
    vec3 col = vec3(0.8, 0.8, 0.8) - 0.4*pixel2dNormalized.y;
    // add Horizon fog
    //col = mix(col, vec3(0.7, 0.75, 0.8), exp(-10.0*pixel2dNormalized.y));


    vec2 castr = castRay(cameraOrigin, cameraDirection);
    float t = castr.x;
    if (t > 0.0) {
        vec3 intersection = cameraOrigin + t*cameraDirection;
        vec3 objectSurfaceNormal = calcNormal(intersection);

        // material base color
        vec3 material = vec3(0.2, 0.2, 0.2);
        if (castr.y == 1.0) { //sphere
        	material *= vec3(1.0, 0.0, 0.0);
        } else if (castr.y == 2.0) {
            material *= vec3(0.1, 0.1, 7.0);  
        } else if (castr.y == 3.0) {
            material *= vec3(0.9, 0.9, 0.0);  
        } else if (castr.y == 4.0) {
            material *= vec3(0.0, 0.9, 0.0);  
        } else if (castr.y == 5.0) {
            material *= vec3(0.8, 0.4, 0.0);  
        }

        vec3 sunDirectionNormal = normalize(vec3(0.5, 0.5, 0.1));
        float sunDiffusion = clamp(dot(objectSurfaceNormal, sunDirectionNormal), 0.0, 1.0);
        float skyDiffusion = clamp(dot(objectSurfaceNormal, vec3(0.6, 0.6, 0.6)), 0.5, 1.0);
        float sunShadow = step(castRay(intersection+objectSurfaceNormal*0.001, sunDirectionNormal).x, 0.0);
        float bounceDiffuse = clamp(dot(objectSurfaceNormal, vec3(1.0, -1.0, 0.0)), 1.0, 0.0);

        // lighthing adjustment
    	col  = material*vec3(5.0, 4.0, 4.0)*sunDiffusion*sunShadow;  
        col += material*vec3(0.5, 0.8, 0.9)*skyDiffusion; 
        col += material*vec3(0.5, 0.3, 0.2)*bounceDiffuse;  
    }

    // Gama correction
    col = pow(col, vec3(0.4545));


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01

mat4 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, 0, s, 0),
        vec4(0, 1, 0, 0),
        vec4(-s, 0, c, 0),
        vec4(0, 0, 0, 1)
    );
}

// https://iquilezles.org/www/articles/distfunctions/distfunctions.htm

float sphere_dist(vec3 p, vec4 s) {
    return distance(p, s.xyz) - s.w;
}

float box_dist(vec3 p, vec3 c, vec3 s, float r) {
    vec3 d = abs(p - c) - (s - r);
    return length(max(d, 0.0)) - r
        + min(max(d.x, max(d.y, d.z)), 0.0);
}

float union_d(float d1, float d2) {
    return min(d1, d2);
}

float intersection_d(float d1, float d2) {
    return max(d1, d2);
}

float difference_d(float d1, float d2) {
    return max(d1, -d2);
}

float pMod1(inout float p, float size) {
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

// (distance,oid)
// oid -1.0 = miss
// oid 0.0 = floor
// oid 1.0 = main object
vec2 dist(vec3 p, bool includeFloor) {
    float sd = sphere_dist(p, vec4(0.0, 0.0, 0.0, 3.0));
    float bd = box_dist(p, vec3(0.0, 0.0, 0.0), vec3(2.5), 0.5);

    float od = difference_d(bd, sd);
    // od = sd;

    if (includeFloor) {
	    float fd = p.y - -3.0;
    	if (fd < od) {
        	return vec2(fd, 0.0);
    	}
    }

    return vec2(od, 1.0);
}

vec2 march(vec3 ro, vec3 rd) {
    float d = 0.0;

    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + d*rd;
        vec2 f = dist(p, true);
        d += f.x;
        if (d > MAX_DIST || f.x < SURF_DIST) {
            return vec2(d, f.y);
        }
    }

    return vec2(0.0, -1.0);
}

vec3 normal(vec3 p) {
    vec2 e = vec2(0.01, 0);

    return normalize(
        dist(p, true).x - vec3(
            dist(p - e.xyy, true).x,
            dist(p - e.yxy, true).x,
            dist(p - e.yyx, true).x));
}

vec3 shade(vec3 rd, vec3 p, int oid) {
    vec3 l = vec3(0.0, 10.0, 0.0);
    l.xz += vec2(sin(iTime+3.0), cos(iTime+3.0))*10.0;

    vec3 to_l = normalize(l - p);
    vec3 n = normal(p);
    float diffuse = max(0.0, dot(to_l, n));
    vec3 col;
    if (oid == 0) {
        // Floor.
        float d = dist(p, false).x;
        d = mod(d, 1.0) > 0.9 ? 1.0 : 0.9;
        col = vec3(0.8, 0.8, 0.8)*diffuse*d;
    } else if (oid == 1) {
        // Object.
        vec3 r = 2.0*dot(to_l, n)*n - to_l;
        float specular = pow(max(dot(r, -rd), 0.0), 5.0);
        col = vec3(1.0, 0.0, 0.0)*diffuse + vec3(1.0, 1.0, 1.0)*specular;
    } else {
        col = vec3(1.0, 1.0, 0.0);
    }

    // Shadow.
    if (march(p + n*SURF_DIST*2.0, to_l).x < distance(l, p)) {
        col *= 0.5;
    }

    return col;
}

mat3 makeCamera(vec3 eye, vec3 lookat) {
    vec3 z = normalize(eye - lookat);
    vec3 x = normalize(cross(vec3(0.0, 1.0, 0.0), z));
    vec3 y = cross(z, x);

    return mat3(x, y, z);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = (fragCoord - iResolution.xy/2.0)/iResolution.y;

    vec3 eye = vec3(sin((iTime + 3.0)/10.0)*10.0, 7.0, cos((iTime + 3.0)/10.0)*10.0);
    vec3 lookat = vec3(0.0, 0.0, 0.0);
    mat3 cam = makeCamera(eye, lookat);

    vec3 rd = normalize(-cam[2]*1.5 + cam[0]*xy.x + cam[1]*xy.y);
    vec3 ro = eye;

    vec2 hit = march(ro, rd);
    vec3 col;
    if (hit.y == -1.0) {
        col = vec3(1.0, 0.0, 1.0);
    } else {
	    vec3 p = ro + rd*hit.x;
    	col = shade(rd, p, int(hit.y));
    }

    fragColor = vec4(col, 1.0);
}

