float hash(vec3 p) 
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float noise( in vec3 x )
{
    vec3 i = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash(i+vec3(0,0,0)), 
                        hash(i+vec3(1,0,0)),f.x),
                   mix( hash(i+vec3(0,1,0)), 
                        hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(i+vec3(0,0,1)), 
                        hash(i+vec3(1,0,1)),f.x),
                   mix( hash(i+vec3(0,1,1)), 
                        hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

vec3 noise33(in vec3 x){
    vec3 p = x;
    //p.z += anim;
    //p *= m;
    float fx = noise(p);
    float fy = noise(p+vec3(1.23,4.56,7.89));
    float fz = noise(p+vec3(1.23,4.56,7.89)*7.6543);
    return vec3(fx,fy,fz);
}

/*
å¨cellX,cellYæå®çæ¶æ ¼ä¸­éæ ·ï¼å¾å°æè¿çåä¸ªè·ç¦»
*/
vec4 AddSamples(int cellX, int cellY, int cellZ, vec3 at, vec4 oldFs,inout vec4 ids){
    vec3 p = vec3(float(cellX),float(cellY),float(cellZ));
    vec3 seed = vec3(p.x*1.234+1234.0, p.y*5.678+5678.0, p.z*4.57+0.752);
    float id = seed.x+seed.y+seed.z;
    //å¨æ¶æ ¼ä¸­äº§çè¥å¹²ä¸ªç¹å¾ç¹ï¼ä¸ªæ°å¨æ³æ¾åå¸è¡¨ä¸­éæºå¾å°
    int count = Poisson_count[int(fract(seed.x+seed.y+seed.z)*256.0)];
    for(int i = 0; i < count; i++){
        vec3 diff = noise33(seed);
        //åç§»ç§å­ï¼ç¨ä»¥äº§çä¸ä¸ä¸ªä¸åçåç§»
        seed.x = seed.x*1.366+0.123;
        seed.y = seed.y*2.4745687+7.73246;
        seed.z = seed.z*475.90235+87.7578;

        vec3 featurePoint = p + diff;

        float d = length(featurePoint - at);
        d*=d;
        if(d<oldFs.x){
            oldFs.yzw = oldFs.xyz; oldFs.x = d;
            ids.yzw = ids.xyz; ids.x = id;
        }else if(d<oldFs.y){
            oldFs.zw = oldFs.yz; oldFs.y = d;
            ids.zw = ids.yz; ids.y = id;
        }else if(d<oldFs.z){
            oldFs.w = oldFs.z; oldFs.z = d;
            ids.w = ids.z; ids.z = id;
        }else if(d<oldFs.w){
            oldFs.w = d;
            ids.w = id;
        }

    }
    return oldFs;
}

