vec4 cellularNoise3D(vec3 at, inout vec4 ids){
    vec3 int_at = floor(at);
    int int_at_x = int(int_at.x);
    int int_at_y = int(int_at.y);
    int int_at_z = int(int_at.z);
    vec4 fs = vec4(9999999.0,9999999.0,9999999.0,9999999.0);
    fs = AddSamples(int_at_x,int_at_y,int_at_z,at,fs,ids);
    float dx1 = at.x - int_at.x;
    float dy1 = at.y - int_at.y;
    float dz1 = at.z - int_at.z;
    float dx2 = (1.0-dx1)*(1.0-dx1);
    float dy2 = (1.0-dy1)*(1.0-dy1);
    float dz2 = (1.0-dz1)*(1.0-dz1);
    dx1 *= dx1;
    dy1 *= dy1;
    dz1 *= dz1;
    int max_order = 4;
    //å¤æ­æ¯å¦å¾å°äºæè¿çåä¸ªè·ç¦»ï¼å¦ææ¯çè¯ï¼ä¸å¿å¨ç¸é»æ¶æ ¼è¿è¡éæ ·
    //z = +1
    if(dz2<fs[max_order-1])
        fs = AddSamples(int_at_x,  int_at_y,  int_at_z+1,at,fs,ids);
    if(dx1+dz2<fs[max_order-1])
        fs = AddSamples(int_at_x-1,int_at_y,  int_at_z+1,at,fs,ids);
    if(dx2+dz2<fs[max_order-1])
        fs = AddSamples(int_at_x+1,int_at_y,  int_at_z+1,at,fs,ids);
    if(dy1+dz2<fs[max_order-1])
        fs = AddSamples(int_at_x,  int_at_y-1,int_at_z+1,at,fs,ids);
    if(dx1+dy1+dz2<fs[max_order-1])
        fs = AddSamples(int_at_x-1,int_at_y-1,int_at_z+1,at,fs,ids);
    if(dx2+dy1+dz2<fs[max_order-1])
        fs = AddSamples(int_at_x+1,int_at_y-1,int_at_z+1,at,fs,ids);
    if(dy2+dz2<fs[max_order-1])
        fs = AddSamples(int_at_x,  int_at_y+1,int_at_z+1,at,fs,ids);
    if(dx1+dy2+dz2<fs[max_order-1])
        fs = AddSamples(int_at_x-1,int_at_y+1,int_at_z+1,at,fs,ids);
    if(dx2+dy2+dz2<fs[max_order-1])
        fs = AddSamples(int_at_x+1,int_at_y+1,int_at_z+1,at,fs,ids);
    //z = -1
    if(dz1<fs[max_order-1])
        fs = AddSamples(int_at_x,  int_at_y,  int_at_z-1,at,fs,ids);
    if(dx1+dz1<fs[max_order-1])
        fs = AddSamples(int_at_x-1,int_at_y,  int_at_z-1,at,fs,ids);
    if(dx2+dz1<fs[max_order-1])
        fs = AddSamples(int_at_x+1,int_at_y,  int_at_z-1,at,fs,ids);
    if(dy1+dz1<fs[max_order-1])
        fs = AddSamples(int_at_x,  int_at_y-1,int_at_z-1,at,fs,ids);
    if(dx1+dy1+dz1<fs[max_order-1])
        fs = AddSamples(int_at_x-1,int_at_y-1,int_at_z-1,at,fs,ids);
    if(dx2+dy1+dz1<fs[max_order-1])
        fs = AddSamples(int_at_x+1,int_at_y-1,int_at_z-1,at,fs,ids);
    if(dy2+dz1<fs[max_order-1])
        fs = AddSamples(int_at_x,  int_at_y+1,int_at_z-1,at,fs,ids);
    if(dx1+dy2+dz1<fs[max_order-1])
        fs = AddSamples(int_at_x-1,int_at_y+1,int_at_z-1,at,fs,ids);
    if(dx2+dy2+dz1<fs[max_order-1])
        fs = AddSamples(int_at_x+1,int_at_y+1,int_at_z-1,at,fs,ids);
    //z = 0
    if(dx1<fs[max_order-1])
        fs = AddSamples(int_at_x-1,int_at_y,  int_at_z,at,fs,ids);
    if(dx2<fs[max_order-1])
        fs = AddSamples(int_at_x+1,int_at_y,  int_at_z,at,fs,ids);
    if(dy1<fs[max_order-1])
        fs = AddSamples(int_at_x,  int_at_y-1,int_at_z,at,fs,ids);
    if(dx1+dy1<fs[max_order-1])
        fs = AddSamples(int_at_x-1,int_at_y-1,int_at_z,at,fs,ids);
    if(dx2+dy1<fs[max_order-1])
        fs = AddSamples(int_at_x+1,int_at_y-1,int_at_z,at,fs,ids);
    if(dy2<fs[max_order-1])
        fs = AddSamples(int_at_x,  int_at_y+1,int_at_z,at,fs,ids);
    if(dx1+dy2<fs[max_order-1])
        fs = AddSamples(int_at_x-1,int_at_y+1,int_at_z,at,fs,ids);
    if(dx2+dy2<fs[max_order-1])
        fs = AddSamples(int_at_x+1,int_at_y+1,int_at_z,at,fs,ids);
    return fs;
}

vec4 fractalCellularNoise3D(vec3 at) {
	vec4 m = vec4(0.0, 0.0, 0.0, 0.0);
	float f = 1.0;
	for (int i = 0; i < 4; i++) {
        vec4 ids;
		m += cellularNoise3D(at*f,ids) / f;
		f *= 2.17;
	}
	return m;
}

