vec2 castRay( in vec3 ro, in vec3 rd )
{
    vec2 res = vec2(-1.0,-1.0);

    float tmin = 1.0;
    float tmax = 20.0;

    float t = tmin;
    for( int i = 0; i < 70 && t < tmax; i++ )
    {
        vec2 h = map( ro+rd*t );
        if( abs(h.x)<(0.0001*t) )
        { 
            res = vec2(t,h.y); 
            break;
        }
        t += h.x;
    }

    return res;
}

vec3 render(in vec3 ro, in vec3 rd)
{
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y; 
    vec3 pos = ro + t * rd;
    vec3 col = vec3(0,0,0); 
    if(m <= -1.0)//skyBox
    {
        col = vec3(0.8,0.8,1.0);
    } else{
        vec4 ids;
         vec4 fs = cellularNoise3D(pos*4.0, ids);// fractalCellularNoise3D(pos*4.0);
         fs = sqrt(fs);
         float c = fs.x*(0.95*fs.y-fs.x); 
		 col = (c<0.0)? vec3(-c) : (1.0 - c)*cellId2Color(ids.x);
         //col = vec3(fs.y);
    }
    return col;
}

mat3 getViewMatrix(in vec3 cameraPos, in vec3 targetPos)
{
    vec3 w = normalize(targetPos - cameraPos);
    vec3 up = vec3(0,1,0);
    vec3 u = normalize(cross(w, up));
    vec3 v = normalize(cross(u,w));
    return mat3(u,v,w);
}

