AABB worldbox = AABB(vec3(-5.0, -5.0, -5.0), vec3(5.0, 5.0, 5.0));
vec2 rayAABB(Ray ray, AABB aabb, float tmin, float tmax) {
    vec3 invD = 1.0/ray.d;
	vec3 t0s = (worldbox.min - ray.o) * invD;
  	vec3 t1s = (worldbox.max - ray.o) * invD;

  	vec3 tsmaller = min(t0s, t1s);
	vec3 tbigger  = max(t0s, t1s);

