float hash( float n )
{
    return fract(sin(n)*43758.5453);
}
float noise( in vec3 x )
{
    x *= cloudscale;

    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res - cloudupdown;
}


float shadow(Ray ray)	
{
    vec2 hit = rayAABB(ray, worldbox, 0.0, 1e20);
    float far = hit.y - hit.x;

    float density = 0.0;
    float dist = 0.0;
    float dlightstepsize = far/lightstepsize;
    vec3 pos = ray.o;
	for(float i = 0.0; i < lightstepsize; ++i)
    {
		pos += ray.d * dlightstepsize;
		density += max(noise(pos) * dlightstepsize, 0.0);
	}
	return exp(-density);
}
float scatter(Ray ray, float far)
{
	float dist = 0.0;
    float density = 1.0;
    float lightDensity = 0.0;
	while(far > dist)
	{
		vec3 pos = ray.o + ray.d * dist;
		float pointDensity = max(noise(pos), 0.0);
        if(pointDensity > 0.0)
        {
        	lightDensity += pointDensity * shadow(Ray(pos, light)) * (1.0/lightstepsize);
            density *= exp(-pointDensity * stepsize);
            if(density < 0.01)
                break;
        }
		dist += stepsize;
	}
	return (lightDensity + (1.0-density)) * 0.25;
}
float cloud(Ray ray)
{
	vec2 boxhit = rayAABB(ray, worldbox, 0.0, 1.e20);
    if(boxhit.x == 0.0)
        return 0.0;
    vec3 hitpoint = ray.o + ray.d * (boxhit.x + 0.001);
	float sss = scatter(Ray(hitpoint, ray.d), boxhit.y - boxhit.x);
	return sss;
}
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
	vec3 ori = vec3(orbit * sin(iTime * 0.5), 10.0, orbit * cos(iTime * 0.5));
    mat4 viewToWorld = viewMatrix(ori, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));
    vec3 dir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

	vec3 c = vec3(0.0);

	float sss = cloud(Ray(ori, dir));
	c = vec3(sss);

    // Output to screen
    fragColor = vec4(c,1.0);
}

