#define TIME mod(iTime,3.5)
float T,SAM;
#define Q(a) T=clamp((TIME-SAM)/a, 0.0, 1.0);SAM+=a;

#define PI acos(-1.0)
#define hue(h) clamp((abs(fract(h+vec3(3,2,1)/3.0)*6.0-3.0)-1.0),0.0,1.0)
#define hsv(h,s,v) mix(vec3(1),hue(h),s)*v
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec2 planeUv(vec3 ro, vec3 rd, vec3 p)
{
    float z = dot(p-ro,vec3(0,0,1))/dot(rd,vec3(0,0,1));
    p-=ro+rd*z;
    return vec2(dot(p,vec3(1,0,0)),dot(p,vec3(0,1,0)));
}

float aa(float d)
{
    return smoothstep(0.01,0.0,d);
}

float fog(float z)
{
    return exp(-z*z*0.005);
}

float rbox(vec2 p, float size)
{
    p-=clamp(p,-size,size);
    return abs(length(p)-0.1);
}


float deLine(vec2 p, vec2 a, vec2 b)
{
  vec2 pa = p - a;
  vec2 ba = b - a;
  float h = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
  return length(pa - ba * h);
}

vec2 bezier(vec2 a, vec2 b, vec2 c, float t)
{
    t=clamp(t,0.0,1.0);
    return mix(mix(a,b,t),mix(b,c,t),t);
}

vec2 bezier(vec2 a, vec2 b, vec2 c, vec2 d, float t){
    return mix(bezier(a,b,c,t),bezier(b,c,d,t),t);
}

vec2 bezierCurve(float t)
{
    vec2 a=vec2(0.3,0.5);
    vec2 b=vec2(0.9,0.1);
    return bezier(vec2(0),a,b,vec2(1),t);
}

// https://www.shadertoy.com/view/3slXD8
float deEase(vec2 p)
{
    float ITR = 50.0, pitch = 1.0, t = 0.5, de = 1e10;
    for(int j=0; j<2; j++)
    {
        float t0 = t-pitch*0.5;
        pitch /= ITR;
        for(float i=0.0; i<=ITR; i++) 
        {
            float de0=distance(p,bezierCurve(t0));
            if (de0<de)
            {
                de = de0;
                t=t0;
            }       
            t0 += pitch;
        }
    }
    return de;
}

float deEase2(vec2 p)
{
    float de=2.0;
    for(float t=0.0;t<1.0;t+=1.0/20.0)
    {
        vec2 a=bezierCurve(t);
        vec2 b=bezierCurve(t+1.0/20.0);
        de=min(de,deLine(p,a,b));
    }
    return de;
}

float ease_bezier(float p)
{
    float ITR=30.0, pitch=1.0, t=0.5, de=1.0;   
    for(int j=0; j<2; j++)
    {
        float t0=t-pitch*0.5;
        pitch/=ITR;
        for(float i=0.0; i<=ITR; i++) 
        {
            float de0=abs(p-bezierCurve(t0).x);
            if(de0<de)
            {
                de=de0;
                t=t0;
            }
            t0+=pitch;
        }
    }
    return bezierCurve(t).y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy*2.0-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(1,3,8);
    ro.xz*=rot(sin(TIME)*0.5-0.5);
    ro.y*=sin(TIME);
    vec3 ta=vec3(0,0,0);
    vec3 w=normalize(ta-ro);
    vec3 u=normalize(cross(w,vec3(0,1,0)));
    vec3 rd=normalize(u*p.x+cross(u,w)*p.y+w*2.0);
    vec3 col=hsv(0.6,0.8,0.5);

    SAM=0.0;
    Q(1.0)
    Q(1.0)

    float de;
    de=rbox(p,0.5)-0.02;
    col=mix(col,hsv(0.0,0.7,0.7),aa(de));
    de=deEase(p+0.5)-0.02;
    col=mix(col,hsv(0.0,0.9,1.0),aa(de));
    de=deEase2(p+0.5)-0.005;
    col=mix(col,hsv(0.25,0.7,1.0),aa(de));
    float x=fract(T);
    de=1.0;
    de=min(de,length(p-vec2(x,0)+0.5));
    de=min(de,length(p-vec2(0,ease_bezier(x))+0.5));
    de=min(de,length(p-vec2(x,ease_bezier(x))+0.5));
    col=mix(col,hsv(0.35,0.7,1.0),aa(de-0.05));

    for(float i=-1.0;i<1.1;i++)
    {
        vec3 coord=vec3(0,0,i*1.5);
        vec2 uv=planeUv(ro,rd,coord);
        uv*=rot(ease_bezier(T)*PI);
        float de=rbox(uv,0.6+ease_bezier(T)*1.2)-0.01;
        col=mix(col,vec3(1),aa(de)*fog(length(coord-ro)));
    }

    fragColor = vec4(col,1.0);
}

