    // sphere (big HexSphere)
    float sphereRad = 0.2;
	vec3 spherePosNew = p-spherePos();
    //spherePosNew.x = fract(spherePosNew.x+1.)-0.5;
    spherePosNew.y = fract(spherePosNew.y+1.)-0.5;
    float sphereDist = sdSphereHex(spherePosNew, sphereRad);

    // box
    vec3 boxSize = vec3(0.5,0.05,0.5);
    vec3 boxPos = vec3(0.,0.5,0.);
    //float boxDist = dBox(p - boxPos, boxSize);
    vec3 boxPosNew = p - boxPos;
    //boxPosNew.x = fract(boxPosNew.x+1.)-0.5;
    //boxPosNew.z = fract(boxPosNew.z+1.)-0.5;
    boxPosNew.y = sin(fract(boxPosNew.y+1.))-0.5;
    float boxDist = sdRoundBox(boxPosNew, boxSize, 0.015);

    // sphere2 (small sphere)
    float sphere2Rad = 0.1;
    vec3 sphere2Pos = vec3(0.4*sin(time),1.1 + 0.4*cos(time*0.3),0.4*cos(time));
    vec3 sphere2PosNew = p-sphere2Pos;
    //sphere2PosNew.x = fract(sphere2PosNew.x+1.)-0.5;
    sphere2PosNew.y = fract(sphere2PosNew.y+1.)-0.5;
    float sphere2Dist = sdSphere(sphere2PosNew, sphere2Rad);

    //box + spheres
    float boxSphereDist = min(boxDist, sphereDist);
    float boxSphereID = 2.;
    boxSphereDist = min(boxSphereDist, sphere2Dist);

    if(groundDist < res.x)
        res = vec2(groundDist, groundID);
    if(boxSphereDist < res.x) 
        res = vec2(boxSphereDist, boxSphereID);

    return res;
}
vec2 RayMarch(vec3 ro, vec3 rd, int steps) 
{
    vec2 dS;
    float dO;
    vec3 p;  
    for(int i = 0; i<steps; i++)
    {
        p = ro + rd * dO;
        dS = GetDist(p);
        if(abs(dS.x) < 0.0001) {break; }
        dO += dS.x;
    }     
    return vec2(dO,dS.y);
}
vec3 GetNormal(vec3 p)
{
    float d = GetDist(p).x;
    vec2 e = vec2(.01, 0.);

    vec3 n = d-vec3(GetDist(p-e.xyy).x, 
                        GetDist(p-e.yxy).x, 
                        GetDist(p-e.yyx).x);
    return normalize(n);
}
float GetLight(vec3 p, vec3 lightpos, int steps)
{
    float lightpower = 1.4;
    float shadowstrength = 0.6;
    vec3 l = normalize(lightpos - p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l*lightpower), 0., 1.);
    float d = (RayMarch(p + n*0.01, l, steps)).x;
    if(d < length(lightpos-p)) {dif *= shadowstrength;}
    return dif;
}
vec3 groundCol(vec3 p)
{
    vec3 col = vec3(1.,1.,1.);
    p.xz *= 10.;
    float squares = smoothstep(-0.5, -0.4, sin(p.x)*cos(p.z));
    return col*squares;
}
vec3 skyCol(vec3 p)
{
    vec3 col;
    float x = atan(p.x, p.z);
    float y = atan(length(p.xz)-1., p.y);
    float a = cos(y + 92.1);
    col = vec3(0., 0.8078, 0.8196);
    col *= vec3(a,a,1.);
    return col;
}
vec3 applyFog( vec3  rgb, float distance) 
{
    float b = 1.;
    float fogAmount = 1.0 - exp( -distance*b );
    vec3  fogColor  = vec3(0.5,0.6,0.7);
    return mix( rgb, fogColor, fogAmount );
}
vec3 applyFog( in vec3  rgb,      // original color of the pixel
               in float distance, // camera to point distance
               in vec3  rayOri,   // camera position
               in vec3  rayDir )  // camera to point vector
{
    float b = 0.3;
    float c = 0.2;
    float fogAmount = c * exp(-rayOri.y*b) * (1.0-exp( -distance*rayDir.y*b ))/rayDir.y;
    vec3  fogColor  = vec3(0.7,1.,1.);
    return mix( rgb, fogColor, fogAmount );
}
float specularReflection(vec3 p, vec3 rd, vec3 lightPos, float intensity, float shininessVal)
{
    vec3 N = GetNormal(p);
    vec3 L = normalize(lightPos - p);
    float lambertian = max(dot(L, N), 0.0);
    float specular = 0.;
  	if(lambertian > 0.0) {
        vec3 R = reflect(-L, N);      // Reflected light vector
        vec3 V = normalize(-rd); // Vector to viewer
        // Compute the specular term
        float specAngle = max(dot(R, V), 0.0);
        specular = pow(specAngle, shininessVal);
  	}
    return specular * intensity;
}
vec3 GetCol(vec3 ro, vec3 rd, vec2 d, vec3 p, int refNum) // refNum for reflection number
{
	//vec3 LightPos = vec3(5., 7., -5.);
    float dif = GetLight(p, LightPos, MaxSteps);
    float spRef = specularReflection(p, rd, LightPos, 1., 1000.);
    vec3 col = vec3(0.,0.,0.);

    if(d.y == 0.) // sky col
        col = skyCol(p);

    if(d.y == 1.) // ground col
        col = groundCol(p)*dif;

    int refMarchSteps = MaxSteps;
    while(refNum > 0)
    {
        refMarchSteps /= 2;
        if(d.y == 2.) // boxSphere col + reflection
        {
            vec3 n = GetNormal(p);
            rd = normalize(reflect(rd,n)); // reflect does: rd - 2.0 * dot(n, rd) * n
            ro = p + rd*0.001;
            d = RayMarch(ro, rd, MaxSteps);
            p = ro + rd*d.x;
            float reflectDif = GetLight(p, LightPos, refMarchSteps);
            //float reflectSpRef = specularReflection(p, rd, LightPos, 0.5, 1000.);

            if(d.y == 0.) // sky col
                col = skyCol(p);

            if(d.y == 1.) // ground col
                col = groundCol(p)*reflectDif;
            if(d.y == 2. && refNum == 1) // color for the last reflection
                col = vec3(0);
            col = applyFog(col, d.x, ro, rd);
            //col += vec3(0.2,0.2,0.);
            col *= vec3(1.9,1.4,1.); // color adjustment
            col = 0.75*col*dif + 0.25*col;
            col += spRef;
        }
        refNum -= 1;

    }
    //col= col*dif;

    return col;
}
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.,0.,0.);

    vec3 lookat = vec3(0.,1.,0.);

    float zoom = 0.5;

    vec2 mou = 5.*(iMouse.xy-.5*iResolution.xy)/iResolution.y;
    mou.y += 2.;
    mou.y = max(0.01, mou.y);
    vec3 ro = vec3(1.,5.,1.);//vec3(mou,-1.);
    ro.x += mou.x;
    ro.y = mou.y;
    ro.z = mou.y;

    vec3 F = normalize(lookat-ro); // Forward
    vec3 R = normalize(cross(vec3(0., 1., 0.), F)); //Right
    vec3 U = cross(F, R); //Up

    vec3 C = ro + F*zoom;
    vec3 I = C + uv.x*R + uv.y*U;
    vec3 rd = normalize(I-ro);

    vec2 d = RayMarch(ro,rd, MaxSteps); // d.y = Object ID
    vec3 p = ro + rd*d.x;

    col = GetCol(ro,rd,d,p,NumberOfReflections);
    col = applyFog(col, d.x,ro, rd);

    fragColor = vec4(col,0.1);
}

