// Created by cravo

// Makes use of a lot of distance functions and noise stuff by iq
// More info here: http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm

float sdPlane( vec3 p )
{
	return p.y;
}

float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float noise(vec3 x) // iq's 3D noise
{
    vec3 f = fract(x);
    vec3 p = x - f;
    f = f*f*(3.0 - 2.0*f);
    vec2 uv = (p.xy + vec2(37.0, 17.0) * p.z) + f.xy;
    vec2 rg = texture(iChannel0, (uv + 0.5)/256.0, -100.0).rg;
    return mix(rg.y, rg.x, f.z);
}

float fbm(vec3 x)
{   
    float r = 0.0;
    float w = 1.0, s = 1.0;
    for (int i=0; i<5; i++)
    {
        w *= 0.5;
        s *= 2.0;
        r += w * noise(s * x);
    }
    return r;
}

float sdFluffBall(vec3 p, float s)
{
    float rad2 = s * 0.3;
    s-= rad2 * fbm(p*8.0);

    float d = sdSphere(p,s);

    return d;
}

float sdFluffTorus(vec3 p, vec2 s)
{
    float rad2 = s.y * 0.3;
    s.y -= rad2 * fbm(p*8.0);
    return sdTorus(p,s);
}

float dot2( in vec2 v ) { return dot(v,v); }
float sdCone( in vec3 p, in float h, in float r1, in float r2 )
{
    vec2 q = vec2( length(p.xz), p.y );

    vec2 k1 = vec2(r2,h);
    vec2 k2 = vec2(r2-r1,2.0*h);
    vec2 ca = vec2(q.x-min(q.x,(q.y < 0.0)?r1:r2), abs(q.y)-h);
    vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
    float s = (cb.x < 0.0 && ca.y < 0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot2(ca),dot2(cb)) );
}

//----------------------------------------------------------------------

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

float opBlend( vec3 p, float d1, float d2 )
{
    return smin( d1, d2, 0.5 );
}

float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

//----------------------------------------------------------------------

vec2 map( in vec3 pos )
{  
    vec3 facePos = vec3(0.0,0.5,0.0);
    float face = sdEllipsoid(pos - facePos, vec3(0.4,0.5,0.4));

    vec3 nosePos = vec3(0.0,0.5,0.4);
    float nose = opBlend(pos, sdEllipsoid(pos - nosePos, vec3(0.1,0.05,0.05)),0.25);

    vec3 ear1Pos = vec3(-0.37, 0.6, 0.1);
    float ear1 = sdEllipsoid(pos - ear1Pos, vec3(0.1,0.1,0.05));

    vec3 ear2Pos = vec3(0.37, 0.6, 0.1);
    float ear2 = sdEllipsoid(pos - ear2Pos, vec3(0.1,0.1,0.05));

    vec3 eye1Pos = vec3(-0.1,0.65,0.325);
    float eye1 = sdEllipsoid(pos - eye1Pos, vec3(0.05,0.1,0.05));

    vec3 eye2Pos = vec3(0.1,0.65,0.325);
    float eye2 = sdEllipsoid(pos - eye2Pos, vec3(0.05,0.1,0.05));

    vec3 mouthPos = vec3(0.0,0.25,0.28);
    float mouth = sdEllipsoid(pos - mouthPos, vec3(0.1,abs(sin(iTime*4.0))*0.05,0.1));

    vec3 hatPos = vec3(0.0, 1.05, 0.0);
    float hat = sdCone(pos - hatPos, 0.22, 0.3, 0.0);

    vec3 hatBobblePos = vec3(0.0,1.2,0.0);
    float hatBobble = sdFluffBall(pos - hatBobblePos, 0.1);

    vec3 hatRingPos = vec3(0.0,0.8,0.0);
    float hatRing = sdFluffTorus(pos - hatRingPos,vec2(0.3,0.1));

    face = opSubtraction(mouth,face);

    vec2 res = vec2(sdPlane(pos), 1.0);
	res = opU(vec2(face,2.0), res);
    res = opU(vec2(nose,2.0), res);
    res = opU(vec2(ear1, 2.0), res);
    res = opU(vec2(ear2, 2.0), res);
    res = opU(vec2(eye1,3.0), res);
    res = opU(vec2(eye2,3.0), res);
    res = opU(vec2(hat, 4.0), res);
    res = opU(vec2(hatBobble,3.0), res);
    res = opU(vec2(hatRing, 3.0), res);

    //res = vec2(sdFluffTorus(pos - facePos,vec2(0.5,0.1)),3.0);


    return res;
}




vec2 castRay( in vec3 ro, in vec3 rd, in float maxd )
{
	float precis = 0.001;
    float h=precis*2.0;
    float t = 0.0;
    float m = -1.0;
    for( int i=0; i<128; i++ )
    {
        if( abs(h)<precis||t>maxd ) continue;//break;
        t += h;
	    vec2 res = map( ro+rd*t );
        h = res.x;
	    m = res.y;
    }

    if( t>maxd ) m=-1.0;
    return vec2( t, m );
}


float softshadow( in vec3 ro, in vec3 rd, in float mint, in float maxt, in float k )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<60; i++ )
    {
		if( t<maxt )
		{
        	float h = map( ro + rd*t ).x;
        	res = min( res, k*h/t );
        	t += 0.02;
		}
    }
    return clamp( res, 0.0, 1.0 );

}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float totao = 0.0;
    float sca = 1.0;
    for( int aoi=0; aoi<5; aoi++ )
    {
        float hr = 0.01 + 0.05*float(aoi);
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        totao += -(dd-hr)*sca;
        sca *= 0.75;
    }
    return clamp( 1.0 - 4.0*totao, 0.0, 1.0 );
}




vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.0);
    vec2 res = castRay(ro,rd,20.0);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );

		col = vec3(0.8);

        if(m == 2.0)
        {
            col = vec3(1.0,0.8,0.7);
        }
        else if(m == 4.0)
        {
            col = vec3(1.0,0.0,0.0);
        }

		vec3 lig = normalize( vec3(0.3, 0.6, 0.5) );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);

		float sh = 1.0;
		if( dif>0.02 ) { sh = softshadow( pos, lig, 0.02, 10.0, 7.0 ); dif *= sh; }

		vec3 brdf = 1.20*dif*vec3(1.00,1.0,1.0);

		float pp = clamp( dot( reflect(rd,nor), lig ), 0.0, 1.0 );
		float spe = sh*pow(pp,16.0);

		col = bac + col*brdf + vec3(1.0)*col*spe;

	}

	// light falloff with distance
	col *= exp( -0.01*t*t );


	return vec3( clamp(col,0.0,1.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
	p.x *= iResolution.x/iResolution.y;

	// camera	
	vec3 ro = vec3( sin(iTime) * 0.5, 0.7, 2.5 );
	vec3 ta = vec3( 0.0, 0.5, 0.0 );

	// camera tx
	vec3 cw = normalize( ta-ro );
	vec3 cp = vec3( 0.0, 1.0, 0.0 );
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
	vec3 rd = normalize( p.x*cu + p.y*cv + 2.5*cw );

    vec3 col = render( ro, rd );

    // gamma
    col = pow( col, vec3(0.4545) );

    fragColor=vec4( col, 1.0 );
}

