float GetDist(vec3 p  ) {	



    float d;
    float dif1;
    float dif2;

    d=999.9;
    float planeDist = p.y;

   vec3 pp;
    pp=p;



    vec2 res;
    res = vec2(9999, 0);




    p.z=p.z-6.0;

    p.x = mod(p.x, 7.0) - 3.5;


     if (res.x < planeDist)
      { res.x = res.x;     	} 
    else
        { 
          res.x = planeDist;
        if (planeDist > 0.5 )
          { res=vec2(res.x,11); }
        else
         { 
            res=vec2(res.x, CPlane2);
         } 
     } 



    pp=p;

   float sdb1=sdBox(pp-vec3(0,0,0), vec3(3.,4.0,0.5));

    float sdb2=sdBox(pp-vec3(3.5,0,0.0), vec3(0.5,2.0,0.4));
    float sdb3=sdBox(pp-vec3(-3.5,0,0.0), vec3(0.5,2.0,0.4));


    float sds1= sdSphere(pp-vec3(0,1.5,-9), 2.0 );

    float sds2= sdSphere(pp-vec3(2.5,4.0,-9.0), 0.4 );
    float sds3= sdSphere(pp-vec3(-2.5,4.0,-9.0), 0.4 );


    dif1=differenceSDF(sdb1,  sds1);
    res=opU(res, vec2(dif1,3));

    res=opU(res, vec2(sds2,8));
    res=opU(res, vec2(sds3,8));

    res=opU(res, vec2(sdb2,6));
    res=opU(res, vec2(sdb3,6));


    d = res.x;
    mObj.dist = res.x;
    mObj.tipo = res.y;

    return d;
}


float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.2;
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}


vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}



float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}


float GetLightv2(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(1, 2);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}


