void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    float circleDiv = .2;
    float tau = 3.1415926535*circleDiv;
    float a = atan(p.x,p.y);
    float r = length(p)*tan(iTime*0.5);
    vec2 uv = vec2(a/tau,r);
	float xCol = (uv.x - (iTime / 2.0)) * 1.0;
	xCol = mod(xCol, 1.0);
	vec3 stripes = vec3(0.4, .2, .3);	
    float circlesize = 1.0;
	uv = (2.0 * uv) - 1.0;
	vec3 horBeam = vec3(0.1*cos(uv.y*4.0)*clamp(floor( circlesize*sin(iTime*4.)),4.0, 10.0)*abs(10.0 / (40.0 * uv.y)));
	if (xCol < .5) 
    	{
		 stripes.r *= sin(iTime) - xCol;
         stripes.g *= cos(iTime)  - xCol;
		 stripes.b += xCol;
		}
    uv = p;
    //uv *= abs(uv);
  	float r2 = max( 0.,  sin(iTime)+3. - length(uv) );
    float t = (iTime*1.);
    t = r2 * r2 * cos(r2+t) * 5.;    
    uv *= mat2( cos(t), sin(t), cos(t), cos(t) );
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx);
    col *=0.4;
    vec3 horBeam2 = horBeam;
    horBeam *= smoothstep(0.,.5, vec3(uv.yx,horBeam.z));
    horBeam2 += smoothstep(0.,.5, vec3(uv.yx,horBeam.z));
    horBeam = mix(horBeam,horBeam2,0.3);
    fragColor = vec4((( horBeam) * stripes +col), 1.0);
}

float getGradientColor(int iteration, int maxIteration){
    return 1.0 -float(iteration) / float(maxIteration);
}

//Thanks to FabriceNeyret2 for simplifying this method!
float getSegmentedColors(int iteration,int maxIteration){
	return 1.0 - min(1.0, float(iteration * 4 / maxIteration) / 3.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv = fragCoord / iResolution.y; 

    //----------------------------Some interesting locations-------------------------------
    //
    //Full Mandelbrot
    //xMin = -2.5, yMin = -1.0, change = 2.0, maxIteration = 500, and minIteration = 30.
    //
    //SeaHorses
    //xMin = -0.75, yMin = 0.1, change = 0.01, maxIteration = 500, and minIteration = 0.
    //
    //Zoomed in sea horse.
    //xMin = -0.748, yMin = 0.11, change = 0.004, maxIteration = 500, and minIteration = 0.
    //
    //-------------------------------------------------------------------------------------

    //xMin and yMin are the mandelbrot coordinates displayed in the bottom right corner of the display. 
    float xMin = -0.75; //The minimum x-coordinate for the viewing window. 
    float yMin =  0.1; //The minimum y-coordinate for the viewing window.
    float change = 0.01;

    float speed = 1.0; //The animation play speed. Increase to play it faster.
    int maxIteration = 600;
    int minIteration = 30;

    vec2 location = vec2(uv.x * change + xMin, uv.y * change + yMin);

    int variableIteration = minIteration + int(float((maxIteration - minIteration)) * (cos(speed * iTime) * 0.5 + 0.5));

    vec2 coordinates = vec2(0.0);

    int currentIteration = 0;

    for(int i = 0; i <= variableIteration; i++){
        vec2 squared = vec2(pow(coordinates.x, 2.0), pow(coordinates.y,2.0));
        currentIteration = i;

        if((dot(squared,vec2(1.0))) >= 4.0){
            break;
    	}
        else{
            coordinates = vec2(squared.x - squared.y + location.x, (2.0 * coordinates.x * coordinates.y + location.y));
        }
    }
    //There are two different coloring methods programmed in, one will color the mandelbrot with
    //four different shades of gray/grey while the other will color each pixel with a linear gradient
    //between white and black based on the iterations reached.

    float value = getSegmentedColors(currentIteration, variableIteration);
    //float value = getGradientColor(currentIteration, variableIteration);

    vec3 col = vec3(1.0, 0.0, 0.53) * vec3(value);

    // Output to screen
    fragColor = vec4(col,1.0);
}

