#define FK(k) floatBitsToInt(cos(k))^floatBitsToInt(k)
float hash(vec2 k) {
  int x = FK(k.x);int y = FK(k.y);
  return float((x*x-y)*(y*y+x)-x)/2.14e9;
}

float hash3(vec3 k) {
  float h1 = hash(k.xy);
  return hash(vec2(h1, k.z));
}

vec3 hash33(vec3 k) {
  float h1 = hash3(k);
  float h2 = hash3(k*h1);
  float h3 = hash3(k*h2);
  return vec3(h1, h2, h3);
}

float smin( float a, float b, float k ) {
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0);
}

vec3 sphercoord(vec2 p) {
  float l1 = acos(p.x);
  float l2 = acos(-1.)*p.y;
  return vec3(cos(l1), sin(l1)*sin(l2), sin(l1)*cos(l2));
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(p,ax)*ax, p, cos(ro)) + sin(ro)*cross(p,ax);
}

float comp(vec3 p, vec3 ro, float t) {
  vec3 ax = sphercoord(ro.xy);
  p = erot(p, ax, ro.z*acos(-1.));
  p.z += t;
  float scale = 4. + hash(ro.xz)*0.5+0.5;
  p = (fract(p/scale)-0.5)*scale;
  return length(p) - 0.8;
}

float scene(vec3 p) {
  float rad = 3.-abs(p.z);
  float dist = 10000.;
  for (int i = 0; i < 5; i++) {
    vec3 rot = hash33(vec3(float(i+1), cos(float(i)), sin(float(i))));
    float d = comp(p, rot, iTime/2.);
    dist = smin(dist, d, 1.);
  }
  return mix(dist, rad, mix(0.3,0.8+sin(iTime)*0.2, sign(p.z)*0.5+0.5));
}

vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p)-mat3(0.1);
  return normalize(scene(p) - vec3(scene(k[0]),scene(k[1]),scene(k[2])));
}

float drawdist = 40.;
vec3 trace(vec3 init, vec3 cam, int itcout, inout bool hit) {
  vec3 p = init;
  hit = false;
  for (int i = 0; i < itcout; i++) {
    float dist = scene(p)*1.1;
    if (abs(dist) < 0.001) { hit = true; p+= dist*cam; break;}
    if (distance(p, init) > drawdist) { break; }
    p += dist*cam;
  }
  return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);


  uv.x += sin(uv.x*5.+iTime)*0.03;
  uv.x += sin(uv.y*3.+0.9*iTime)*0.03;
  uv.y += sin(uv.x*7.-0.7*iTime)*0.01;
  uv.y += sin(uv.y*4.+0.6*iTime)*0.01;


  vec3 slimecol = vec3(0.05,0.4,0.05);
  float bt = floor(iTime*0.8);
  float beat = mod(bt, 4.);
  if (beat ==1.) slimecol = slimecol.yxz;
  if (beat ==2.) slimecol = vec3(0.05,0.2,0.4);
  if (beat ==3.) slimecol = slimecol.yxy;

  vec3 cam = normalize(vec3(0.8,uv));
  bool hit = false;
  vec3 col = vec3(1);
  vec3 totalball = vec3(1);
  vec3 p; vec3 n;
  vec3 init = vec3(0,0,sin(iTime*0.37)*1.4);
  cam = erot(cam, vec3(0,1,0), -0.2);
  init = erot(init, vec3(0,1,0), -0.2);
  cam = erot(cam, vec3(1,0,0), hash(vec2(bt,bt))*0.2);
  init = erot(init, vec3(1,0,0), hash(vec2(bt,bt))*0.2);
  init += vec3(mod(iTime,100.)*3.+beat*8.,0.,0.);
  vec3 firtn;
  float fog;
  for (int i = 0; i < 3; i++) {
    p = trace(init, cam, 300/(i+1), hit);
    if (!hit) break;
    n = norm(p);
    if (i ==0) firtn = n;
    float fresnel = pow(1.-abs(dot(cam, n))*0.98,2.);
    if (i==0) { fog = pow(distance(p, init)/drawdist, 2.); }
    col *= fresnel*mix(slimecol,vec3(1),0.4);
    cam = reflect(cam, n);
    hit = false;
    init = p+n*.01;
  }
  col = mix(col, vec3(1), fog);

  fragColor.xyz = sqrt(1.-(1.-slimecol*(sin(firtn*4.)*0.3+0.7))*(1.-col));
  fragColor.xyz = smoothstep(vec3(0),vec3(1),fragColor.xyz);
}

#define IT 32
#define SD .005
#define MD 8.

mat2 rot(float a) {
    float s = sin(radians(a));
    float c = cos(radians(a));
    return mat2(c,-s,s,c);
}

float dist(vec3 p) {
    float d = dot(normalize(p),vec3(0.,1.,0.));
    float s = sin(atan(p.x,p.z)*5.) * pow(1.-d,.75);
    float c = cos(d*3.1415*2.5+iTime*2.);
    return length(p)-2.+s*c*.3;
}

vec3 raymarch(vec3 ro, vec3 rd) {
    vec3 p = ro;
    float td;
    for (int i; i < IT; i++) {
        float d = dist(p);
        td += d;
        p += rd * d;
        if (abs(d) < SD || td > MD) break;
    }
    return p;
}

vec3 normal(vec3 p) {
    vec2 o = vec2(SD,0);
    return normalize(vec3(
    dist(p+o.xyy)-dist(p-o.xyy),
    dist(p+o.yxy)-dist(p-o.yxy),
    dist(p+o.yyx)-dist(p-o.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,3,-5);
    vec3 rd = normalize(vec3(uv,1));
    rd.yz *= rot(31.);
    ro.xz *= rot(iTime*18.);
    rd.xz *= rot(iTime*18.);
    vec3 p = raymarch(ro,rd);
    vec3 n = normal(p);
    vec3 ld = normalize(vec3(0,3,-1));
    float l = length(raymarch(p+n*SD*2.,ld)-p) < MD ? 0. : max(dot(n,ld),0.);
    float b = dot(rd,ld)*.5+.5;
    float s = pow(max(dot(reflect(rd,n),ld),0.),50.);
    vec3 obj = mix(vec3(0.,.1,.2),vec3(.7,.8,1.),l)+s*l;
    vec3 sky = mix(vec3(0.,.05,.1),vec3(.4,.6,.9),b);
    vec3 col = length(p) < 3. ? obj : sky;
    fragColor = vec4(col,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv =  uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(dot(uv, uv));
    col.r = sin(col.r + iTime*0.3) * 0.5 + 0.5;
    col.g = sin(col.r + iTime*0.5) * 0.5 + 0.5;

    // Output to screen
    fragColor = vec4(col,1.0);
}

