const int numIterations = 11;

float map( in vec3 p, out vec4 oTrap, in float w0 )
{
    vec4 z = vec4(p, w0);

    vec4 trap = vec4(abs(z.xyz),dot(z,z));

    float n = 2.0;  
#if TYPE < 3
    vec2 x = vec2(z.z,z.w); // x = b
#else
    vec2 x = vec2(0,0); // x = 0
#endif    
    vec2 mda = vec2(0,0); // a derivative of x
    vec2 mdb = vec2(1,0); // b derivative of x
	float mz2 = dot(x,x);
    for( int i=0; i<numIterations; i++ )
    {
        // x -> (x^2 + a)^2 + b
        mda = vec2(2.0*(mda.x*x.x-mda.y*x.y)+1.0, 
                   2.0*(mda.x*x.y+mda.y*x.x));
        mdb = vec2(2.0*(mdb.x*x.x-mdb.y*x.y),
                   2.0*(mdb.x*x.y+mdb.y*x.x));
        x = vec2(x.x*x.x - x.y*x.y + z.x, 2.0*x.x*x.y + z.y);
        mda = vec2(2.0*(mda.x*x.x-mda.y*x.y), 
                   2.0*(mda.x*x.y+mda.y*x.x));
        mdb = vec2(2.0*(mdb.x*x.x-mdb.y*x.y)+1.0,
                   2.0*(mdb.x*x.y+mdb.y*x.x));
        x = vec2(x.x*x.x - x.y*x.y + z.z, 2.0*x.x*x.y + z.w);

        mz2 = dot(x,x);

        if(mz2>10.0) break;
        n += 2.0;
    }
#if TYPE<2    
    vec2 y = x;
    vec2 mda1=mda;
    vec2 mdb1=mdb;
    float mz21=mz2;

    float m = n;
    n = 2.0;
    x = vec2(z.x,z.y); // z = a
    mda = vec2(1,0);
    mdb = vec2(0,0);
	mz2 = dot(x,x);
    for( int i=0; i<numIterations; i++ )
    {
        // x -> (x^2 + b)^2 + a
        mda = vec2(2.0*(mda.x*x.x-mda.y*x.y), 
                   2.0*(mda.x*x.y+mda.y*x.x));
        mdb = vec2(2.0*(mdb.x*x.x-mdb.y*x.y)+1.0,
                   2.0*(mdb.x*x.y+mdb.y*x.x));
        x = vec2(x.x*x.x - x.y*x.y + z.z, 2.0*x.x*x.y + z.w);
        mda = vec2(2.0*(mda.x*x.x-mda.y*x.y)+1.0, 
                   2.0*(mda.x*x.y+mda.y*x.x));
        mdb = vec2(2.0*(mdb.x*x.x-mdb.y*x.y),
                   2.0*(mdb.x*x.y+mdb.y*x.x));
        x = vec2(x.x*x.x - x.y*x.y + z.x, 2.0*x.x*x.y + z.y);

        mz2 = dot(x,x);
        if(mz2>10.0) break;
        n += 2.0;
    }

    // choose faster/slower escaping 
#if TYPE==1
    if (exp2(-m)*log(mz21) > exp2(-n)*log(mz2)) {
#else
    if (exp2(-m)*log(mz21) < exp2(-n)*log(mz2)) {
#endif
        n=m; x=y; mda=mda1; mdb=mdb1; mz2=mz21;
    }
#endif
    oTrap = trap;

    return 0.25*sqrt(mz2/(dot(mda,mda)+dot(mdb,mdb)))*log(mz2);  
}

