// Create infinite copies of an object -  http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
vec2 opRep( in vec2 p, in float s )
{
    return mod(p+s*0.5,s)-s*0.5;
}


float GetDist(vec3 p  ) {	



    float d, dif1, dif2;
     vec2 res;
    res = vec2(9999, 0);


    d=999.9;
    float planeDist = p.y;

   vec3 pp;
    pp=p;

    pp.xz=opRep(pp.xz,10.0);



    float bxDer = sdBox (pp-vec3(-1.6,1.9,4.5),vec3(0.1,1.0,2.5));
    float bxIzq = sdBox (pp-vec3(1.6,1.9,4.5),vec3(0.1,1.0,2.5));
    float bxSup = sdBox (pp-vec3(-0.0,3.0,4.5),vec3(2.5,0.1,2.5));


    float Cielo = sdBox (pp-vec3(0.0,5.0,4.5),vec3(8.0,0.1,8.0));
    float dtri1=sdTriPrism(pp -vec3(0,3.5,4.5), vec2(1,1.5) );






     if (res.x < planeDist)
      { res.x = res.x;     	} 
    else
        { 
          res.x = planeDist;
        if (planeDist > 0.5 )
          {
              res=vec2(res.x,CSky); 

          }
        else
         { 
            //res=vec2(res.x, CPlane2);
              vec2 cb = floor(p.xz);
			  float cb2 = mod(cb.x + cb.y, 2.0);
              res=vec2(-cb2,11);
         } 
     } 




    res = opU(res, vec2(bxDer, CDer));
    res = opU(res, vec2(bxIzq, CIzq));
    res = opU(res, vec2(bxSup, CSup));
    res=opU(res, vec2( Cielo, 8));
    res=opU(res, vec2 (dtri1,11));



    d = res.x;
    mObj.dist = res.x;
    mObj.tipo = res.y;

    return d;
}


float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.2;
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}





vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}



float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}


float GetLightv2(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(1, 2);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}




