vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

// Voronoi noise
float vnoise(vec2 p)
{
    vec2 cell_i = floor(p);
    vec2 cell_f = fract(p);

    float min_dist = 2.0;
    for (int x = -1; x <= 1; x++){
        for (int y = -1; y <= 1; y++){

 			vec2 xy = vec2(float(x), float(y)); 
            vec2 point = random2(cell_i + xy);
            point = 0.5 + 0.5*sin(6.2831*point);
        	float dist = distance(cell_f, xy + point);
        	min_dist = min(min_dist, dist);
        }
    }
	return min_dist;
}

float fbm(vec2 p)
{
    float v;

    v  = vnoise(p * 1.0) * 0.5;
    v += vnoise(p * 2.0) * 0.25;
    v += vnoise(p * 4.0) * 0.125;
    v += vnoise(p * 8.0) * 0.0625;

    return v;
}

