// Technique from: https://www.shadertoy.com/view/4slGWH

// Messed around with cogwheels distance functions and applied it the IQ's Fractal Nyancat 

#define PI  3.141592654
#define TAU (2.0*PI)

const float cogRadius = 0.02;
const float smallWheelRadius = 0.30;
const float bigWheelRadius = 0.55;
const float wheelOffset = smallWheelRadius + bigWheelRadius -cogRadius;
const vec3 baseCol = vec3(240.0, 115.0, 51.0)/vec3(255.0);


float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

float modMirror1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize,size) - halfsize;
  p *= mod(c, 2.0)*2.0 - 1.0;
  return c;
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}

float unevenCapsule(vec2 p, float r1, float r2, float h) {
  p.x = abs(p.x);
  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);
  float k = dot(p,vec2(-b,a));
  if( k < 0.0 ) return length(p) - r1;
  if( k > a*h ) return length(p-vec2(0.0,h)) - r2;
  return dot(p, vec2(a,b) ) - r1;
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(p.x*c + p.y*s, -p.x*s + p.y*c);
}

float softMin(float a, float b, float k) {
  float res = exp( -k*a ) + exp( -k*b );
  return -log( res )/k;
}

float smallCogwheel(vec2 p) {
  rot(p, -iTime*2.0 + TAU/32.0);
  vec2 op  = p;
  float dc = circle(p, 0.25);  
  vec2 pp = toPolar(p);
  mod1(pp.y, TAU/16.0);
  pp.y += PI/2.0;
  vec2 cp = toRect(pp);
  float ds = unevenCapsule(cp, 0.05, cogRadius, smallWheelRadius);
  float dcw = softMin(ds, dc, 100.0);
  float dic = circle(p, 0.125/2.0);
  pp = toPolar(p);
  mod1(pp.y, TAU/6.0);
  vec2 ip = toRect(pp);
  float dic2 = circle(ip - vec2(0.15, 0.0), 0.125/2.0);
  float di = min(dic, dic2);
  return max(dcw, -di);
}

float bigCogwheel(vec2 p) {
  rot(p, iTime);
  vec2 op  = p;
  float dc = circle(p, 0.5);  
  vec2 pp = toPolar(p);
  mod1(pp.y, TAU/32.0);
  pp.y += PI/2.0;
  vec2 cp = toRect(pp);
  float ds = unevenCapsule(cp, 0.1, cogRadius, bigWheelRadius);
  float dcw = softMin(ds, dc, 100.0);
  float dic = circle(p, 0.125);
  pp = toPolar(p);
  mod1(pp.y, TAU/6.0);
  vec2 ip = toRect(pp);
  float dic2 = circle(ip - vec2(0.3, 0.0), 0.125);
  float di = min(dic, dic2);
  return max(dcw, -di);
}

float cogwheels(vec2 p) {
  p.x += wheelOffset*0.5;
  float dsc = smallCogwheel(p - vec2(wheelOffset, 0.0));
  float dbc = bigCogwheel(p);
  return min(dsc, dbc);
}


float df(vec2 p) {
  float i = modMirror1(p.x, wheelOffset);
  float sy = mix(1.0, -1.0, mod(i, 2.0));
  p.y *= sy;
  float dcs = cogwheels(p);
  return dcs;
}

vec4 sample_(vec2 p) {
  const float borderStep = 0.001;
  vec3 col = baseCol;  
  p *= 4.0;
  float d = df(p);
  float t = smoothstep(-borderStep, 0.0, -d);
  t *= exp(-dot(p, p)*0.005);
  return vec4(col, t);
}

vec3 saturate(vec3 col) {
  return clamp(col, 0.0, 1.0);
}

vec3 postProcess(in vec3 col, in vec2 q)  {
  col = saturate(col);
  col=pow(clamp(col,0.0,1.0),vec3(0.75)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

  p = vec2(0.5, -0.05) + p*0.75*pow(0.9, 20.0*(0.5+0.5*cos(0.3*sqrt(2.0)*iTime*sqrt(0.5))));

  vec4 col = vec4(0.0);
  vec3 ss = mix(vec3(0.2, 0.2, 0.5), vec3(0.2,-0.2,1.0), 2.2 + 1.25*sin(iTime*0.5));

  vec2 c = vec2(-0.76, 0.15);
  rot(c, 0.2*sin(iTime*sqrt(3.0)/12.0));
  float f = 0.0;
  vec2 z = p;

  float transparency = 1.0;

  vec3 bg = vec3(0.0);

  float minTrap = 10000.0;

  const int maxIter = 100;
  const float maxIterF = float(maxIter);
  for(int i=0; i<=maxIter; ++i)
  {
    if (transparency < 0.05) break;
    float re2 = z.x*z.x;
    float im2 = z.y*z.y;
    if((re2 + im2>4.0) || (transparency<0.1)) break;
    float reim = z.x*z.y;

    z = vec2(re2 - im2, 2.0*reim) + c;
    minTrap = min(minTrap, length(z - c));

    float fi = f/maxIterF;
    float shade = pow(1.0-0.5*fi, 1.5);

    vec4 sample_ = sample_(ss.xy + ss.z*z);
    float ff = mix(0.0, 0.5, pow(fi, 0.5));
    sample_.xyz = pow(sample_.xyz, mix(vec3(1.0), vec3(75.0, 0.5, 0.0), ff));
    sample_.xyz = mix(bg, sample_.xyz, shade);

    transparency *= 1.0 - clamp(sample_.w, 0.0, 1.0);
    vec4 newCol = vec4(col.xyz + sample_.xyz*(1.0 - col.w)*sample_.w, 1.0 - transparency);

    col = newCol;

    f += 1.0;
  }

  bg= vec3(0.3, 0.25, 0.4)*max(0.5 - sqrt(minTrap), 0.0);
  col.xyz = mix(bg, col.xyz, col.w);
  float fade = smoothstep(0.0, 3.0, iTime);
  fragColor = vec4(fade*postProcess(col.xyz, q), 1.0);
}

//////////////////// Re-oriented 8-point BCC noise by KdotJPG ////////////////////
//////////////////// Output: vec4(dF/dx, dF/dy, dF/dz, value) ////////////////////

// Borrowed from Stefan Gustavson's noise code
vec4 permute(vec4 t) {
    return t * (t * 34.0 + 133.0);
}

// Gradient set is a normalized expanded rhombic dodecahedron
vec3 grad(float hash) {

    // Random vertex of a cube, +/- 1 each
    vec3 cube = mod(floor(hash / vec3(1.0, 2.0, 4.0)), 2.0) * 2.0 - 1.0;

    // Random edge of the three edges connected to that vertex
    // Also a cuboctahedral vertex
    // And corresponds to the face of its dual, the rhombic dodecahedron
    vec3 cuboct = cube;
    cuboct[int(hash / 16.0)] = 0.0;

    // In a funky way, pick one of the four points on the rhombic face
    float type = mod(floor(hash / 8.0), 2.0);
    vec3 rhomb = (1.0 - type) * cube + type * (cuboct + cross(cube, cuboct));

    // Expand it so that the new edges are the same length
    // as the existing ones
    vec3 grad = cuboct * 1.22474487139 + rhomb;

    // To make all gradients the same length, we only need to shorten the
    // second type of vector. We also put in the whole noise scale constant.
    // The compiler should reduce it into the existing floats. I think.
    grad *= (1.0 - 0.042942436724648037 * type) * 3.5946317686139184;

    return grad;
}

// BCC lattice split up into 2 cube lattices
vec4 bccNoiseDerivativesPart(vec3 X) {
    vec3 b = floor(X);
    vec4 i4 = vec4(X - b, 2.5);

    // Pick between each pair of oppposite corners in the cube.
    vec3 v1 = b + floor(dot(i4, vec4(.25)));
    vec3 v2 = b + vec3(1, 0, 0) + vec3(-1, 1, 1) * floor(dot(i4, vec4(-.25, .25, .25, .35)));
    vec3 v3 = b + vec3(0, 1, 0) + vec3(1, -1, 1) * floor(dot(i4, vec4(.25, -.25, .25, .35)));
    vec3 v4 = b + vec3(0, 0, 1) + vec3(1, 1, -1) * floor(dot(i4, vec4(.25, .25, -.25, .35)));

    // Gradient hashes for the four vertices in this half-lattice.
    vec4 hashes = permute(mod(vec4(v1.x, v2.x, v3.x, v4.x), 289.0));
    hashes = permute(mod(hashes + vec4(v1.y, v2.y, v3.y, v4.y), 289.0));
    hashes = mod(permute(mod(hashes + vec4(v1.z, v2.z, v3.z, v4.z), 289.0)), 48.0);

    // Gradient extrapolations & kernel function
    vec3 d1 = X - v1; vec3 d2 = X - v2; vec3 d3 = X - v3; vec3 d4 = X - v4;
    vec4 a = max(0.75 - vec4(dot(d1, d1), dot(d2, d2), dot(d3, d3), dot(d4, d4)), 0.0);
    vec4 aa = a * a; vec4 aaaa = aa * aa;
    vec3 g1 = grad(hashes.x); vec3 g2 = grad(hashes.y);
    vec3 g3 = grad(hashes.z); vec3 g4 = grad(hashes.w);
    vec4 extrapolations = vec4(dot(d1, g1), dot(d2, g2), dot(d3, g3), dot(d4, g4));

    // Derivatives of the noise
    vec3 derivative = -8.0 * mat4x3(d1, d2, d3, d4) * (aa * a * extrapolations)
        + mat4x3(g1, g2, g3, g4) * aaaa;

    // Return it all as a vec4
    return vec4(derivative, dot(aaaa, extrapolations));
}

// Rotates domain, but preserve shape. Hides grid better in cardinal slices.
// Good for texturing 3D objects with lots of flat parts along cardinal planes.
vec4 bccNoiseDerivatives_XYZ(vec3 X) {
    X = dot(X, vec3(2.0/3.0)) - X;

    vec4 result = bccNoiseDerivativesPart(X) + bccNoiseDerivativesPart(X + 144.5);

    return vec4(dot(result.xyz, vec3(2.0/3.0)) - result.xyz, result.w);
}

// Gives X and Y a triangular alignment, and lets Z move up the main diagonal.
// Might be good for terrain, or a time varying X/Y plane. Z repeats.
vec4 bccNoiseDerivatives_PlaneFirst(vec3 X) {

    // Not a skew transform.
    mat3 orthonormalMap = mat3(
        0.788675134594813, -0.211324865405187, -0.577350269189626,
        -0.211324865405187, 0.788675134594813, -0.577350269189626,
        0.577350269189626, 0.577350269189626, 0.577350269189626);

    X = orthonormalMap * X;
    vec4 result = bccNoiseDerivativesPart(X) + bccNoiseDerivativesPart(X + 144.5);

    return vec4(result.xyz * orthonormalMap, result.w);
}

//////////////////////////////// End noise code ////////////////////////////////


void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord / iResolution.y;

    // I think this helps prevent roundoff errors in the noise for big iTime?
    vec3 baseNoiseInput = vec3(0, -iTime * 2.0, iTime);
    mat3 orthonormalMap = mat3(
        0.788675134594813, -0.211324865405187, -0.577350269189626,
        -0.211324865405187, 0.788675134594813, -0.577350269189626,
        0.577350269189626, 0.577350269189626, 0.577350269189626);
    baseNoiseInput = (fract(orthonormalMap * baseNoiseInput / 289.0) * 289.0) * orthonormalMap;

    float value = bccNoiseDerivatives_PlaneFirst(vec3(uv * 9.0, 0) + baseNoiseInput).w;

    vec3 col = vec3(1.0, 0.77, 0.2) * (0.5 + 0.5 * value - uv.y * 1.125 + 0.25);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

// by @etiennejcb
// Using code from : https://www.shadertoy.com/view/Xt2XDt
// and 3D Simplex noise from https://www.shadertoy.com/view/XsX3zB
// Got help from @tdhooper

const float MAX_TRACE_DISTANCE = 10.0;           // max trace distance
const float INTERSECTION_PRECISION = 0.0006;        // precision of the intersection
const int NUM_OF_TRACE_STEPS = 200;


vec3 random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
	 w = max(0.6 - w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 /* multiply d by w^4 */
	 w *= w;
	 w *= w;
	 d *= w;

	 /* 3. return the sum of the four surflets */
	 return dot(d, vec4(52.0));
}

